     1â†’'use client';
     2â†’
     3â†’import { useState } from 'react';
     4â†’import { motion, AnimatePresence } from 'framer-motion';
     5â†’import {
     6â†’  BookOpen,
     7â†’  Dumbbell,
     8â†’  Sparkles,
     9â†’  Clock,
    10â†’  ChevronRight,
    11â†’  Search,
    12â†’  Brain,
    13â†’  Wrench,
    14â†’  TrendingDown,
    15â†’  Calendar,
    16â†’  Target,
    17â†’  Heart,
    18â†’  Utensils,
    19â†’  Droplet,
    20â†’  X,
    21â†’  ArrowLeft,
    22â†’  MessageSquare,
    23â†’  ExternalLink,
    24â†’  ShoppingCart,
    25â†’} from 'lucide-react';
    26â†’import Image from 'next/image';
    27â†’import Link from 'next/link';
    28â†’import { TabContent } from '../ResultsLayout';
    29â†’import {
    30â†’  GUIDE_CATEGORIES,
    31â†’  getGuidesByCategory,
    32â†’  getTotalGuideCount,
    33â†’  getTotalReadTime,
    34â†’  searchGuides,
    35â†’  getGuideProductById,
    36â†’} from '@/data/guides';
    37â†’import { Guide, GuideCategory, GuideMedia } from '@/types/guides';
    38â†’import { useRegion } from '@/contexts/RegionContext';
    39â†’import ReactMarkdown from 'react-markdown';
    40â†’
    41â†’// ============================================
    42â†’// ICON MAPPING
    43â†’// ============================================
    44â†’
    45â†’const ICON_MAP: Record<string, React.ReactNode> = {
    46â†’  BookOpen: <BookOpen size={20} />,
    47â†’  Dumbbell: <Dumbbell size={20} />,
    48â†’  Sparkles: <Sparkles size={20} />,
    49â†’  Brain: <Brain size={20} />,
    50â†’  Wrench: <Wrench size={20} />,
    51â†’  TrendingDown: <TrendingDown size={20} />,
    52â†’  Calendar: <Calendar size={20} />,
    53â†’  Target: <Target size={20} />,
    54â†’  Heart: <Heart size={20} />,
    55â†’  Utensils: <Utensils size={20} />,
    56â†’  Droplet: <Droplet size={20} />,
    57â†’};
    58â†’
    59â†’function getIconComponent(iconName: string, size = 20): React.ReactNode {
    60â†’  // Check icon map first
    61â†’  const mappedIcon = ICON_MAP[iconName];
    62â†’  if (mappedIcon) return mappedIcon;
    63â†’
    64â†’  // Fallback to BookOpen
    65â†’  return <BookOpen size={size} />;
    66â†’}
    67â†’
    68â†’// ============================================
    69â†’// MEDIA RENDERER
    70â†’// ============================================
    71â†’
    72â†’interface MediaRendererProps {
    73â†’  media: GuideMedia;
    74â†’}
    75â†’
    76â†’function MediaRenderer({ media }: MediaRendererProps) {
    77â†’  const placementClasses = {
    78â†’    inline: 'max-w-md mx-auto',
    79â†’    hero: 'w-full max-w-2xl mx-auto',
    80â†’    'full-width': 'w-full',
    81â†’  };
    82â†’
    83â†’  const isGif = media.type === 'gif' || media.url.endsWith('.gif');
    84â†’
    85â†’  return (
    86â†’    <figure className={`my-6 ${placementClasses[media.placement || 'inline']}`}>
    87â†’      <div className="relative rounded-lg overflow-hidden bg-neutral-800">
    88â†’        {isGif ? (
    89â†’          // Use img for GIFs to preserve animation
    90â†’          // eslint-disable-next-line @next/next/no-img-element
    91â†’          <img
    92â†’            src={media.url}
    93â†’            alt={media.alt}
    94â†’            className="w-full h-auto"
    95â†’            loading="lazy"
    96â†’          />
    97â†’        ) : (
    98â†’          <Image
    99â†’            src={media.url}
   100â†’            alt={media.alt}
   101â†’            width={media.width || 600}
   102â†’            height={media.height || 400}
   103â†’            className="w-full h-auto"
   104â†’            placeholder="blur"
   105â†’            blurDataURL="data:image/jpeg;base64,"
   106â†’          />
   107â†’        )}
   108â†’      </div>
   109â†’      {media.caption && (
   110â†’        <figcaption className="mt-2 text-center text-sm text-neutral-400 italic">
   111â†’          {media.caption}
   112â†’        </figcaption>
   113â†’      )}
   114â†’    </figure>
   115â†’  );
   116â†’}
   117â†’
   118â†’// ============================================
   119â†’// PRODUCT CALLOUT (CONVERSION OPTIMIZED)
   120â†’// ============================================
   121â†’
   122â†’interface ProductCalloutProps {
   123â†’  productId: string;
   124â†’}
   125â†’
   126â†’function ProductCallout({ productId }: ProductCalloutProps) {
   127â†’  const { getLink } = useRegion();
   128â†’  const product = getGuideProductById(productId);
   129â†’
   130â†’  if (!product) return null;
   131â†’
   132â†’  const link = getLink(product.regionLinks, product.directLink);
   133â†’
   134â†’  return (
   135â†’    <div className="my-6 p-4 bg-gradient-to-r from-cyan-500/10 to-blue-500/10 border border-cyan-500/20 rounded-xl">
   136â†’      <div className="flex items-center gap-4">
   137â†’        {/* Product Image */}
   138â†’        {product.imageUrl && (
   139â†’          <div className="w-16 h-16 bg-neutral-800 rounded-lg overflow-hidden flex-shrink-0">
   140â†’            <Image
   141â†’              src={product.imageUrl}
   142â†’              alt={product.name}
   143â†’              width={64}
   144â†’              height={64}
   145â†’              className="w-full h-full object-cover"
   146â†’            />
   147â†’          </div>
   148â†’        )}
   149â†’
   150â†’        {/* Product Info */}
   151â†’        <div className="flex-1 min-w-0">
   152â†’          <div className="flex items-center gap-2">
   153â†’            <h4 className="font-semibold text-white truncate">{product.name}</h4>
   154â†’            {product.brand && (
   155â†’              <span className="text-xs text-neutral-500">{product.brand}</span>
   156â†’            )}
   157â†’          </div>
   158â†’          <p className="text-sm text-cyan-300 italic">&ldquo;{product.tagline}&rdquo;</p>
   159â†’        </div>
   160â†’
   161â†’        {/* CTA Button */}
   162â†’        <a
