     1→'use client';
     2→
     3→import { useEffect, useState, useMemo } from 'react';
     4→import Link from 'next/link';
     5→import { useResults } from '@/contexts/ResultsContext';
     6→import { TabContent } from '../ResultsLayout';
     7→import { api, ArchetypeForumRecommendation } from '@/lib/api';
     8→import { RecommendedForum, Category } from '@/types/forum';
     9→import { classifyFromRatios } from '@/lib/archetype-classifier';
    10→import { Ethnicity, Gender } from '@/lib/harmony-scoring';
    11→import {
    12→  Users,
    13→  ArrowRight,
    14→  MessageSquare,
    15→  Flame,
    16→  Target,
    17→  TrendingUp,
    18→  Sparkles,
    19→  Crown,
    20→} from 'lucide-react';
    21→
    22→export function CommunityTab() {
    23→  const { flaws, frontRatios, sideRatios, gender, ethnicity } = useResults();
    24→  const [recommendedForums, setRecommendedForums] = useState<RecommendedForum[]>([]);
    25→  const [archetypeForums, setArchetypeForums] = useState<ArchetypeForumRecommendation[]>([]);
    26→  const [allCategories, setAllCategories] = useState<Category[]>([]);
    27→  const [isLoading, setIsLoading] = useState(true);
    28→  const [error, setError] = useState<string | null>(null);
    29→
    30→  // Classify archetype from ratios
    31→  const archetypeClassification = useMemo(() => {
    32→    if (!frontRatios || frontRatios.length === 0 || !gender || !ethnicity) {
    33→      return null;
    34→    }
    35→    try {
    36→      return classifyFromRatios(
    37→        frontRatios,
    38→        sideRatios || [],
    39→        gender as Gender,
    40→        ethnicity as Ethnicity
    41→      );
    42→    } catch {
    43→      return null;
    44→    }
    45→  }, [frontRatios, sideRatios, gender, ethnicity]);
    46→
    47→  useEffect(() => {
    48→    let isMounted = true;
    49→    const controller = new AbortController();
    50→
    51→    async function fetchData() {
    52→      setIsLoading(true);
    53→      setError(null);
    54→
    55→      try {
    56→        // Convert flaw IDs to snake_case format for API
    57→        // flaw.id format: "insight_flaw_narrow_jaw" -> extract "narrow_jaw"
    58→        const flawIds = flaws.map(f => {
    59→          const id = f.id.replace('insight_flaw_', '');
    60→          return id;
    61→        });
    62→
    63→        // Get archetype category for recommendations
    64→        const archetypeCategory = archetypeClassification?.primary?.category;
    65→
    66→        const [recommended, archetype, all] = await Promise.all([
    67→          flawIds.length > 0 ? api.getRecommendedForums(flawIds) : Promise.resolve([]),
    68→          archetypeCategory ? api.getArchetypeForumRecommendations(archetypeCategory) : Promise.resolve([]),
    69→          api.getForumCategories(),
    70→        ]);
    71→
    72→        if (isMounted) {
    73→          setRecommendedForums(recommended);
    74→          setArchetypeForums(archetype);
    75→          setAllCategories(all);
    76→        }
    77→      } catch (err) {
    78→        if (isMounted && err instanceof Error && err.name !== 'AbortError') {
    79→          setError(err.message || 'Failed to load communities');
    80→        }
    81→      } finally {
    82→        if (isMounted) {
    83→          setIsLoading(false);
    84→        }
    85→      }
    86→    }
    87→
    88→    fetchData();
    89→
    90→    return () => {
    91→      isMounted = false;
    92→      controller.abort();
    93→    };
