from mitmproxy import io
import gzip
import os

FLOW_FILE = "traffic_capture_gradient_2.flow"

def extract_mediapipe():
    if not os.path.exists(FLOW_FILE):
        print(f"Flow file {FLOW_FILE} not found.")
        return

    print(f"Scanning {FLOW_FILE} for MediaPipe assets...")
    count = 0

    try:
        with open(FLOW_FILE, "rb") as log:
            freader = io.FlowReader(log)
            for flow in freader.stream():
                if flow.response and flow.response.content:
                    url = flow.request.url
                    content = flow.response.content

                    # Decompress if needed
                    ce = flow.response.headers.get("content-encoding", "").lower()
                    if "br" in ce:
                        try:
                            content_decoded = brotli.decompress(content)
                        except:
                            content_decoded = content
                    elif "gzip" in ce or content.startswith(b'\x1f\x8b'):
                        try:
                            content_decoded = gzip.decompress(content)
                        except:
                            content_decoded = content
                    else:
                        content_decoded = content

                    # Check for Keywords in URL or Content
                    is_hit = False
                    reason = ""

                    # Check URL
                    if "mediapipe" in url.lower() or "tfjs" in url.lower():
                        is_hit = True
                        reason = "URL match"

                    # Check Content (if text)
                    if not is_hit:
                        try:
                            text = content_decoded.decode('utf-8', errors='ignore')
                            if "FaceLandmarker" in text or "FilesetResolver" in text or "face_landmarker" in text:
                                is_hit = True
                                reason = "Content match"
                        except:
                            pass

                    if is_hit:
                        ext = ".js"
                        if ".wasm" in url: ext = ".wasm"
                        if ".tflite" in url: ext = ".tflite"
                        if ".binary" in url: ext = ".bin"

                        filename = f"mediapipe_asset_{count}{ext}"
                        with open(filename, "wb") as f:
                            f.write(content_decoded)

                        print(f"Extracted {filename} | Reason: {reason} | URL: {url}")
                        count += 1

    except Exception as e:
        print(f"Error reading flow: {e}")

if __name__ == "__main__":
    extract_mediapipe()

