-- Migration: Add HOT sort composite index for forum posts
-- Date: 2025-12-27

CREATE INDEX IF NOT EXISTS idx_posts_hot_sort
ON forum_posts(sub_forum_id, is_pinned DESC, vote_count DESC, created_at DESC);

-- Add comment for documentation
COMMENT ON INDEX idx_posts_hot_sort IS 'Optimizes HOT sorting algorithm for forum posts - covers sub_forum filtering with pinned priority, then vote ranking, then recency';

