-- Migration: Add audit timestamps for soft deletes
-- Date: 2025-12-27
-- Purpose: GDPR compliance and audit trails

-- Add deleted_at and deleted_by columns to forum_posts
ALTER TABLE forum_posts ADD COLUMN IF NOT EXISTS deleted_at TIMESTAMP WITH TIME ZONE;
ALTER TABLE forum_posts ADD COLUMN IF NOT EXISTS deleted_by UUID REFERENCES users(id);

-- Add deleted_at and deleted_by columns to forum_comments
ALTER TABLE forum_comments ADD COLUMN IF NOT EXISTS deleted_at TIMESTAMP WITH TIME ZONE;
ALTER TABLE forum_comments ADD COLUMN IF NOT EXISTS deleted_by UUID REFERENCES users(id);

-- Create indexes for efficient querying of non-deleted content
CREATE INDEX IF NOT EXISTS idx_posts_deleted_at ON forum_posts(deleted_at) WHERE deleted_at IS NOT NULL;
CREATE INDEX IF NOT EXISTS idx_comments_deleted_at ON forum_comments(deleted_at) WHERE deleted_at IS NOT NULL;

-- Comments
COMMENT ON COLUMN forum_posts.deleted_at IS 'Timestamp when post was soft-deleted, NULL if active';
COMMENT ON COLUMN forum_posts.deleted_by IS 'User ID who deleted the post (self or moderator)';
COMMENT ON COLUMN forum_comments.deleted_at IS 'Timestamp when comment was soft-deleted, NULL if active';
COMMENT ON COLUMN forum_comments.deleted_by IS 'User ID who deleted the comment (self or moderator)';
