interface HierarchicalCategoryFilterProps {
  selectedPrimary: string | null;
  selectedSecondary: string | null;
  onSelectPrimary: (primary: string | null) => void;
  onSelectSecondary: (secondary: string | null) => void;
  ratios: Ratio[];
}

