'use client';

import { useState } from 'react';
import { motion } from 'framer-motion';
import {
  User,
  Bell,
  Eye,
  Download,
  Share2,
  Trash2,
  Lock,
  Globe,
  ChevronRight,
} from 'lucide-react';
import { TabContent } from '../ResultsLayout';

// ============================================
// SETTING ITEM
// ============================================

interface SettingItemProps {
  icon: React.ReactNode;
  title: string;
  description?: string;
  children?: React.ReactNode;
  onClick?: () => void;
}

function SettingItem({ icon, title, description, children, onClick }: SettingItemProps) {
  const Wrapper = onClick ? 'button' : 'div';

  return (
    <Wrapper
      onClick={onClick}
      className={`w-full flex items-center gap-4 p-4 bg-neutral-900/60 border border-neutral-800 rounded-xl ${
        onClick ? 'hover:border-neutral-700 transition-all cursor-pointer' : ''
      }`}
    >
      <div className="w-10 h-10 rounded-lg bg-neutral-800 flex items-center justify-center flex-shrink-0">
        {icon}
      </div>
      <div className="flex-1 text-left">
        <h4 className="font-medium text-white">{title}</h4>
        {description && (
          <p className="text-sm text-neutral-500">{description}</p>
        )}
      </div>
      {children || (onClick && <ChevronRight size={18} className="text-neutral-600" />)}
    </Wrapper>
  );
}

// ============================================
// TOGGLE SWITCH
// ============================================

interface ToggleSwitchProps {
  enabled: boolean;
  onToggle: () => void;
}

function ToggleSwitch({ enabled, onToggle }: ToggleSwitchProps) {
  return (
    <button
      onClick={onToggle}
      className={`relative w-11 h-6 rounded-full transition-colors ${
        enabled ? 'bg-cyan-500' : 'bg-neutral-700'
      }`}
    >
      <motion.div
        className="absolute top-1 w-4 h-4 bg-white rounded-full"
        animate={{ left: enabled ? 24 : 4 }}
        transition={{ type: 'spring', stiffness: 500, damping: 30 }}
      />
    </button>
  );
}

// ============================================

/**
 * Insights Engine
 *
 * Processes insight definitions from insights.json and classifies them
 * as strengths or areas of improvement based on user's metric scores.
 *
 * Logic:
 * - For each insight definition, calculate average score of affected metrics
 * - Weakness: if avg score < threshold → classify with severity label
 * - Strength: if avg score > threshold → classify with grade label
 *
 * Z-Score Based Severity Logic (NEW):
 * - Ideal (Green): User is inside the ideal range
 * - Good (Blue): Within 1 Standard Deviation of the mean (|z| < 1)
 * - Moderate (Yellow): Between 1 and 2 Standard Deviations (1 ≤ |z| < 2)
 * - Severe (Red): More than 2 Standard Deviations away (|z| ≥ 2)
 *
 * Severity Labels (Weaknesses):
 *   0-3 = "Severe"
 *   3-5 = "Moderate"
 *   5-7 = "Minor"
 *
 * Grade Labels (Strengths):
 *   8-9   = "Good"
 *   9-9.5 = "Excellent"
 *   9.5-10 = "Ideal"
 */

import { Strength, Flaw } from '@/types/results';
