  },
  {
    id: 'mental',
    name: 'Mental Performance',
    description: 'Optimize your brain.',
    icon: 'Brain',
    color: 'violet',
    guideIds: ['dopamine-optimization', 'environmental-engineering', 'brain-fuel', 'cognitive-enhancers'],
  },
  {
    id: 'nutrition',
    name: 'Diet & Nutrition',
