   329â†’
   330â†’function GuideViewer({ guide, onClose, onSwitchGuide, allGuides, gender }: GuideViewerProps) {
   331â†’  const [currentSection, setCurrentSection] = useState(0);
   332â†’  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
   333â†’  const [expandedCategories, setExpandedCategories] = useState<string[]>(() => {
   334â†’    // Find the category of the current guide and expand it by default
   335â†’    const currentCategory = GUIDE_CATEGORIES.find(c => c.guideIds.includes(guide.id));
   336â†’    return currentCategory ? [currentCategory.id] : [];
   337â†’  });
   338â†’
   339â†’  // Reset to first section when switching guides
   340â†’  useEffect(() => {
   341â†’    setCurrentSection(0);
   342â†’    // Also expand the new guide's category
   343â†’    const newCategory = GUIDE_CATEGORIES.find(c => c.guideIds.includes(guide.id));
   344â†’    if (newCategory) {
   345â†’      setExpandedCategories(prev =>
   346â†’        prev.includes(newCategory.id) ? prev : [...prev, newCategory.id]
   347â†’      );
   348â†’    }
   349â†’  }, [guide.id]);
   350â†’
   351â†’  const section = guide.sections[currentSection];
   352â†’
   353â†’  if (!section) return null;
   354â†’
   355â†’  // Calculate reading progress (0% at start, 100% at last section)
   356â†’  const progress = guide.sections.length > 1
   357â†’    ? (currentSection / (guide.sections.length - 1)) * 100
   358â†’    : 100;
   359â†’
   360â†’  const toggleCategory = (categoryId: string) => {
   361â†’    setExpandedCategories(prev =>
   362â†’      prev.includes(categoryId)
   363â†’        ? prev.filter(id => id !== categoryId)
   364â†’        : [...prev, categoryId]
   365â†’    );
   366â†’  };
   367â†’
   368â†’  // Filter categories by gender
   369â†’  const filteredCategories = GUIDE_CATEGORIES.filter(cat => {
   370â†’    if (cat.id === 'male' && gender === 'female') return false;
   371â†’    if (cat.id === 'female' && gender === 'male') return false;
   372â†’    return true;
   373â†’  });
   374â†’
   375â†’  const colorClasses: Record<string, { text: string; bg: string; border: string }> = {
   376â†’    blue: { text: 'text-cyan-400', bg: 'bg-cyan-500/10', border: 'border-cyan-500/20' },
   377â†’    purple: { text: 'text-purple-400', bg: 'bg-purple-500/10', border: 'border-purple-500/20' },
   378â†’    amber: { text: 'text-amber-400', bg: 'bg-amber-500/10', border: 'border-amber-500/20' },
   379â†’    pink: { text: 'text-pink-400', bg: 'bg-pink-500/10', border: 'border-pink-500/20' },
   380â†’    emerald: { text: 'text-emerald-400', bg: 'bg-emerald-500/10', border: 'border-emerald-500/20' },
   381â†’    red: { text: 'text-red-400', bg: 'bg-red-500/10', border: 'border-red-500/20' },
   382â†’  };
   383â†’
   384â†’  return (
   385â†’    <motion.div
   386â†’      initial={{ opacity: 0 }}
   387â†’      animate={{ opacity: 1 }}
   388â†’      exit={{ opacity: 0 }}
   389â†’      className="fixed inset-0 z-50 bg-black"
   390â†’    >
   391â†’      {/* Header */}
   392â†’      <div className="sticky top-0 z-20 bg-black/95 backdrop-blur-md border-b border-neutral-900">
   393â†’        <div className="flex items-center justify-between px-4 lg:px-8 py-3">
   394â†’          <button
   395â†’            onClick={onClose}
   396â†’            className="flex items-center gap-2 text-neutral-500 hover:text-cyan-400 text-xs font-black uppercase tracking-widest transition-colors"
   397â†’          >
   398â†’            <ArrowLeft size={14} />
   399â†’            <span className="hidden sm:inline">BACK TO GUIDES</span>
   400â†’          </button>
   401â†’
   402â†’          {/* Progress bar */}
   403â†’          <div className="hidden md:flex items-center gap-4 flex-1 max-w-md mx-8">
   404â†’            <div className="flex-1 h-1 bg-neutral-900 rounded-full overflow-hidden">
   405â†’              <motion.div
   406â†’                className="h-full bg-gradient-to-r from-cyan-500 to-cyan-400"
   407â†’                initial={{ width: 0 }}
   408â†’                animate={{ width: `${progress}%` }}
   409â†’                transition={{ duration: 0.3 }}
   410â†’              />
   411â†’            </div>
   412â†’            <span className="text-[10px] font-black uppercase tracking-widest text-neutral-600 whitespace-nowrap">
   413â†’              {currentSection + 1} / {guide.sections.length}
   414â†’            </span>
