   422â†’            >
   423â†’              <BookOpen size={18} className="text-neutral-400" />
   424â†’            </button>
   425â†’            <button
   426â†’              onClick={onClose}
   427â†’              className="p-2 rounded-xl bg-neutral-900/50 border border-white/5 hover:border-white/10 transition-colors"
   428â†’            >
   429â†’              <X size={18} className="text-neutral-400" />
   430â†’            </button>
   431â†’          </div>
   432â†’        </div>
   433â†’      </div>
   434â†’
   435â†’      {/* Main Layout: Sidebar + Content */}
   436â†’      <div className="flex h-[calc(100vh-57px)]">
   437â†’        {/* Sidebar Navigation */}
   438â†’        <aside
   439â†’          className={`
   440â†’            fixed lg:relative inset-y-0 left-0 z-30 lg:z-0
   441â†’            w-72 lg:w-80 bg-black lg:bg-neutral-950/50
   442â†’            border-r border-neutral-900
   443â†’            transform transition-transform duration-300 ease-in-out
   444â†’            ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full lg:translate-x-0'}
   445â†’            flex flex-col
