   456â†’          {/* Categories & Guides List */}
   457â†’          <nav className="flex-1 overflow-y-auto p-3">
   458â†’            {filteredCategories.map(category => {
   459â†’              const categoryGuides = allGuides.filter(g => category.guideIds.includes(g.id));
   460â†’              if (categoryGuides.length === 0) return null;
   461â†’
   462â†’              const isExpanded = expandedCategories.includes(category.id);
   463â†’              const styles = colorClasses[category.color] || colorClasses.blue;
