   727→interface ApiOptions {
   728→  method?: 'GET' | 'POST' | 'PUT' | 'DELETE';
   729→  body?: unknown;
   730→  token?: string;
   731→}
   732→
   733→interface Analysis {
   734→  id: string;
   735→  front_image_url: string | null;
   736→  side_image_url: string | null;
   737→  front_landmarks: Record<string, unknown> | null;
   738→  side_landmarks: Record<string, unknown> | null;
   739→  scores: Record<string, unknown> | null;
   740→  gender: string | null;
   741→  ethnicity: string | null;
   742→  created_at: string;
   743→}
   744→
   745→class ApiClient {
   746→  private token: string | null = null;
   747→
   748→  setToken(token: string | null) {
   749→    this.token = token;
   750→  }
   751→
   752→  getToken(): string | null {
   753→    if (typeof window !== 'undefined') {
   754→      return this.token || localStorage.getItem('auth_token');
   755→    }
   756→    return this.token;
   757→  }
   758→
   759→  private async request<T>(endpoint: string, options: ApiOptions = {}): Promise<T> {
   760→    const { method = 'GET', body, token } = options;
   761→    const authToken = token || this.getToken();
   762→
   763→    const headers: Record<string, string> = {
   764→      'Content-Type': 'application/json',
   765→    };
   766→
   767→    if (authToken) {
   768→      headers['Authorization'] = `Bearer ${authToken}`;
   769→    }
   770→
   771→    try {
   772→      const response = await fetch(`${API_URL}${endpoint}`, {
   773→        method,
   774→        headers,
   775→        body: body ? JSON.stringify(body) : undefined,
   776→      });
   777→
   778→      if (!response.ok) {
   779→        const error = await response.json().catch(() => ({ detail: 'Request failed' }));
   780→        throw new Error(error.detail || `HTTP ${response.status}`);
   781→      }
   782→
   783→      // Handle empty responses (204 No Content)
   784→      if (response.status === 204) {
   785→        return undefined as T;
   786→      }
   787→
   788→      return response.json();
   789→    } catch (err) {
   790→      // Network errors, CORS issues, etc.
   791→      if (err instanceof TypeError && err.message.includes('fetch')) {
   792→        throw new Error('Network error - please check your internet connection');
   793→      }
   794→      throw err;
   795→    }
   796→  }
   797→
   798→  // Health check
   799→  async health(): Promise<{ status: string; detection_ready: boolean }> {
   800→    return this.request('/health');
   801→  }
   802→
   803→  // Analyses CRUD
   804→  async listAnalyses(limit = 50, offset = 0): Promise<Analysis[]> {
   805→    return this.request(`/analyses?limit=${limit}&offset=${offset}`);
   806→  }
   807→
   808→  async createAnalysis(data: {
   809→    front_image_url?: string;
   810→    side_image_url?: string;
   811→    front_landmarks?: Record<string, unknown>;
   812→    side_landmarks?: Record<string, unknown>;
   813→    scores?: Record<string, unknown>;
   814→    gender?: string;
   815→    ethnicity?: string;
   816→  }): Promise<Analysis> {
   817→    return this.request('/analyses', { method: 'POST', body: data });
   818→  }
   819→
   820→  async getAnalysis(id: string): Promise<Analysis> {
   821→    return this.request(`/analyses/${id}`);
   822→  }
   823→
   824→  async deleteAnalysis(id: string): Promise<void> {
   825→    return this.request(`/analyses/${id}`, { method: 'DELETE' });
   826→  }
   827→
   828→  // Payments
   829→  async createCheckout(plan: 'basic' | 'pro'): Promise<{ checkout_url: string; session_id: string }> {
   830→    return this.request(`/payments/checkout?plan=${plan}`, { method: 'POST' });
   831→  }
