   833→  async getPaymentHistory(): Promise<Array<{
   834→    id: string;
   835→    amount: number;
   836→    currency: string;
   837→    status: string;
   838→    created_at: string;
   839→  }>> {
   840→    return this.request('/payments/history');
   841→  }
   842→
   843→  // Leaderboard
   844→  async submitScore(data: {
   845→    score: number;
   846→    analysis_id?: string;
   847→    gender: 'male' | 'female';
   848→    ethnicity?: string;
   849→    face_photo_url?: string;
   850→    top_strengths?: string[];
   851→    top_improvements?: string[];
   852→  }): Promise<UserRank> {
   853→    const response = await this.request<LeaderboardApiResponse>('/leaderboard/score', {
   854→      method: 'POST',
   855→      body: data,
   856→    });
   857→    return transformUserRank(response);
   858→  }
   859→
   860→  async getMyRank(): Promise<UserRank> {
   861→    const response = await this.request<LeaderboardApiResponse>('/leaderboard/rank');
   862→    return transformUserRank(response);
   863→  }
   864→
   865→  async getLeaderboard(options?: {
   866→    gender?: 'male' | 'female';
   867→    limit?: number;
   868→    offset?: number;
