  1317→  async uploadPhysiquePhotos(
  1318→    front?: File,
  1319→    side?: File,
  1320→    back?: File
  1321→  ): Promise<{
  1322→    front_photo_url: string | null;
  1323→    side_photo_url: string | null;
  1324→    back_photo_url: string | null;
  1325→    created_at: string;
  1326→    updated_at: string;
  1327→  }> {
  1328→    const formData = new FormData();
  1329→    if (front) formData.append('front', front);
  1330→    if (side) formData.append('side', side);
  1331→    if (back) formData.append('back', back);
  1332→
  1333→    const authToken = this.getToken();
  1334→    const headers: Record<string, string> = {};
  1335→    if (authToken) {
  1336→      headers['Authorization'] = `Bearer ${authToken}`;
  1337→    }
  1338→
  1339→    const response = await fetch(`${API_URL}/physique/upload`, {
  1340→      method: 'POST',
  1341→      headers,
  1342→      body: formData,
  1343→    });
  1344→
  1345→    if (!response.ok) {
  1346→      const error = await response.json().catch(() => ({ detail: 'Upload failed' }));
  1347→      throw new Error(error.detail || `HTTP ${response.status}`);
  1348→    }
  1349→
  1350→    return response.json();
  1351→  }
