'use client';

import { useState, useCallback, useEffect, useRef } from 'react';
import { useRouter } from 'next/navigation';
import { Search, X, Filter, ChevronDown } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

interface SearchBarProps {
  initialQuery?: string;
  initialType?: 'posts' | 'comments' | 'all';
  onSearch?: (query: string, type: 'posts' | 'comments' | 'all') => void;
  showFilters?: boolean;
  placeholder?: string;
  className?: string;
  autoFocus?: boolean;
}

export function SearchBar({
  initialQuery = '',
  initialType = 'all',
  onSearch,
  showFilters = true,
  placeholder = 'Search posts and comments...',
  className = '',
  autoFocus = false,
}: SearchBarProps) {
  const router = useRouter();
  const [query, setQuery] = useState(initialQuery);
  const [searchType, setSearchType] = useState<'posts' | 'comments' | 'all'>(initialType);
  const [showFilterDropdown, setShowFilterDropdown] = useState(false);
  const inputRef = useRef<HTMLInputElement>(null);
  const dropdownRef = useRef<HTMLDivElement>(null);
  const debounceTimerRef = useRef<NodeJS.Timeout | null>(null);

  // Close dropdown when clicking outside
  useEffect(() => {
    function handleClickOutside(event: MouseEvent) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setShowFilterDropdown(false);
      }
    }
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // Handle search with debounce
  const handleSearch = useCallback(() => {
    if (query.trim().length < 2) return;

    if (onSearch) {
      onSearch(query.trim(), searchType);
    } else {
      // Navigate to search results page
      const params = new URLSearchParams();
      params.set('q', query.trim());
      if (searchType !== 'all') params.set('type', searchType);
      router.push(`/forum/search?${params.toString()}`);
    }
  }, [query, searchType, onSearch, router]);

  // Debounced search (300ms)
  const handleInputChange = useCallback((value: string) => {
    setQuery(value);

    if (debounceTimerRef.current) {
      clearTimeout(debounceTimerRef.current);
    }

    // Only auto-search if we have an onSearch callback (inline search)
    if (onSearch && value.trim().length >= 2) {
      debounceTimerRef.current = setTimeout(() => {
        onSearch(value.trim(), searchType);
      }, 300);
    }
  }, [onSearch, searchType]);

  // Handle Enter key
  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      if (debounceTimerRef.current) {
        clearTimeout(debounceTimerRef.current);
      }
      handleSearch();
    }
  };

  // Clear search
  const handleClear = () => {
    setQuery('');
    inputRef.current?.focus();
  };

  const filterOptions = [
    { value: 'all', label: 'All' },
    { value: 'posts', label: 'Posts' },
    { value: 'comments', label: 'Comments' },
  ] as const;

  const selectedFilter = filterOptions.find(f => f.value === searchType);

  return (
    <div className={`relative flex items-center gap-2 ${className}`}>
      {/* Search Input */}
      <div className="relative flex-1">
        <div className="absolute left-4 top-1/2 -translate-y-1/2 pointer-events-none">
          <Search size={16} className="text-neutral-500" />
        </div>
        <input
          ref={inputRef}
          type="text"
          value={query}
          onChange={(e) => handleInputChange(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder={placeholder}
          autoFocus={autoFocus}
          className="w-full h-11 pl-11 pr-10 rounded-xl bg-neutral-900/50 border border-white/10
                     text-sm text-white placeholder:text-neutral-600
                     focus:outline-none focus:border-cyan-500/30 focus:ring-1 focus:ring-cyan-500/20
                     transition-all"
        />
        {query && (
          <button
            onClick={handleClear}
            className="absolute right-3 top-1/2 -translate-y-1/2 p-1 rounded-md
                       text-neutral-500 hover:text-white hover:bg-white/10 transition-all"
          >
            <X size={14} />
          </button>
        )}
      </div>

      {/* Filter Dropdown */}
      {showFilters && (
        <div className="relative" ref={dropdownRef}>
          <button
            onClick={() => setShowFilterDropdown(!showFilterDropdown)}
            className="h-11 px-4 rounded-xl bg-neutral-900/50 border border-white/10
                       flex items-center gap-2 text-[10px] font-black uppercase tracking-widest
                       text-neutral-400 hover:text-white hover:border-white/20 transition-all"
          >
            <Filter size={14} />
            <span className="hidden sm:inline">{selectedFilter?.label}</span>
            <ChevronDown size={12} className={`transition-transform ${showFilterDropdown ? 'rotate-180' : ''}`} />
          </button>

          <AnimatePresence>
            {showFilterDropdown && (
              <motion.div
                initial={{ opacity: 0, y: -8 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -8 }}
                transition={{ duration: 0.15 }}
                className="absolute right-0 top-full mt-2 w-36 py-2 rounded-xl
                           bg-neutral-900 border border-white/10 shadow-xl shadow-black/50 z-50"
              >
                {filterOptions.map((option) => (
                  <button
                    key={option.value}
                    onClick={() => {
                      setSearchType(option.value);
                      setShowFilterDropdown(false);
                    }}
                    className={`w-full px-4 py-2.5 text-left text-[11px] font-bold uppercase tracking-wider
                               transition-colors ${
                                 searchType === option.value
                                   ? 'text-cyan-400 bg-cyan-500/10'
                                   : 'text-neutral-400 hover:text-white hover:bg-white/5'
                               }`}
                  >
                    {option.label}
                  </button>
                ))}
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      )}

      {/* Search Button */}
      <button
        onClick={handleSearch}
        disabled={query.trim().length < 2}
        className="h-11 px-5 rounded-xl bg-cyan-500 text-black text-[10px] font-black uppercase tracking-widest
                   flex items-center gap-2 hover:bg-cyan-400 transition-all shadow-lg shadow-cyan-500/20
                   disabled:opacity-50 disabled:cursor-not-allowed disabled:hover:bg-cyan-500"
      >
        <Search size={14} />
        <span className="hidden sm:inline">Search</span>
      </button>
    </div>
  );
}

