'use client';

import { useEffect, useState, useCallback, Suspense } from 'react';
import { useSearchParams } from 'next/navigation';
import Link from 'next/link';
import { motion } from 'framer-motion';
import {
  Search,
  MessageSquare,
  FileText,
  ThumbsUp,
  Clock,
  ArrowRight,
  User,
  ChevronLeft,
  ChevronRight,
  AlertCircle,
} from 'lucide-react';
import { ForumHeader, ForumBreadcrumb, SearchBar } from '@/components/forum';
import { api } from '@/lib/api';
import type { ForumSearchResult } from '@/types/forum';

// ============================================
// SEARCH RESULT CARD
// ============================================
function SearchResultCard({ result, index }: { result: ForumSearchResult; index: number }) {
  const isPost = result.type === 'post';
  const linkUrl = isPost
    ? `/forum/post/${result.id}`
    : `/forum/post/${result.postId}#comment-${result.id}`;

  // Format date
  const date = new Date(result.createdAt);
  const timeAgo = getTimeAgo(date);

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: index * 0.05 }}
    >
      <Link href={linkUrl}>
        <div className="group p-6 rounded-2xl bg-neutral-900/30 border border-white/5 hover:border-cyan-500/20 transition-all">
          <div className="flex items-start gap-4">
            {/* Type Icon */}
            <div className={`w-10 h-10 rounded-xl flex items-center justify-center flex-shrink-0 ${
              isPost
                ? 'bg-gradient-to-br from-cyan-500/20 to-blue-500/20 border border-cyan-500/30'
                : 'bg-gradient-to-br from-purple-500/20 to-pink-500/20 border border-purple-500/30'
            }`}>
              {isPost ? (
                <FileText size={16} className="text-cyan-400" />
              ) : (
                <MessageSquare size={16} className="text-purple-400" />
              )}
            </div>

            {/* Content */}
            <div className="flex-1 min-w-0">
              {/* Type badge + title/post reference */}
              <div className="flex flex-wrap items-center gap-2 mb-2">
                <span className={`px-2 py-0.5 rounded-md text-[9px] font-black uppercase tracking-widest ${
                  isPost
                    ? 'bg-cyan-500/10 text-cyan-400 border border-cyan-500/20'
                    : 'bg-purple-500/10 text-purple-400 border border-purple-500/20'
                }`}>
                  {isPost ? 'Post' : 'Comment'}
                </span>

                {isPost && result.title && (
                  <h3 className="text-sm font-bold text-white group-hover:text-cyan-400 transition-colors truncate">
                    {result.title}
                  </h3>
                )}

                {!isPost && result.postTitle && (
                  <span className="text-xs text-neutral-500 truncate">
                    on &quot;{result.postTitle}&quot;
                  </span>
                )}
              </div>

              {/* Content preview */}
              <p className="text-[12px] text-neutral-400 line-clamp-2 leading-relaxed mb-3">
                {result.contentPreview}
              </p>

              {/* Meta info */}
              <div className="flex flex-wrap items-center gap-4 text-[10px] text-neutral-500">
                <span className="flex items-center gap-1.5">
                  <User size={12} />
                  {result.author.username}
                </span>
                <span className="flex items-center gap-1.5">
                  <ThumbsUp size={12} />
                  {result.voteCount} votes
                </span>
                <span className="flex items-center gap-1.5">
                  <Clock size={12} />
                  {timeAgo}
                </span>
                {result.categorySlug && (
                  <span className="px-2 py-0.5 rounded-md bg-neutral-800 text-neutral-400 uppercase tracking-wider font-bold">
                    {result.categorySlug.replace(/-/g, ' ')}
                  </span>
                )}
              </div>
            </div>

            {/* Arrow */}
            <div className="flex-shrink-0 w-8 h-8 rounded-lg bg-neutral-800/50 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity">
              <ArrowRight size={14} className="text-cyan-400" />
            </div>
          </div>
        </div>
      </Link>
    </motion.div>
  );
}

// ============================================
// LOADING SKELETON
// ============================================
function SearchResultSkeleton() {
  return (
    <div className="p-6 rounded-2xl bg-neutral-900/30 border border-white/5 animate-pulse">
      <div className="flex items-start gap-4">
        <div className="w-10 h-10 rounded-xl bg-neutral-800" />
        <div className="flex-1 space-y-3">
          <div className="flex items-center gap-2">
            <div className="h-5 w-16 bg-neutral-800 rounded" />
            <div className="h-4 w-48 bg-neutral-800 rounded" />
          </div>
          <div className="h-3 w-full bg-neutral-800 rounded" />
          <div className="h-3 w-2/3 bg-neutral-800 rounded" />
          <div className="flex gap-4">
            <div className="h-3 w-20 bg-neutral-800 rounded" />
            <div className="h-3 w-16 bg-neutral-800 rounded" />
            <div className="h-3 w-24 bg-neutral-800 rounded" />
          </div>
        </div>
      </div>
    </div>
  );
}

// ============================================
// PAGINATION
// ============================================
function Pagination({
  currentPage,
  totalPages,
  onPageChange,
}: {
  currentPage: number;
  totalPages: number;
  onPageChange: (page: number) => void;
}) {
  if (totalPages <= 1) return null;

  return (
    <div className="flex items-center justify-center gap-2 mt-8">
      <button
        onClick={() => onPageChange(currentPage - 1)}
        disabled={currentPage === 1}
        className="h-10 px-4 rounded-xl bg-neutral-900/50 border border-white/10 text-neutral-400
                   hover:text-white hover:border-white/20 disabled:opacity-50 disabled:cursor-not-allowed
                   flex items-center gap-2 text-[10px] font-black uppercase tracking-widest transition-all"
      >
        <ChevronLeft size={14} />
        Prev
      </button>

      <span className="px-4 text-sm text-neutral-500">
        Page <span className="text-white font-bold">{currentPage}</span> of{' '}
        <span className="text-white font-bold">{totalPages}</span>
      </span>

      <button
        onClick={() => onPageChange(currentPage + 1)}
        disabled={currentPage === totalPages}
        className="h-10 px-4 rounded-xl bg-neutral-900/50 border border-white/10 text-neutral-400
                   hover:text-white hover:border-white/20 disabled:opacity-50 disabled:cursor-not-allowed
                   flex items-center gap-2 text-[10px] font-black uppercase tracking-widest transition-all"
      >
        Next
        <ChevronRight size={14} />
      </button>
    </div>
  );
}

// ============================================
// EMPTY STATE
// ============================================
function EmptyState({ query }: { query: string }) {
  return (
    <div className="text-center py-16 rounded-2xl bg-neutral-900/30 border border-white/5">
      <div className="w-14 h-14 rounded-2xl bg-neutral-900/50 flex items-center justify-center mx-auto mb-4">
        <Search size={24} className="text-neutral-600" />
      </div>
      <h3 className="text-lg font-bold text-white mb-2">No results found</h3>
      <p className="text-neutral-500 text-sm mb-4 max-w-md mx-auto">
        We couldn&apos;t find any posts or comments matching &quot;{query}&quot;.
        Try different keywords or check your spelling.
      </p>
      <Link
        href="/forum"
        className="inline-flex items-center gap-2 text-cyan-400 text-sm hover:text-cyan-300 transition-colors"
      >
        <ArrowRight size={14} className="rotate-180" />
        Browse all communities
      </Link>
    </div>
  );
}

// ============================================
// SEARCH CONTENT COMPONENT
// ============================================
function SearchContent() {
  const searchParams = useSearchParams();
  const [results, setResults] = useState<ForumSearchResult[]>([]);
  const [totalCount, setTotalCount] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [currentPage, setCurrentPage] = useState(1);

  const query = searchParams.get('q') || '';
  const type = (searchParams.get('type') || 'all') as 'posts' | 'comments' | 'all';
  const PAGE_SIZE = 20;

  const totalPages = Math.ceil(totalCount / PAGE_SIZE);

  // Perform search
  const performSearch = useCallback(async (searchQuery: string, searchType: 'posts' | 'comments' | 'all', page: number) => {
    if (!searchQuery || searchQuery.length < 2) {
      setResults([]);
      setTotalCount(0);
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const response = await api.searchForum({
        query: searchQuery,
        type: searchType,
        limit: PAGE_SIZE,
        offset: (page - 1) * PAGE_SIZE,
      });
      setResults(response.results);
      setTotalCount(response.totalCount);
    } catch (err) {
      console.error('Search error:', err);
      setError(err instanceof Error ? err.message : 'Search failed. Please try again.');
      setResults([]);
      setTotalCount(0);
    } finally {
      setIsLoading(false);
    }
  }, []);

  // Search when params change
  useEffect(() => {
    setCurrentPage(1);
    performSearch(query, type, 1);
  }, [query, type, performSearch]);

  // Handle page change
  const handlePageChange = (page: number) => {
    setCurrentPage(page);
    performSearch(query, type, page);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Handle new search from SearchBar
  const handleSearch = (newQuery: string, newType: 'posts' | 'comments' | 'all') => {
    const params = new URLSearchParams();
    params.set('q', newQuery);
    if (newType !== 'all') params.set('type', newType);
    window.history.pushState({}, '', `/forum/search?${params.toString()}`);
    setCurrentPage(1);
    performSearch(newQuery, newType, 1);
  };

  return (
    <>
      {/* Search Header */}
      <section className="border-b border-white/5">
        <div className="max-w-4xl mx-auto px-6 py-8">
          <h1 className="text-3xl md:text-4xl font-black tracking-tighter italic uppercase mb-6">
            Search <span className="text-cyan-400">Results</span>
          </h1>

          <SearchBar
            initialQuery={query}
            initialType={type}
            onSearch={handleSearch}
            placeholder="Search posts and comments..."
          />

          {query && !isLoading && (
            <p className="mt-4 text-sm text-neutral-500">
              {totalCount > 0 ? (
                <>Found <span className="text-white font-bold">{totalCount}</span> result{totalCount !== 1 ? 's' : ''} for &quot;{query}&quot;</>
              ) : (
                <>Searching for &quot;{query}&quot;...</>
              )}
            </p>
          )}
        </div>
      </section>

      {/* Results */}
      <main className="max-w-4xl mx-auto px-6 py-8">
        {/* Error State */}
        {error && (
          <div className="p-4 rounded-xl bg-red-500/10 border border-red-500/20 mb-6 flex items-center gap-3">
            <AlertCircle size={18} className="text-red-400" />
            <p className="text-red-400 text-sm">{error}</p>
          </div>
        )}

        {/* Loading State */}
        {isLoading && (
          <div className="space-y-4">
            {[1, 2, 3, 4, 5].map((i) => (
              <SearchResultSkeleton key={i} />
            ))}
          </div>
        )}

        {/* Results List */}
        {!isLoading && results.length > 0 && (
          <>
            <div className="space-y-4">
              {results.map((result, index) => (
                <SearchResultCard key={`${result.type}-${result.id}`} result={result} index={index} />
              ))}
            </div>

            <Pagination
              currentPage={currentPage}
              totalPages={totalPages}
              onPageChange={handlePageChange}
            />
          </>
        )}

        {/* Empty State */}
        {!isLoading && !error && query && results.length === 0 && (
          <EmptyState query={query} />
        )}

        {/* No Query State */}
        {!isLoading && !query && (
          <div className="text-center py-16 rounded-2xl bg-neutral-900/30 border border-white/5">
            <div className="w-14 h-14 rounded-2xl bg-neutral-900/50 flex items-center justify-center mx-auto mb-4">
              <Search size={24} className="text-neutral-600" />
            </div>
            <h3 className="text-lg font-bold text-white mb-2">Search the forum</h3>
            <p className="text-neutral-500 text-sm max-w-md mx-auto">
              Enter a search term above to find posts and comments across all communities.
            </p>
          </div>
        )}
      </main>
    </>
  );
}

// ============================================
// MAIN PAGE
// ============================================
export default function ForumSearchPage() {
  return (
    <div className="min-h-screen bg-black selection:bg-cyan-500/30">
      <ForumHeader />
      <ForumBreadcrumb
        items={[
          { label: 'Forum', href: '/forum' },
          { label: 'Search' },
        ]}
      />
      <Suspense fallback={
        <div className="max-w-4xl mx-auto px-6 py-8">
          <div className="space-y-4">
            {[1, 2, 3, 4, 5].map((i) => (
              <SearchResultSkeleton key={i} />
            ))}
          </div>
        </div>
      }>
        <SearchContent />
      </Suspense>
    </div>
  );
}

// ============================================
// HELPER FUNCTIONS
// ============================================
function getTimeAgo(date: Date): string {
  const now = new Date();
  const seconds = Math.floor((now.getTime() - date.getTime()) / 1000);

  if (seconds < 60) return 'just now';
  if (seconds < 3600) return `${Math.floor(seconds / 60)}m ago`;
  if (seconds < 86400) return `${Math.floor(seconds / 3600)}h ago`;
  if (seconds < 604800) return `${Math.floor(seconds / 86400)}d ago`;
  if (seconds < 2592000) return `${Math.floor(seconds / 604800)}w ago`;
  if (seconds < 31536000) return `${Math.floor(seconds / 2592000)}mo ago`;
  return `${Math.floor(seconds / 31536000)}y ago`;
}

