'use client';

import { useState, useEffect, useCallback } from 'react';
import Link from 'next/link';
import { motion } from 'framer-motion';
import { api } from '@/lib/api';
import { ForumHeader, PostCard } from '@/components/forum';
import type { PostListItem, VoteType } from '@/types/forum';
import {
  Bookmark,
  BookmarkX,
  ArrowLeft,
  Loader2,
  ChevronDown,
} from 'lucide-react';

const PAGE_SIZE = 20;

export default function SavedPostsPage() {
  const [posts, setPosts] = useState<PostListItem[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isLoadingMore, setIsLoadingMore] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [totalCount, setTotalCount] = useState(0);
  const [hasMore, setHasMore] = useState(false);
  const [offset, setOffset] = useState(0);

  const fetchBookmarks = useCallback(async (loadMore = false) => {
    const currentOffset = loadMore ? offset : 0;

    if (loadMore) {
      setIsLoadingMore(true);
    } else {
      setIsLoading(true);
    }
    setError(null);

    try {
      const data = await api.getBookmarkedPosts({
        limit: PAGE_SIZE,
        offset: currentOffset,
      });

      if (loadMore) {
        setPosts(prev => [...prev, ...data.posts]);
      } else {
        setPosts(data.posts);
      }
      setTotalCount(data.totalCount);
      setHasMore(data.hasMore);
      setOffset(currentOffset + PAGE_SIZE);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to load bookmarks');
    } finally {
      setIsLoading(false);
      setIsLoadingMore(false);
    }
  }, [offset]);

  useEffect(() => {
    fetchBookmarks(false);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const handleVote = async (postId: string, voteType: VoteType) => {
    try {
      const result = await api.voteForumPost(postId, voteType);
      setPosts(prev =>
        prev.map(p =>
          p.id === postId
            ? { ...p, voteCount: result.newVoteCount, userVote: result.userVote }
            : p
        )
      );
    } catch (err) {
      console.error('Failed to vote:', err);
    }
  };

  const handleRemoveBookmark = async (postId: string) => {
    try {
      await api.toggleBookmark(postId);
      // Remove from local state
      setPosts(prev => prev.filter(p => p.id !== postId));
      setTotalCount(prev => prev - 1);
    } catch (err) {
      console.error('Failed to remove bookmark:', err);
    }
  };

  const handleLoadMore = () => {
    if (hasMore && !isLoadingMore) {
      fetchBookmarks(true);
    }
  };

  return (
    <div className="min-h-screen bg-black selection:bg-cyan-500/30">
      <ForumHeader />

      {/* Hero */}
      <section className="border-b border-white/5">
        <div className="max-w-4xl mx-auto px-6 pt-16 pb-12">
          <Link
            href="/forum"
            className="inline-flex items-center gap-2 text-neutral-500 hover:text-white text-xs font-bold uppercase tracking-widest mb-8 transition-colors"
          >
            <ArrowLeft size={14} />
            Back to Forum
          </Link>

          <div className="flex items-center gap-4 mb-4">
            <div className="w-14 h-14 rounded-2xl bg-gradient-to-br from-cyan-500/20 to-blue-500/20 border border-white/5 flex items-center justify-center">
              <Bookmark size={24} className="text-cyan-400" />
            </div>
            <div>
              <h1 className="text-4xl md:text-5xl font-black tracking-tighter italic uppercase">
                Saved <span className="text-cyan-400">Posts</span>
              </h1>
              <p className="text-neutral-500 font-medium uppercase text-xs tracking-[0.2em] mt-2">
                {totalCount} {totalCount === 1 ? 'post' : 'posts'} bookmarked
              </p>
            </div>
          </div>
        </div>
      </section>

      {/* Main Content */}
      <main className="max-w-4xl mx-auto px-6 py-12">
        {/* Error State */}
        {error && (
          <div className="p-4 rounded-xl bg-red-500/10 border border-red-500/20 mb-6">
            <p className="text-red-400 text-sm">{error}</p>
          </div>
        )}

        {/* Loading State */}
        {isLoading && (
          <div className="flex flex-col items-center justify-center py-20">
            <Loader2 size={32} className="text-cyan-400 animate-spin mb-4" />
            <p className="text-neutral-500 text-sm">Loading your saved posts...</p>
          </div>
        )}

        {/* Posts List */}
        {!isLoading && posts.length > 0 && (
          <div className="space-y-4">
            {posts.map((post, index) => (
              <motion.div
                key={post.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.03 }}
                className="relative group/card"
              >
                <PostCard
                  post={post}
                  onVote={handleVote}
                  showSubForum
                />
                {/* Remove Bookmark Button - appears on hover */}
                <button
                  onClick={() => handleRemoveBookmark(post.id)}
                  className="absolute top-3 right-3 z-10 opacity-0 group-hover/card:opacity-100 transition-opacity p-2 rounded-lg bg-neutral-900/90 border border-white/10 hover:border-red-500/30 hover:bg-red-500/10 text-neutral-400 hover:text-red-400"
                  title="Remove bookmark"
                >
                  <BookmarkX size={16} />
                </button>
              </motion.div>
            ))}

            {/* Load More Button */}
            {hasMore && (
              <div className="flex justify-center pt-6">
                <button
                  onClick={handleLoadMore}
                  disabled={isLoadingMore}
                  className="flex items-center gap-2 px-6 py-3 rounded-xl bg-neutral-900/50 border border-white/10 text-white text-[10px] font-black uppercase tracking-widest hover:border-cyan-500/30 hover:bg-cyan-500/10 transition-all disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {isLoadingMore ? (
                    <>
                      <Loader2 size={14} className="animate-spin" />
                      Loading...
                    </>
                  ) : (
                    <>
                      <ChevronDown size={14} />
                      Load More ({totalCount - posts.length} remaining)
                    </>
                  )}
                </button>
              </div>
            )}
          </div>
        )}

        {/* Empty State */}
        {!isLoading && posts.length === 0 && !error && (
          <div className="text-center py-20 rounded-2xl bg-neutral-900/30 border border-white/5">
            <div className="w-20 h-20 rounded-2xl bg-neutral-900/50 flex items-center justify-center mx-auto mb-6">
              <Bookmark size={32} className="text-neutral-600" />
            </div>
            <h3 className="text-lg font-bold text-white mb-2">No Saved Posts Yet</h3>
            <p className="text-neutral-500 text-sm mb-6 max-w-sm mx-auto">
              When you find posts worth keeping, click the bookmark icon to save them here for easy access.
            </p>
            <Link
              href="/forum"
              className="inline-flex items-center gap-2 px-6 py-3 rounded-xl bg-cyan-500 text-black text-[10px] font-black uppercase tracking-widest hover:bg-cyan-400 transition-all"
            >
              Browse Forum
            </Link>
          </div>
        )}
      </main>
    </div>
  );
}

