'use client';

import { useEffect, useState, useCallback } from 'react';
import { useParams } from 'next/navigation';
import Link from 'next/link';
import { api } from '@/lib/api';
import { ForumUserProfile, UserPostItem, UserCommentItem, VoteType } from '@/types/forum';
import { UserProfileCard } from '@/components/forum/UserProfileCard';
import { ForumHeader, ForumBreadcrumb, VoteButtons } from '@/components/forum';
import { formatDistanceToNow } from '@/lib/utils';
import { MessageSquare, FileText, User, Pin, Sparkles, ArrowUpRight } from 'lucide-react';

// ============================================
// SKELETON COMPONENTS
// ============================================

function ProfileSkeleton() {
  return (
    <div className="rounded-2xl bg-neutral-900/30 border border-white/5 overflow-hidden animate-pulse">
      <div className="h-24 bg-neutral-800" />
      <div className="px-6 pt-12 pb-6">
        <div className="h-7 w-40 bg-neutral-800 rounded mb-2" />
        <div className="h-4 w-32 bg-neutral-800 rounded mb-6" />
        <div className="grid grid-cols-3 gap-4">
          {[1, 2, 3].map((i) => (
            <div key={i} className="h-24 bg-neutral-800 rounded-xl" />
          ))}
        </div>
      </div>
    </div>
  );
}

function PostItemSkeleton() {
  return (
    <div className="p-4 rounded-xl bg-neutral-900/30 border border-white/5 animate-pulse">
      <div className="flex gap-4">
        <div className="w-10 space-y-2">
          <div className="h-5 w-5 bg-neutral-800 rounded mx-auto" />
          <div className="h-4 w-4 bg-neutral-800 rounded mx-auto" />
        </div>
        <div className="flex-1 space-y-2">
          <div className="h-5 w-3/4 bg-neutral-800 rounded" />
          <div className="h-4 w-full bg-neutral-800 rounded" />
          <div className="h-3 w-32 bg-neutral-800 rounded" />
        </div>
      </div>
    </div>
  );
}

function CommentItemSkeleton() {
  return (
    <div className="p-4 rounded-xl bg-neutral-900/30 border border-white/5 animate-pulse">
      <div className="flex gap-4">
        <div className="w-10 space-y-2">
          <div className="h-5 w-5 bg-neutral-800 rounded mx-auto" />
          <div className="h-4 w-4 bg-neutral-800 rounded mx-auto" />
        </div>
        <div className="flex-1 space-y-2">
          <div className="h-4 w-48 bg-neutral-800 rounded" />
          <div className="h-4 w-full bg-neutral-800 rounded" />
          <div className="h-3 w-24 bg-neutral-800 rounded" />
        </div>
      </div>
    </div>
  );
}

// ============================================
// POST ITEM COMPONENT
// ============================================

interface UserPostCardProps {
  post: UserPostItem;
  onVote?: (postId: string, voteType: VoteType) => void;
}

function UserPostCard({ post, onVote }: UserPostCardProps) {
  return (
    <article className="group relative rounded-xl bg-neutral-900/30 border border-white/5 hover:border-cyan-500/20 transition-all overflow-hidden">
      <div className="flex">
        {/* Vote column */}
        <div className="w-12 bg-neutral-900/50 border-r border-white/5 flex flex-col items-center py-4">
          <VoteButtons
            voteCount={post.voteCount}
            userVote={post.userVote}
            onVote={(voteType) => onVote?.(post.id, voteType)}
            size="sm"
          />
        </div>

        {/* Content */}
        <div className="flex-1 p-4 min-w-0">
          {/* Meta line */}
          <div className="flex items-center flex-wrap gap-2 mb-2">
            {post.isPinned && (
              <span className="flex items-center gap-1 px-2 py-0.5 rounded-md bg-cyan-500/10 border border-cyan-500/20 text-[9px] font-black uppercase tracking-wider text-cyan-400">
                <Pin size={10} />
                Pinned
              </span>
            )}
            {post.isGuide && (
              <span className="flex items-center gap-1 px-2 py-0.5 rounded-md bg-green-500/10 border border-green-500/20 text-[9px] font-black uppercase tracking-wider text-green-400">
                <Sparkles size={10} />
                Guide
              </span>
            )}
            <Link
              href={`/forum/${post.categorySlug}`}
              className="text-[10px] font-black uppercase tracking-widest text-cyan-400 hover:text-cyan-300"
            >
              {post.subForumSlug}
            </Link>
            <span className="text-[10px] text-neutral-700">|</span>
            <span className="text-[10px] text-neutral-600">{formatDistanceToNow(post.createdAt)}</span>
          </div>

          {/* Title */}
          <Link href={`/forum/post/${post.id}`}>
            <h3 className="text-base font-bold text-white group-hover:text-cyan-400 transition-colors leading-snug mb-2">
              {post.title}
            </h3>
          </Link>

          {/* Preview text */}
          {post.contentPreview && (
            <p className="text-neutral-500 text-sm line-clamp-2 mb-3 leading-relaxed">
              {post.contentPreview}
            </p>
          )}

          {/* Stats */}
          <div className="flex items-center gap-3">
            <span className="flex items-center gap-1.5 text-[10px] font-medium text-neutral-500">
              <MessageSquare size={12} />
              {post.commentCount}
            </span>
          </div>
        </div>
      </div>
    </article>
  );
}

// ============================================
// COMMENT ITEM COMPONENT
// ============================================

interface UserCommentCardProps {
  comment: UserCommentItem;
  onVote?: (commentId: string, voteType: VoteType) => void;
}

function UserCommentCard({ comment, onVote }: UserCommentCardProps) {
  return (
    <article className="group relative rounded-xl bg-neutral-900/30 border border-white/5 hover:border-cyan-500/20 transition-all overflow-hidden">
      <div className="flex">
        {/* Vote column */}
        <div className="w-12 bg-neutral-900/50 border-r border-white/5 flex flex-col items-center py-4">
          <VoteButtons
            voteCount={comment.voteCount}
            userVote={comment.userVote}
            onVote={(voteType) => onVote?.(comment.id, voteType)}
            size="sm"
          />
        </div>

        {/* Content */}
        <div className="flex-1 p-4 min-w-0">
          {/* Post reference */}
          <Link
            href={`/forum/post/${comment.postId}`}
            className="flex items-center gap-2 text-[10px] font-medium text-neutral-500 hover:text-cyan-400 transition-colors mb-2"
          >
            <ArrowUpRight size={12} />
            <span className="line-clamp-1">{comment.postTitle}</span>
          </Link>

          {/* Comment content */}
          <p className="text-neutral-300 text-sm leading-relaxed line-clamp-3 mb-3">
            {comment.content}
          </p>

          {/* Meta */}
          <div className="flex items-center gap-3">
            <span className="text-[10px] text-neutral-600">
              {formatDistanceToNow(comment.createdAt)}
            </span>
          </div>
        </div>
      </div>
    </article>
  );
}

// ============================================
// MAIN PAGE
// ============================================

type TabType = 'posts' | 'comments';

export default function UserProfilePage() {
  const params = useParams();
  const userId = params.userId as string;

  const [profile, setProfile] = useState<ForumUserProfile | null>(null);
  const [posts, setPosts] = useState<UserPostItem[]>([]);
  const [comments, setComments] = useState<UserCommentItem[]>([]);
  const [activeTab, setActiveTab] = useState<TabType>('posts');

  const [isLoadingProfile, setIsLoadingProfile] = useState(true);
  const [isLoadingContent, setIsLoadingContent] = useState(false);
  const [hasMorePosts, setHasMorePosts] = useState(true);
  const [hasMoreComments, setHasMoreComments] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const PAGE_SIZE = 20;

  // Fetch profile
  useEffect(() => {
    async function fetchProfile() {
      setIsLoadingProfile(true);
      setError(null);
      try {
        const data = await api.getForumUserProfile(userId);
        setProfile(data);
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to load profile');
      } finally {
        setIsLoadingProfile(false);
      }
    }

    fetchProfile();
  }, [userId]);

  // Fetch posts
  const fetchPosts = useCallback(async (offset = 0) => {
    setIsLoadingContent(true);
    try {
      const data = await api.getUserForumPosts(userId, { limit: PAGE_SIZE, offset });
      if (offset === 0) {
        setPosts(data.posts);
      } else {
        setPosts((prev) => [...prev, ...data.posts]);
      }
      setHasMorePosts(data.hasMore);
    } catch {
      // Silent fail for content loading
    } finally {
      setIsLoadingContent(false);
    }
  }, [userId]);

  // Fetch comments
  const fetchComments = useCallback(async (offset = 0) => {
    setIsLoadingContent(true);
    try {
      const data = await api.getUserForumComments(userId, { limit: PAGE_SIZE, offset });
      if (offset === 0) {
        setComments(data.comments);
      } else {
        setComments((prev) => [...prev, ...data.comments]);
      }
      setHasMoreComments(data.hasMore);
    } catch {
      // Silent fail for content loading
    } finally {
      setIsLoadingContent(false);
    }
  }, [userId]);

  // Fetch content when tab changes or on initial load
  useEffect(() => {
    if (activeTab === 'posts' && posts.length === 0) {
      fetchPosts();
    } else if (activeTab === 'comments' && comments.length === 0) {
      fetchComments();
    }
  }, [activeTab, posts.length, comments.length, fetchPosts, fetchComments]);

  const handleLoadMore = () => {
    if (activeTab === 'posts' && hasMorePosts) {
      fetchPosts(posts.length);
    } else if (activeTab === 'comments' && hasMoreComments) {
      fetchComments(comments.length);
    }
  };

  const handleVotePost = async (postId: string, voteType: VoteType) => {
    try {
      const result = await api.voteForumPost(postId, voteType);
      setPosts((prev) =>
        prev.map((p) =>
          p.id === postId
            ? { ...p, voteCount: result.newVoteCount, userVote: result.userVote }
            : p
        )
      );
    } catch {
      // Silent fail
    }
  };

  const handleVoteComment = async (commentId: string, voteType: VoteType) => {
    try {
      const result = await api.voteForumComment(commentId, voteType);
      setComments((prev) =>
        prev.map((c) =>
          c.id === commentId
            ? { ...c, voteCount: result.newVoteCount, userVote: result.userVote }
            : c
        )
      );
    } catch {
      // Silent fail
    }
  };

  return (
    <div className="min-h-screen bg-black selection:bg-cyan-500/30">
      <ForumHeader />
      <ForumBreadcrumb
        items={[
          { label: 'Community', href: '/forum' },
          { label: profile?.username || 'User Profile' },
        ]}
      />

      {/* Content */}
      <div className="max-w-4xl mx-auto px-6 py-10">
        {/* Error */}
        {error && (
          <div className="p-4 rounded-xl bg-red-500/10 border border-red-500/20 mb-6">
            <p className="text-red-400 text-sm">{error}</p>
          </div>
        )}

        {/* Profile Card */}
        {isLoadingProfile ? (
          <ProfileSkeleton />
        ) : profile ? (
          <UserProfileCard profile={profile} variant="full" />
        ) : !error ? (
          <div className="text-center py-16 rounded-2xl bg-neutral-900/30 border border-white/5">
            <User size={48} className="mx-auto text-neutral-700 mb-4" />
            <h2 className="text-xl font-black text-white mb-3">User not found</h2>
            <p className="text-neutral-400 mb-6">
              This user doesn&apos;t exist or has been banned.
            </p>
            <Link
              href="/forum"
              className="inline-flex items-center gap-2 px-6 py-3 bg-cyan-500 text-black text-[10px] font-black uppercase tracking-widest rounded-xl hover:bg-cyan-400 transition-all"
            >
              Go back to forum
            </Link>
          </div>
        ) : null}

        {/* Tabs */}
        {profile && (
          <>
            <div className="flex gap-2 mt-8 mb-6">
              <button
                onClick={() => setActiveTab('posts')}
                className={`flex items-center gap-2 px-5 py-2.5 rounded-xl text-[10px] font-black uppercase tracking-widest transition-all ${
                  activeTab === 'posts'
                    ? 'bg-cyan-500 text-black'
                    : 'bg-neutral-900/30 text-neutral-400 border border-white/5 hover:border-cyan-500/20 hover:text-cyan-400'
                }`}
              >
                <FileText size={14} />
                Posts ({profile.forumPostsCount})
              </button>
              <button
                onClick={() => setActiveTab('comments')}
                className={`flex items-center gap-2 px-5 py-2.5 rounded-xl text-[10px] font-black uppercase tracking-widest transition-all ${
                  activeTab === 'comments'
                    ? 'bg-cyan-500 text-black'
                    : 'bg-neutral-900/30 text-neutral-400 border border-white/5 hover:border-cyan-500/20 hover:text-cyan-400'
                }`}
              >
                <MessageSquare size={14} />
                Comments ({profile.forumCommentsCount})
              </button>
            </div>

            {/* Content */}
            {activeTab === 'posts' && (
              <div className="space-y-4">
                {isLoadingContent && posts.length === 0 ? (
                  <>
                    {[1, 2, 3].map((i) => (
                      <PostItemSkeleton key={i} />
                    ))}
                  </>
                ) : posts.length > 0 ? (
                  <>
                    {posts.map((post) => (
                      <UserPostCard key={post.id} post={post} onVote={handleVotePost} />
                    ))}
                    {hasMorePosts && (
                      <button
                        onClick={handleLoadMore}
                        disabled={isLoadingContent}
                        className="w-full py-4 rounded-xl bg-neutral-900/30 border border-white/5 text-neutral-400 text-[10px] font-black uppercase tracking-widest hover:border-cyan-500/20 hover:text-cyan-400 transition-all disabled:opacity-50"
                      >
                        {isLoadingContent ? 'Loading...' : 'Load More Posts'}
                      </button>
                    )}
                  </>
                ) : (
                  <div className="text-center py-12 rounded-2xl bg-neutral-900/30 border border-white/5">
                    <FileText size={32} className="mx-auto text-neutral-700 mb-3" />
                    <p className="text-neutral-400">No posts yet</p>
                  </div>
                )}
              </div>
            )}

            {activeTab === 'comments' && (
              <div className="space-y-4">
                {isLoadingContent && comments.length === 0 ? (
                  <>
                    {[1, 2, 3].map((i) => (
                      <CommentItemSkeleton key={i} />
                    ))}
                  </>
                ) : comments.length > 0 ? (
                  <>
                    {comments.map((comment) => (
                      <UserCommentCard
                        key={comment.id}
                        comment={comment}
                        onVote={handleVoteComment}
                      />
                    ))}
                    {hasMoreComments && (
                      <button
                        onClick={handleLoadMore}
                        disabled={isLoadingContent}
                        className="w-full py-4 rounded-xl bg-neutral-900/30 border border-white/5 text-neutral-400 text-[10px] font-black uppercase tracking-widest hover:border-cyan-500/20 hover:text-cyan-400 transition-all disabled:opacity-50"
                      >
                        {isLoadingContent ? 'Loading...' : 'Load More Comments'}
                      </button>
                    )}
                  </>
                ) : (
                  <div className="text-center py-12 rounded-2xl bg-neutral-900/30 border border-white/5">
                    <MessageSquare size={32} className="mx-auto text-neutral-700 mb-3" />
                    <p className="text-neutral-400">No comments yet</p>
                  </div>
                )}
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import Link from 'next/link';
import {
  Shield,
  AlertTriangle,
  CheckCircle,
  Clock,
  TrendingUp,
  Users,
  ArrowLeft,
  ExternalLink,
} from 'lucide-react';
import { api } from '@/lib/api';
import { ReportQueue } from '@/components/admin/ReportQueue';
import type { ModerationStats } from '@/types/forum';

export default function ModerationDashboard() {
  const router = useRouter();
  const [stats, setStats] = useState<ModerationStats | null>(null);
  const [isLoadingStats, setIsLoadingStats] = useState(true);
  const [statsError, setStatsError] = useState<string | null>(null);
  const [isAuthorized, setIsAuthorized] = useState<boolean | null>(null);

  useEffect(() => {
    // Check if user is authenticated and is admin
    const checkAuth = async () => {
      try {
        const response = await api.getModerationStats();
        setStats(response);
        setIsAuthorized(true);
      } catch (err) {
        if (err instanceof Error && err.message.includes('403')) {
          setIsAuthorized(false);
        } else if (err instanceof Error && err.message.includes('401')) {
          // Not logged in, redirect to login
          router.push('/login?redirect=/admin/moderation');
          return;
        } else {
          setStatsError(err instanceof Error ? err.message : 'Failed to load stats');
          setIsAuthorized(true); // Assume authorized but error occurred
        }
      } finally {
        setIsLoadingStats(false);
      }
    };

    checkAuth();
  }, [router]);

  const handleViewContent = (targetType: 'post' | 'comment', targetId: string) => {
    if (targetType === 'post') {
      window.open(`/forum/post/${targetId}`, '_blank');
    } else {
      // For comments, we'd need to know the post ID - for now just alert
      alert(`Comment ID: ${targetId}\nView in forum to see full context.`);
    }
  };

  // Loading state
  if (isAuthorized === null) {
    return (
      <div className="min-h-screen bg-black flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-2 border-cyan-500 border-t-transparent rounded-full animate-spin mx-auto mb-4" />
          <p className="text-zinc-400">Checking authorization...</p>
        </div>
      </div>
    );
  }

  // Unauthorized state
  if (isAuthorized === false) {
    return (
      <div className="min-h-screen bg-black flex items-center justify-center">
        <div className="text-center max-w-md mx-auto p-8">
          <Shield className="w-16 h-16 text-red-500 mx-auto mb-4" />
          <h1 className="text-2xl font-bold text-white mb-2">Access Denied</h1>
          <p className="text-zinc-400 mb-6">
            You do not have permission to access the moderation dashboard. This area is restricted to administrators only.
          </p>
          <Link
            href="/"
            className="inline-flex items-center gap-2 px-4 py-2 bg-zinc-800 hover:bg-zinc-700 text-white rounded-lg transition-colors"
          >
            <ArrowLeft className="w-4 h-4" />
            Return Home
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-black">
      {/* Header */}
      <header className="sticky top-0 z-50 bg-black/80 backdrop-blur-sm border-b border-zinc-800">
        <div className="max-w-7xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <Link
                href="/forum"
                className="p-2 text-zinc-400 hover:text-white hover:bg-zinc-800 rounded-lg transition-colors"
              >
                <ArrowLeft className="w-5 h-5" />
              </Link>
              <div>
                <h1 className="text-xl font-bold text-white flex items-center gap-2">
                  <Shield className="w-5 h-5 text-cyan-400" />
                  Moderation Dashboard
                </h1>
                <p className="text-sm text-zinc-400">Manage reports and moderate content</p>
              </div>
            </div>
            <Link
              href="/admin/moderation/banned"
              className="flex items-center gap-2 px-4 py-2 bg-zinc-800 hover:bg-zinc-700 text-zinc-300 rounded-lg transition-colors"
            >
              <Users className="w-4 h-4" />
              Banned Users
              <ExternalLink className="w-3 h-3" />
            </Link>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 py-8">
        {/* Stats Cards */}
        {isLoadingStats ? (
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="h-24 bg-zinc-900 rounded-lg animate-pulse" />
            ))}
          </div>
        ) : statsError ? (
          <div className="mb-8 p-4 bg-red-500/10 border border-red-500/30 rounded-lg text-red-400">
            {statsError}
          </div>
        ) : stats ? (
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
            {/* Pending Reports */}
            <div className="p-4 bg-zinc-900 border border-zinc-800 rounded-lg">
              <div className="flex items-center gap-3 mb-2">
                <div className="p-2 bg-yellow-500/10 rounded-lg">
                  <AlertTriangle className="w-5 h-5 text-yellow-400" />
                </div>
                <span className="text-sm text-zinc-400">Pending</span>
              </div>
              <p className="text-2xl font-bold text-white">{stats.pendingReports}</p>
            </div>

            {/* Resolved Today */}
            <div className="p-4 bg-zinc-900 border border-zinc-800 rounded-lg">
              <div className="flex items-center gap-3 mb-2">
                <div className="p-2 bg-green-500/10 rounded-lg">
                  <CheckCircle className="w-5 h-5 text-green-400" />
                </div>
                <span className="text-sm text-zinc-400">Resolved Today</span>
              </div>
              <p className="text-2xl font-bold text-white">{stats.resolvedToday}</p>
            </div>

            {/* Resolved This Week */}
            <div className="p-4 bg-zinc-900 border border-zinc-800 rounded-lg">
              <div className="flex items-center gap-3 mb-2">
                <div className="p-2 bg-cyan-500/10 rounded-lg">
                  <TrendingUp className="w-5 h-5 text-cyan-400" />
                </div>
                <span className="text-sm text-zinc-400">This Week</span>
              </div>
              <p className="text-2xl font-bold text-white">{stats.resolvedThisWeek}</p>
            </div>

            {/* Total Reports */}
            <div className="p-4 bg-zinc-900 border border-zinc-800 rounded-lg">
              <div className="flex items-center gap-3 mb-2">
                <div className="p-2 bg-purple-500/10 rounded-lg">
                  <Clock className="w-5 h-5 text-purple-400" />
                </div>
                <span className="text-sm text-zinc-400">Total Reports</span>
              </div>
              <p className="text-2xl font-bold text-white">{stats.totalReports}</p>
            </div>
          </div>
        ) : null}

        {/* Reports by Reason (mini chart) */}
        {stats && Object.keys(stats.reportsByReason).length > 0 && (
          <div className="mb-8 p-4 bg-zinc-900 border border-zinc-800 rounded-lg">
            <h3 className="text-sm font-medium text-zinc-400 mb-3">Reports by Reason</h3>
            <div className="flex flex-wrap gap-3">
              {Object.entries(stats.reportsByReason).map(([reason, count]) => (
                <div
                  key={reason}
                  className="px-3 py-1.5 bg-zinc-800 rounded-lg text-sm"
                >
                  <span className="text-zinc-400 capitalize">{reason.replace('_', ' ')}</span>
                  <span className="ml-2 text-white font-medium">{count}</span>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Report Queue */}
        <div>
          <h2 className="text-lg font-semibold text-white mb-4">Report Queue</h2>
          <ReportQueue onViewContent={handleViewContent} />
        </div>
      </main>
    </div>
  );
}

'use client';

import Link from 'next/link';
import { User, MessageSquare, ArrowUpCircle, Calendar } from 'lucide-react';
import { ForumUserProfile } from '@/types/forum';
import { formatDistanceToNow } from '@/lib/utils';

interface UserProfileCardProps {
  profile: ForumUserProfile;
  variant?: 'full' | 'compact';
}

/**
 * Reusable user profile card component
 * - Full variant: Shows complete profile with karma, post/comment counts
 * - Compact variant: Minimal display for sidebar/inline use
 */
export function UserProfileCard({ profile, variant = 'full' }: UserProfileCardProps) {
  if (variant === 'compact') {
    return (
      <Link
        href={`/forum/user/${profile.id}`}
        className="flex items-center gap-3 p-3 rounded-xl bg-neutral-900/30 border border-white/5 hover:border-cyan-500/20 transition-all group"
      >
        <div className="w-10 h-10 rounded-full bg-gradient-to-br from-cyan-500/20 to-purple-500/20 flex items-center justify-center border border-white/10">
          <User size={18} className="text-cyan-400" />
        </div>
        <div className="flex-1 min-w-0">
          <p className="text-sm font-bold text-white group-hover:text-cyan-400 transition-colors truncate">
            u/{profile.username}
          </p>
          <p className="text-[10px] text-neutral-500">
            {profile.forumKarma.toLocaleString()} karma
          </p>
        </div>
      </Link>
    );
  }

  return (
    <div className="rounded-2xl bg-neutral-900/30 border border-white/5 overflow-hidden">
      {/* Header with gradient */}
      <div className="h-24 bg-gradient-to-br from-cyan-500/10 via-purple-500/10 to-pink-500/10 relative">
        <div className="absolute -bottom-8 left-6">
          <div className="w-16 h-16 rounded-full bg-neutral-900 border-4 border-neutral-900 flex items-center justify-center">
            <div className="w-14 h-14 rounded-full bg-gradient-to-br from-cyan-500/20 to-purple-500/20 flex items-center justify-center">
              <User size={28} className="text-cyan-400" />
            </div>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="px-6 pt-12 pb-6">
        {/* Username */}
        <h2 className="text-xl font-black text-white mb-1">
          u/{profile.username}
        </h2>

        {/* Member since */}
        <div className="flex items-center gap-1.5 text-neutral-500 text-xs mb-6">
          <Calendar size={12} />
          <span>Member since {formatDistanceToNow(profile.memberSince)}</span>
        </div>

        {/* Stats grid */}
        <div className="grid grid-cols-3 gap-4">
          {/* Karma */}
          <div className="text-center p-4 rounded-xl bg-neutral-900/50 border border-white/5">
            <div className="flex items-center justify-center gap-1.5 mb-2">
              <ArrowUpCircle size={16} className="text-cyan-400" />
            </div>
            <p className="text-lg font-black text-white">
              {profile.forumKarma.toLocaleString()}
            </p>
            <p className="text-[10px] font-medium uppercase tracking-widest text-neutral-500">
              Karma
            </p>
          </div>

          {/* Posts */}
          <div className="text-center p-4 rounded-xl bg-neutral-900/50 border border-white/5">
            <div className="flex items-center justify-center gap-1.5 mb-2">
              <MessageSquare size={16} className="text-purple-400" />
            </div>
            <p className="text-lg font-black text-white">
              {profile.forumPostsCount.toLocaleString()}
            </p>
            <p className="text-[10px] font-medium uppercase tracking-widest text-neutral-500">
              Posts
            </p>
          </div>

          {/* Comments */}
          <div className="text-center p-4 rounded-xl bg-neutral-900/50 border border-white/5">
            <div className="flex items-center justify-center gap-1.5 mb-2">
              <MessageSquare size={16} className="text-emerald-400" />
            </div>
            <p className="text-lg font-black text-white">
              {profile.forumCommentsCount.toLocaleString()}
            </p>
            <p className="text-[10px] font-medium uppercase tracking-widest text-neutral-500">
              Comments
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

/**
 * Inline username link that navigates to user profile
 */
interface UsernameLinkProps {
  userId: string;
  username: string;
  className?: string;
}

export function UsernameLink({ userId, username, className = '' }: UsernameLinkProps) {
  return (
    <Link
      href={`/forum/user/${userId}`}
      className={`hover:text-cyan-400 transition-colors ${className}`}
    >
      u/{username}
    </Link>
  );
}

