   103→// === FORUM CONTEXT ===
   104→
   105→interface ForumContextType {
   106→  // Categories
   107→  categories: Category[];
   108→  currentCategory: Category | null;
   109→  isLoadingCategories: boolean;
   110→  fetchCategories: () => Promise<void>;
