   194→
   195→  const clearError = useCallback(() => setError(null), []);
   196→
   197→  // === CATEGORIES ===
   198→
   199→  const fetchCategories = useCallback(async () => {
   200→    setIsLoadingCategories(true);
   201→    setError(null);
   202→    try {
   203→      const data = await api.getForumCategories();
   204→      setCategories(data);
