   207→    } finally {
   208→      setIsLoadingCategories(false);
   209→    }
   210→  }, []);
   211→
   212→  const fetchCategory = useCallback(async (slug: string) => {
   213→    setIsLoadingCategories(true);
   214→    setError(null);
   215→    try {
   216→      const data = await api.getForumCategory(slug);
   217→      setCurrentCategory(data);
