  1403→/**
  1404→ * Get the appropriate metric configuration for a given gender and ethnicity
  1405→ * Falls back to base config if no override exists
  1406→ */
  1407→export function getMetricConfig(
  1408→  metricId: string,
  1409→  gender: Gender = 'male',
  1410→  ethnicity: Ethnicity = 'white'
  1411→): MasterMetricConfig | undefined {
  1412→  const key = `${gender}_${ethnicity}`;
  1413→  const override = ETHNICITY_OVERRIDES[key]?.[metricId];
  1414→  const baseConfig = MASTER_SCORING_DB[metricId];
  1415→
  1416→  if (!baseConfig) return undefined;
  1417→
  1418→  // Merge override with base config
  1419→  if (override) {
  1420→    return {
  1421→      ...baseConfig,
  1422→      ...override,
  1423→      flaws: {
  1424→        ...baseConfig.flaws,
  1425→        ...override.flaws
  1426→      }
  1427→    } as MasterMetricConfig;
  1428→  }
  1429→
  1430→  return baseConfig;
  1431→}
  1432→
  1433→// ============================================
  1434→// Z-SCORE SEVERITY FUNCTIONS
  1435→// ============================================
  1436→
  1437→/**
  1438→ * Calculate Z-score for a given value
  1439→ * Z = (value - mean) / std_dev
  1440→ */
  1441→export function calculateZScore(value: number, mean: number, stdDev: number): number {
  1442→  if (stdDev === 0) return 0;
  1443→  return Math.abs((value - mean) / stdDev);
  1444→}
  1445→
  1446→/**
  1447→ * Determine severity level based on Z-score and ideal range (5-tier system)
  1448→ * - Ideal: Value is within ideal range
  1449→ * - Good: |z| < 0.5 (within 0.5 standard deviation)
  1450→ * - Fair: 0.5 ≤ |z| < 1 (between 0.5-1 standard deviations)
  1451→ * - Moderate: 1 ≤ |z| < 2 (between 1-2 standard deviations)
  1452→ * - Severe: |z| ≥ 2 (more than 2 standard deviations away)
  1453→ */
  1454→export function getSeverityFromZScore(
  1455→  value: number,
  1456→  config: MasterMetricConfig
  1457→): { severity: SeverityLevel; zScore: number; isInIdeal: boolean } {
  1458→  const { ideal, mean, std_dev } = config;
  1459→  const isInIdeal = value >= ideal[0] && value <= ideal[1];
  1460→  const zScore = calculateZScore(value, mean, std_dev);
  1461→
  1462→  let severity: SeverityLevel;
  1463→  if (isInIdeal) {
  1464→    severity = 'ideal';
  1465→  } else if (zScore < 0.5) {
  1466→    severity = 'good';
  1467→  } else if (zScore < 1) {
  1468→    severity = 'fair';
  1469→  } else if (zScore < 2) {
  1470→    severity = 'moderate';
  1471→  } else {
  1472→    severity = 'severe';
  1473→  }
  1474→
  1475→  return { severity, zScore, isInIdeal };
  1476→}
  1477→
  1478→/**
  1479→ * Get severity for a metric value with ethnicity/gender awareness
  1480→ */
  1481→export function getSeverityForMetric(
  1482→  metricId: string,
  1483→  value: number,
  1484→  gender: Gender = 'male',
  1485→  ethnicity: Ethnicity = 'white'
  1486→): { severity: SeverityLevel; zScore: number; isInIdeal: boolean; config: MasterMetricConfig } | null {
  1487→  const config = getMetricConfig(metricId, gender, ethnicity);
  1488→  if (!config) return null;
  1489→
  1490→  const result = getSeverityFromZScore(value, config);
  1491→  return { ...result, config };
  1492→}
  1493→
  1494→/**
  1495→ * Get badge color for severity level (5-tier)
  1496→ */
  1497→export function getSeverityColor(severity: SeverityLevel): string {
  1498→  switch (severity) {
  1499→    case 'ideal': return 'green';
  1500→    case 'good': return 'blue';
  1501→    case 'fair': return 'cyan';
  1502→    case 'moderate': return 'yellow';
  1503→    case 'severe': return 'red';
  1504→    default: return 'gray';
  1505→  }
  1506→}
  1507→
  1508→/**
  1509→ * Get severity label for display (5-tier)
  1510→ */
  1511→export function getSeverityLabel(severity: SeverityLevel): string {
  1512→  switch (severity) {
  1513→    case 'ideal': return 'Ideal';
  1514→    case 'good': return 'Good';
  1515→    case 'fair': return 'Fair';
  1516→    case 'moderate': return 'Moderate';
  1517→    case 'severe': return 'Severe';
  1518→    default: return 'Unknown';
  1519→  }
  1520→}
