  2340→// ============================================
  2341→// CLASSIFICATION FUNCTIONS
  2342→// ============================================
  2343→
  2344→/**
  2345→ * Get weakness severity based on average score
  2346→ * 0-3 = "Severe", 3-5 = "Moderate", 5-7 = "Minor"
  2347→ */
  2348→function getWeaknessSeverity(avgScore: number): WeaknessSeverity {
  2349→  if (avgScore <= 3) return 'severe';
  2350→  if (avgScore <= 5) return 'moderate';
  2351→  return 'minor';
  2352→}
  2353→
  2354→/**
  2355→ * Get strength grade based on average score
  2356→ * 8-9 = "Good", 9-9.5 = "Excellent", 9.5-10 = "Ideal"
  2357→ */
  2358→function getStrengthGrade(avgScore: number): StrengthGrade {
  2359→  if (avgScore >= 9.5) return 'ideal';
  2360→  if (avgScore >= 9) return 'excellent';
  2361→  return 'good';
  2362→}
  2363→
  2364→/**
  2365→ * Find metric by display name from measurements array
  2366→ */
  2367→function findMetricByName(
  2368→  metricName: string,
  2369→  measurements: MetricScoreResult[]
  2370→): MetricScoreResult | undefined {
  2371→  // First try direct ID mapping
  2372→  const metricId = METRIC_NAME_TO_ID[metricName];
  2373→  if (metricId) {
  2374→    return measurements.find(m => m.metricId === metricId);
  2375→  }
  2376→
  2377→  // Fallback: try fuzzy matching on name
  2378→  const normalizedName = metricName.toLowerCase().replace(/[^a-z0-9]/g, '');
  2379→  return measurements.find(m => {
  2380→    const normalizedMetricName = m.name.toLowerCase().replace(/[^a-z0-9]/g, '');
  2381→    return normalizedMetricName.includes(normalizedName) ||
  2382→           normalizedName.includes(normalizedMetricName);
  2383→  });
  2384→}
  2385→
  2386→/**
  2387→ * Check if a metric should be considered a "false positive" weakness.
  2388→ * This handles directional/dimorphic metrics where low scores don't mean weakness.
  2389→ *
  2390→ * Example: Canthal Tilt of 3° gets a low score (not in 6-8° ideal range)
  2391→ * but is still a POSITIVE trait, not a weakness.
  2392→ */
  2393→function isFalsePositiveWeakness(metric: MatchedMetric): boolean {
  2394→  const config = METRIC_CONFIGS[metric.metricId];
  2395→  if (!config) return false;
  2396→
  2397→  // Check if the value is actually acceptable based on polarity
  2398→  const { acceptable } = isValueAcceptable(metric.value, config);
  2399→  return acceptable;
  2400→}
  2401→
  2402→/**
  2403→ * Process all insight definitions against user's measurements
  2404→ */
  2405→export function classifyInsights(
  2406→  measurements: MetricScoreResult[],
  2407→  insightDefinitions: InsightDefinition[] = INSIGHTS_DEFINITIONS
  2408→): InsightClassificationResult {
  2409→  const strengths: ClassifiedStrength[] = [];
  2410→  const weaknesses: ClassifiedWeakness[] = [];
  2411→
  2412→  for (const insight of insightDefinitions) {
  2413→    const { metrics, threshold } = insight.severity_logic;
  2414→
  2415→    // Find matching measurements for this insight
  2416→    const matchedMetrics: MatchedMetric[] = [];
  2417→    for (const metricName of metrics) {
  2418→      const measurement = findMetricByName(metricName, measurements);
  2419→      if (measurement) {
  2420→        matchedMetrics.push({
  2421→          metricId: measurement.metricId,
  2422→          metricName: measurement.name,
  2423→          score: measurement.score,
  2424→          value: measurement.value,
  2425→          idealMin: measurement.idealMin,
  2426→          idealMax: measurement.idealMax,
  2427→          unit: measurement.unit,
  2428→          category: measurement.category,
  2429→        });
  2430→      }
  2431→    }
  2432→
  2433→    // Skip if no metrics matched (need at least 1)
  2434→    if (matchedMetrics.length === 0) continue;
  2435→
  2436→    // Calculate average score
  2437→    const avgScore = matchedMetrics.reduce((sum, m) => sum + m.score, 0) / matchedMetrics.length;
  2438→
  2439→    // Classify based on type and threshold
  2440→    if (insight.type === 'weakness') {
  2441→      // Weakness: avg < threshold → add to areas of improvement
  2442→      if (avgScore < threshold) {
  2443→        // ========== FALSE POSITIVE CHECK ==========
  2444→        // Before adding to weaknesses, check if ANY of the matched metrics
  2445→        // are actually in an "acceptable" zone based on their polarity.
  2446→        // If so, this is a false positive and should be skipped or reclassified.
  2447→        const falsePositiveMetrics = matchedMetrics.filter(isFalsePositiveWeakness);
  2448→
  2449→        // If ALL matched metrics are false positives, skip this weakness entirely
  2450→        if (falsePositiveMetrics.length === matchedMetrics.length) {
  2451→          // This is a false positive weakness - the values are actually acceptable
  2452→          // Consider adding to strengths if scores are decent
  2453→          if (avgScore >= 6) {
  2454→            strengths.push({
  2455→              insightId: `${insight.id}_acceptable`,
  2456→              title: insight.title.replace('Negative', 'Neutral').replace('Weak', 'Moderate'),
  2457→              description: `While not in the peak ideal range, the measured values are within acceptable limits.`,
  2458→              grade: 'good',
  2459→              gradeLabel: 'Acceptable',
  2460→              avgScore,
  2461→              matchedMetrics,
  2462→            });
  2463→          }
  2464→          continue; // Skip adding as weakness
  2465→        }
  2466→
  2467→        // If SOME metrics are false positives, filter them out
  2468→        const trueWeaknessMetrics = matchedMetrics.filter(m => !isFalsePositiveWeakness(m));
  2469→        if (trueWeaknessMetrics.length === 0) continue;
  2470→
  2471→        // Recalculate average with only true weakness metrics
  2472→        const trueAvgScore = trueWeaknessMetrics.reduce((sum, m) => sum + m.score, 0) / trueWeaknessMetrics.length;
  2473→
  2474→        // Only add as weakness if true average is still below threshold
  2475→        if (trueAvgScore < threshold) {
  2476→          const severity = getWeaknessSeverity(trueAvgScore);
  2477→          const labels = insight.content.severity_labels;
  2478→          const severityLabel = labels?.[severity] || severity;
  2479→
  2480→          weaknesses.push({
  2481→            insightId: insight.id,
  2482→            title: insight.title,
  2483→            description: insight.content.description,
  2484→            severity,
  2485→            severityLabel,
  2486→            avgScore: trueAvgScore,
  2487→            matchedMetrics: trueWeaknessMetrics,
  2488→          });
  2489→        }
  2490→      }
  2491→    } else {
  2492→      // Strength: avg > threshold → add to key strengths
  2493→      if (avgScore > threshold) {
  2494→        const grade = getStrengthGrade(avgScore);
  2495→        const labels = insight.content.grade_labels;
  2496→        const gradeLabel = labels?.[grade] || grade;
  2497→
  2498→        strengths.push({
  2499→          insightId: insight.id,
  2500→          title: insight.title,
  2501→          description: insight.content.description,
  2502→          grade,
  2503→          gradeLabel,
  2504→          avgScore,
  2505→          matchedMetrics,
  2506→        });
  2507→      }
  2508→    }
  2509→  }
  2510→
  2511→  // Sort by score (weaknesses by lowest score first, strengths by highest first)
  2512→  weaknesses.sort((a, b) => a.avgScore - b.avgScore);
  2513→  strengths.sort((a, b) => b.avgScore - a.avgScore);
  2514→
  2515→  return { strengths, weaknesses };
  2516→}
  2517→
  2518→/**
  2519→ * Convert ClassifiedStrength to Strength type for UI compatibility
  2520→ */
  2521→export function convertToStrength(classified: ClassifiedStrength): Strength {
  2522→  return {
  2523→    id: `insight_strength_${classified.insightId}`,
  2524→    strengthName: `${classified.title} (${classified.gradeLabel})`,
  2525→    summary: classified.description,
  2526→    avgScore: classified.avgScore,
  2527→    qualityLevel: classified.grade === 'ideal' ? 'ideal' :
  2528→                  classified.grade === 'excellent' ? 'excellent' : 'good',
  2529→    categoryName: classified.matchedMetrics[0]?.category || 'General',
  2530→    responsibleRatios: classified.matchedMetrics.map(m => ({
  2531→      ratioName: m.metricName,
  2532→      ratioId: m.metricId,
  2533→      score: m.score,
  2534→      value: m.value,
  2535→      idealMin: m.idealMin,
  2536→      idealMax: m.idealMax,
  2537→      unit: m.unit,
  2538→      category: m.category,
  2539→    })),
  2540→  };
  2541→}
  2542→
  2543→/**
  2544→ * Convert ClassifiedWeakness to Flaw type for UI compatibility
  2545→ */
  2546→export function convertToFlaw(classified: ClassifiedWeakness, index: number, rollingLost: number): Flaw {
  2547→  const impact = (10 - classified.avgScore) * 0.5;
  2548→  const newRollingLost = rollingLost + impact;
  2549→
  2550→  return {
  2551→    id: `insight_flaw_${classified.insightId}`,
  2552→    flawName: `${classified.title} (${classified.severityLabel})`,
  2553→    summary: classified.description,
  2554→    harmonyPercentageLost: impact,
  2555→    standardizedImpact: impact / 10,
  2556→    categoryName: classified.matchedMetrics[0]?.category || 'General',
  2557→    responsibleRatios: classified.matchedMetrics.map(m => ({
  2558→      ratioName: m.metricName,
  2559→      ratioId: m.metricId,
  2560→      score: m.score,
  2561→      value: m.value,
  2562→      idealMin: m.idealMin,
  2563→      idealMax: m.idealMax,
  2564→      unit: m.unit,
  2565→      category: m.category,
  2566→    })),
  2567→    rollingPointsDeducted: newRollingLost,
  2568→    rollingHarmonyPercentageLost: newRollingLost,
  2569→    rollingStandardizedImpact: newRollingLost / 10,
  2570→  };
  2571→}
  2572→
  2573→/**
  2574→ * Main function: Generate strengths and flaws from measurements using insights
  2575→ */
  2576→export function generateInsightBasedResults(
  2577→  measurements: MetricScoreResult[]
  2578→): { strengths: Strength[]; flaws: Flaw[] } {
  2579→  const { strengths: classifiedStrengths, weaknesses: classifiedWeaknesses } = classifyInsights(measurements);
  2580→
  2581→  // Convert to UI-compatible types
  2582→  const strengths = classifiedStrengths.map((s) => convertToStrength(s));
  2583→
  2584→  let rollingLost = 0;
  2585→  const flaws = classifiedWeaknesses.map((w, i) => {
  2586→    const flaw = convertToFlaw(w, i, rollingLost);
  2587→    rollingLost = flaw.rollingPointsDeducted || 0;
  2588→    return flaw;
  2589→  });
  2590→
  2591→  return { strengths, flaws };
  2592→}
  2593→

