     1→"use client";
     2→
     3→import { useState, useEffect, useCallback, Suspense } from "react";
     4→import { useRouter, useSearchParams } from "next/navigation";
     5→import Link from "next/link";
     6→import { Eye, EyeOff } from "lucide-react";
     7→import { api } from "@/lib/api";
     8→
     9→function CheckIcon({ className }: { className?: string }) {
    10→  return (
    11→    <svg className={className} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
    12→      <polyline points="20 6 9 17 4 12" />
    13→    </svg>
    14→  );
    15→}
    16→
    17→function XIcon({ className }: { className?: string }) {
    18→  return (
    19→    <svg className={className} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
    20→      <line x1="18" y1="6" x2="6" y2="18" />
    21→      <line x1="6" y1="6" x2="18" y2="18" />
    22→    </svg>
    23→  );
    24→}
    25→
    26→function LoaderIcon({ className }: { className?: string }) {
    27→  return (
    28→    <svg className={`${className} animate-spin`} viewBox="0 0 24 24" fill="none">
    29→      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
    30→      <path
    31→        className="opacity-75"
