import {
  GUIDE_CATEGORIES,
  getGuidesByCategory,
  getTotalGuideCount,
  getTotalReadTime,
  getGuideProductById,
  getGuidesByGender,
} from '@/data/guides';
import { Guide, GuideCategory, GuideMedia } from '@/types/guides';
import { useRegion } from '@/contexts/RegionContext';
import { useResults } from '@/contexts/ResultsContext';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';

// ============================================
// ICON MAPPING
// ============================================

const ICON_MAP: Record<string, React.ReactNode> = {
  BookOpen: <BookOpen size={20} />,
  Dumbbell: <Dumbbell size={20} />,
  Sparkles: <Sparkles size={20} />,
  Brain: <Brain size={20} />,
  Wrench: <Wrench size={20} />,
  TrendingDown: <TrendingDown size={20} />,
  Calendar: <Calendar size={20} />,
  Target: <Target size={20} />,
  Heart: <Heart size={20} />,
  Utensils: <Utensils size={20} />,
  Droplet: <Droplet size={20} />,
};

function getIconComponent(iconName: string, size = 20): React.ReactNode {
  // Check icon map first
  const mappedIcon = ICON_MAP[iconName];
  if (mappedIcon) return mappedIcon;

  // Fallback to BookOpen
  return <BookOpen size={size} />;
}

// ============================================
// MEDIA RENDERER
// ============================================

interface MediaRendererProps {
  media: GuideMedia;
}

function MediaRenderer({ media }: MediaRendererProps) {
  const [hasError, setHasError] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  const handleError = useCallback(() => {
    setHasError(true);
    setIsLoading(false);
  }, []);

  const handleLoad = useCallback(() => {
    setIsLoading(false);
  }, []);

  const placementClasses = {
    inline: 'max-w-lg mx-auto',
    hero: 'w-full',
    'full-width': 'w-full',
  };

  const isGif = media.type === 'gif' || media.url.endsWith('.gif');

  // Show styled placeholder if image failed to load or doesn't exist
  if (hasError) {
    return (
      <figure className={`my-10 ${placementClasses[media.placement || 'inline']}`}>
        <div className="relative rounded-2xl overflow-hidden bg-neutral-900/40 border border-white/5">
          <div className="aspect-video flex items-center justify-center p-8">
            <div className="text-center max-w-sm">
              <div className="w-16 h-16 bg-cyan-500/10 rounded-2xl flex items-center justify-center mx-auto mb-4 border border-cyan-500/20">
                {isGif ? (
                  <div className="w-6 h-6 border-2 border-cyan-400 border-t-transparent rounded-full animate-spin" />
                ) : (
                  <Sparkles size={28} className="text-cyan-400" />
                )}
              </div>
              <p className="text-neutral-300 font-black uppercase tracking-wider text-sm mb-2">{media.alt}</p>
              {media.caption && (
                <p className="text-neutral-600 text-xs">{media.caption}</p>
              )}
            </div>
          </div>
        </div>
      </figure>
    );
  }

  return (
    <figure className={`my-10 ${placementClasses[media.placement || 'inline']}`}>
      <div className="relative rounded-2xl overflow-hidden bg-neutral-900/40 border border-white/5">
        {/* Loading skeleton */}
        {isLoading && (
          <div className="absolute inset-0 bg-neutral-900/40 animate-pulse flex items-center justify-center">
            <div className="w-8 h-8 border-2 border-cyan-500 border-t-transparent rounded-full animate-spin" />
          </div>
        )}

        {isGif ? (
          // Use img for GIFs to preserve animation
          // eslint-disable-next-line @next/next/no-img-element
          <img
            src={media.url}
            alt={media.alt}
            className={`w-full h-auto max-h-[400px] object-contain transition-opacity duration-300 ${isLoading ? 'opacity-0' : 'opacity-100'}`}
            loading="lazy"
            onError={handleError}
            onLoad={handleLoad}
          />
        ) : (
          <Image
            src={media.url}
            alt={media.alt}
            width={media.width || 800}
            height={media.height || 450}
            className={`w-full h-auto max-h-[400px] object-contain transition-opacity duration-300 ${isLoading ? 'opacity-0' : 'opacity-100'}`}
            onError={handleError}
            onLoad={handleLoad}
          />
        )}
      </div>
      {media.caption && (
        <figcaption className="mt-4 text-center text-[10px] font-black uppercase tracking-widest text-neutral-600">
          {media.caption}
        </figcaption>
      )}
    </figure>
  );
}

// ============================================
// PRODUCT CALLOUT (CONVERSION OPTIMIZED)
// ============================================

interface ProductCalloutProps {
  productId: string;
}

function ProductCallout({ productId }: ProductCalloutProps) {
  const { getLink } = useRegion();
  const product = getGuideProductById(productId);
  const [hasError, setHasError] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  if (!product) return null;

  const link = getLink(product.regionLinks, product.directLink);

  return (
    <div className="my-6 p-5 rounded-2xl bg-neutral-900/40 border border-white/5 hover:border-cyan-500/20 transition-all">
      <div className="flex items-center gap-4">
        {/* Product Image */}
        <div className="w-16 h-16 rounded-xl bg-neutral-900 border border-white/10 overflow-hidden flex-shrink-0 relative">
          {isLoading && !hasError && product.imageUrl && (
            <div className="absolute inset-0 bg-neutral-900/60 animate-pulse flex items-center justify-center z-10">
              <div className="w-4 h-4 border-2 border-cyan-500 border-t-transparent rounded-full animate-spin" />
            </div>
          )}
          {hasError || !product.imageUrl ? (
            <div className="w-full h-full flex items-center justify-center bg-neutral-900">
              <ShoppingCart size={20} className="text-neutral-600" />
            </div>
          ) : (
            <Image
              src={product.imageUrl}
              alt={product.name}
              width={64}
              height={64}
              className={`w-full h-full object-cover transition-opacity ${isLoading ? 'opacity-0' : 'opacity-100'}`}
              onLoad={() => setIsLoading(false)}
              onError={() => { setHasError(true); setIsLoading(false); }}
              unoptimized
            />
          )}
        </div>

        {/* Product Info */}
        <div className="flex-1 min-w-0">
          <div className="flex items-center gap-2 mb-1">
            <h4 className="font-black uppercase tracking-wide text-white truncate">{product.name}</h4>
            {product.brand && (
              <span className="text-[10px] font-black uppercase tracking-widest text-neutral-600">{product.brand}</span>
            )}
          </div>
          <p className="text-sm text-cyan-400 italic">&ldquo;{product.tagline}&rdquo;</p>
        </div>

        {/* CTA Button */}
        <a
          href={link}
          target="_blank"
          rel="noopener noreferrer"
          className="flex-shrink-0 flex items-center gap-2 px-5 py-2.5 rounded-xl bg-cyan-500 hover:bg-cyan-400 text-black font-black uppercase tracking-wider text-xs transition-colors"
        >
          <ShoppingCart size={14} />
          <span className="hidden sm:inline">BUY</span>
        </a>
      </div>
    </div>
  );
}

// ============================================
// FORUM DISCUSSION LINK
// ============================================

interface ForumDiscussionLinkProps {
  forumCategory?: string;
  guideTitle: string;
}

function ForumDiscussionLink({ forumCategory, guideTitle }: ForumDiscussionLinkProps) {
  if (!forumCategory) return null;

  return (
    <div className="mt-8 p-6 rounded-2xl bg-neutral-900/40 border border-white/5">
      <div className="flex items-center gap-3 mb-4">
        <div className="w-10 h-10 rounded-xl bg-purple-500/10 border border-purple-500/20 flex items-center justify-center">
          <MessageSquare size={20} className="text-purple-400" />
        </div>
        <div>
          <h3 className="font-black uppercase tracking-wide text-white">DISCUSS THIS GUIDE</h3>
          <p className="text-[10px] font-bold uppercase tracking-wider text-neutral-600">Join the community conversation</p>
        </div>
      </div>

      <p className="text-sm text-neutral-400 mb-5">
        Have questions about {guideTitle.toLowerCase()}? Want to share your experience?
        Join the discussion in our community forum.
      </p>

      <Link
        href={`/forum/${forumCategory}`}
        className="inline-flex items-center gap-2 px-5 py-2.5 rounded-xl bg-neutral-900/50 border border-white/5 hover:border-purple-500/30 text-purple-400 font-black uppercase tracking-wider text-xs transition-all"
      >
        <MessageSquare size={14} />
        GO TO FORUM
        <ExternalLink size={12} />
      </Link>
    </div>
  );
}

// ============================================
// GUIDE STATS CARD
// ============================================

function GuideStatsCard() {
  const totalGuides = getTotalGuideCount();
  const totalMinutes = getTotalReadTime();

  return (
    <div className="rounded-[2rem] bg-neutral-900/40 border border-white/5 p-6 mb-8">
      <div className="flex items-center gap-4 mb-6">
        <div className="w-12 h-12 rounded-xl bg-neutral-900 border border-white/10 flex items-center justify-center">
          <BookOpen size={22} className="text-cyan-400" />
        </div>
        <div>
          <h2 className="text-xl font-black italic uppercase tracking-tight text-white">PRODUCT <span className="text-cyan-400">GUIDES</span></h2>
          <p className="text-[10px] font-black uppercase tracking-[0.2em] text-neutral-600">EVIDENCE-BASED LOOKSMAXXING RESOURCES</p>
        </div>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div className="rounded-xl bg-neutral-900/50 border border-white/5 p-4">
          <div className="text-3xl font-black text-white mb-1">{totalGuides}</div>
          <div className="text-[10px] font-black uppercase tracking-widest text-neutral-600">TOTAL GUIDES</div>
        </div>
        <div className="rounded-xl bg-neutral-900/50 border border-white/5 p-4">
          <div className="flex items-center gap-2 mb-1">
            <Clock size={18} className="text-cyan-400" />
            <div className="text-3xl font-black text-white">{totalMinutes}</div>
          </div>
          <div className="text-[10px] font-black uppercase tracking-widest text-neutral-600">MINUTES OF CONTENT</div>
        </div>
      </div>
    </div>
  );
}

// ============================================
// GUIDE VIEWER MODAL
// ============================================

'use client';

import { useRouter } from 'next/navigation';
import { motion } from 'framer-motion';
import {
  HelpCircle,
  Book,
  Mail,
  ChevronRight,
  FileText,
  Users,
  Ruler,
  FlaskConical,
  Sparkles,
} from 'lucide-react';
import { TabContent } from '../ResultsLayout';

const SUPPORT_EMAIL = 'support@looksmaxx.app';

// ============================================
// SUPPORT CARD
// ============================================

interface SupportCardProps {
  icon: React.ReactNode;
  title: string;
  description: string;
  action?: string;
  onClick?: () => void;
}

function SupportCard({ icon, title, description, action, onClick }: SupportCardProps) {
  return (
    <motion.button
      onClick={onClick}
      className="w-full p-4 bg-neutral-900/60 border border-neutral-800 rounded-xl hover:border-neutral-700 transition-all text-left"
      whileHover={{ scale: 1.01 }}
      whileTap={{ scale: 0.99 }}
    >
      <div className="flex items-start gap-4">
        <div className="w-12 h-12 rounded-xl bg-gradient-to-br from-cyan-500/20 to-blue-600/20 flex items-center justify-center flex-shrink-0">
          {icon}
        </div>
        <div className="flex-1">
          <h4 className="font-medium text-white mb-1">{title}</h4>
          <p className="text-sm text-neutral-500">{description}</p>
          {action && (
            <div className="flex items-center gap-1 mt-2 text-sm text-cyan-400">
              {action}
              <ChevronRight size={14} />
            </div>
          )}
        </div>
      </div>
    </motion.button>
  );
}

// ============================================
// FAQ ITEM
// ============================================

interface FAQItemProps {
  question: string;
  answer: string;
}

function FAQItem({ question, answer }: FAQItemProps) {
  return (
    <div className="p-4 bg-neutral-900/40 border border-neutral-800 rounded-xl">
      <h4 className="font-medium text-white mb-2">{question}</h4>
      <p className="text-sm text-neutral-400">{answer}</p>
    </div>
  );
}

// ============================================
// SUPPORT TAB
// ============================================

export function SupportTab() {
  const router = useRouter();

  const handleDocs = () => {
    router.push('/docs');
  };

  const handleEmail = () => {
    window.location.href = `mailto:${SUPPORT_EMAIL}?subject=Support Request`;
  };

  return (
    <TabContent
      title="Support"
      subtitle="Get help and learn more about facial analysis"
    >
      <div className="max-w-3xl space-y-8">
        {/* Quick Help */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <SupportCard
            icon={<Book size={24} className="text-cyan-400" />}
            title="Documentation"
            description="Learn about facial measurements, scoring, and enhancement methods"
            action="Read docs"
            onClick={handleDocs}
          />
          <SupportCard
            icon={<Mail size={24} className="text-yellow-400" />}
            title="Contact Support"
            description="Get personalized help from our team"
            action="Send email"
            onClick={handleEmail}
          />
        </div>

        {/* FAQ Section */}
        <div>
          <h3 className="text-lg font-semibold text-white mb-4 flex items-center gap-2">
            <HelpCircle size={20} className="text-cyan-400" />
            Frequently Asked Questions
          </h3>
          <div className="space-y-3">
            <FAQItem
              question="How accurate is the facial analysis?"
              answer="Our analysis uses advanced AI models trained on thousands of facial measurements. While results are highly accurate for general proportions, individual variations exist. Use results as a guide rather than absolute measurements."
            />
            <FAQItem
              question="What do the scores mean?"
              answer="Scores range from 0-10, where 10 represents ideal proportions based on established facial harmony standards. Scores above 7 are considered good, above 8 excellent, and above 9 exceptional."
            />
            <FAQItem
              question="Can I improve my facial harmony score?"
              answer="Yes! The 'Your Plan' section provides personalized recommendations ranging from foundational lifestyle changes to minimally invasive and surgical options. Many improvements are achievable without surgery."
            />
            <FAQItem
              question="Are my photos stored securely?"
              answer="Your photos are processed locally in your browser and are not stored on our servers unless you explicitly choose to save them. All data is encrypted and you can delete it at any time."
            />
            <FAQItem
              question="Why are my front and side scores different?"
              answer="Front and side profiles measure different facial features. It's common to have different scores as facial harmony varies by angle. The overall score balances both profiles."
            />
          </div>
        </div>

        {/* Resources */}
        <div>
          <h3 className="text-lg font-semibold text-white mb-4 flex items-center gap-2">
            <FileText size={20} className="text-cyan-400" />
            Learning Resources
          </h3>
          <div className="space-y-2">
            <button
              onClick={() => router.push('/docs#proportions')}
              className="w-full flex items-center justify-between p-4 bg-neutral-900/40 border border-neutral-800 rounded-xl hover:border-cyan-500/30 transition-colors group"
            >
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-xl bg-cyan-500/20 border border-cyan-500/30 flex items-center justify-center">
                  <Ruler size={16} className="text-cyan-400" />
                </div>
                <div className="text-left">
                  <span className="text-sm font-bold text-white block">Understanding Facial Proportions</span>
                  <span className="text-[10px] text-neutral-500">Golden ratios, facial thirds, angular measurements</span>
                </div>
              </div>
              <ChevronRight size={16} className="text-neutral-600 group-hover:text-cyan-400 transition-colors" />
            </button>
            <button
              onClick={() => router.push('/docs#harmony')}
              className="w-full flex items-center justify-between p-4 bg-neutral-900/40 border border-neutral-800 rounded-xl hover:border-purple-500/30 transition-colors group"
            >
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-xl bg-purple-500/20 border border-purple-500/30 flex items-center justify-center">
                  <FlaskConical size={16} className="text-purple-400" />
                </div>
                <div className="text-left">
                  <span className="text-sm font-bold text-white block">The Science of Facial Harmony</span>
                  <span className="text-[10px] text-neutral-500">Scoring algorithms, PSL ratings, bezier curves</span>
                </div>
              </div>
              <ChevronRight size={16} className="text-neutral-600 group-hover:text-purple-400 transition-colors" />
            </button>
            <button
              onClick={() => router.push('/docs#enhancement')}
              className="w-full flex items-center justify-between p-4 bg-neutral-900/40 border border-neutral-800 rounded-xl hover:border-green-500/30 transition-colors group"
            >
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-xl bg-green-500/20 border border-green-500/30 flex items-center justify-center">
                  <Sparkles size={16} className="text-green-400" />
                </div>
                <div className="text-left">
                  <span className="text-sm font-bold text-white block">Non-Surgical Enhancement Guide</span>
                  <span className="text-[10px] text-neutral-500">Supplements, lifestyle, minimally invasive options</span>
                </div>
              </div>
              <ChevronRight size={16} className="text-neutral-600 group-hover:text-green-400 transition-colors" />
            </button>
          </div>
        </div>

        {/* Contact */}
        <div className="bg-gradient-to-r from-cyan-500/10 to-blue-600/10 border border-cyan-500/20 rounded-xl p-6 text-center">
          <Users size={32} className="mx-auto text-cyan-400 mb-3" />
          <h3 className="text-lg font-semibold text-white mb-2">Still need help?</h3>
          <p className="text-sm text-neutral-400 mb-4">
            Our support team is available 24/7 to assist you with any questions.
          </p>
          <button
            onClick={handleEmail}
            className="px-6 py-2.5 bg-cyan-500 text-black font-medium rounded-lg hover:bg-cyan-400 transition-colors"
          >
            Contact Support
          </button>
        </div>
      </div>
    </TabContent>
  );
}

