     1→'use client';
     2→
     3→import { useState, useCallback } from 'react';
     4→import { motion, AnimatePresence } from 'framer-motion';
     5→import {
     6→  AlertTriangle,
     7→  Shield,
     8→  CheckCircle,
     9→  X,
    10→  ChevronRight,
    11→  FileText,
    12→  Stethoscope,
    13→  Clock,
    14→  DollarSign,
    15→  Heart,
    16→} from 'lucide-react';
    17→
    18→// ============================================
    19→// TYPES
    20→// ============================================
    21→
    22→export interface SurgeryConsentData {
    23→  procedureId: string;
    24→  procedureName: string;
    25→  consentedAt: string;
    26→  acknowledged: {
    27→    risks: boolean;
    28→    costs: boolean;
    29→    recovery: boolean;
    30→    consultation: boolean;
    31→    informational: boolean;
    32→  };
    33→  signature?: string;
    34→  age?: number;
    35→}
    36→
    37→interface SurgeryConsentModalProps {
    38→  isOpen: boolean;
    39→  onClose: () => void;
    40→  onConsent: (data: SurgeryConsentData) => void;
    41→  procedure: {
    42→    id: string;
    43→    name: string;
    44→    description?: string;
    45→    risks?: string;
    46→    costMin?: number;
    47→    costMax?: number;
    48→    recoveryTime?: string;
    49→  };
    50→}
    51→
    52→// ============================================
    53→// STORAGE
    54→// ============================================
    55→
    56→const CONSENT_STORAGE_KEY = 'looksmaxx_surgery_consents';
    57→
    58→export function getStoredConsents(): Record<string, SurgeryConsentData> {
    59→  if (typeof window === 'undefined') return {};
    60→
    61→  try {
    62→    const stored = localStorage.getItem(CONSENT_STORAGE_KEY);
    63→    if (stored) {
    64→      return JSON.parse(stored);
    65→    }
    66→  } catch {
    67→    console.error('[SurgeryConsent] Failed to load consents');
    68→  }
    69→
    70→  return {};
    71→}
    72→
    73→export function saveConsent(data: SurgeryConsentData): void {
    74→  if (typeof window === 'undefined') return;
    75→
    76→  try {
    77→    const existing = getStoredConsents();
    78→    existing[data.procedureId] = data;
    79→    localStorage.setItem(CONSENT_STORAGE_KEY, JSON.stringify(existing));
    80→  } catch (e) {
    81→    console.error('[SurgeryConsent] Failed to save consent:', e);
    82→  }
    83→}
    84→
    85→export function hasConsented(procedureId: string): boolean {
    86→  const consents = getStoredConsents();
    87→  return !!consents[procedureId];
    88→}
    89→
    90→export function revokeConsent(procedureId: string): void {
    91→  if (typeof window === 'undefined') return;
    92→
    93→  try {
    94→    const existing = getStoredConsents();
    95→    delete existing[procedureId];
    96→    localStorage.setItem(CONSENT_STORAGE_KEY, JSON.stringify(existing));
    97→  } catch (e) {
    98→    console.error('[SurgeryConsent] Failed to revoke consent:', e);
    99→  }
   100→}
   101→
   102→// ============================================
   103→// CONSENT MODAL
   104→// ============================================
   105→
   106→export function SurgeryConsentModal({
   107→  isOpen,
   108→  onClose,
   109→  onConsent,
   110→  procedure,
   111→}: SurgeryConsentModalProps) {
   112→  const [step, setStep] = useState(1);
   113→  const [acknowledged, setAcknowledged] = useState({
   114→    risks: false,
   115→    costs: false,
   116→    recovery: false,
   117→    consultation: false,
   118→    informational: false,
   119→  });
   120→  const [signature, setSignature] = useState('');
   121→  const [age, setAge] = useState<number | undefined>();
   122→
   123→  const allAcknowledged = Object.values(acknowledged).every(Boolean);
   124→  const canProceed = allAcknowledged && signature.length >= 2 && (age === undefined || age >= 18);
   125→
   126→  const handleAcknowledge = (key: keyof typeof acknowledged) => {
   127→    setAcknowledged((prev) => ({ ...prev, [key]: !prev[key] }));
   128→  };
   129→
   130→  const handleConsent = useCallback(() => {
   131→    const data: SurgeryConsentData = {
   132→      procedureId: procedure.id,
   133→      procedureName: procedure.name,
   134→      consentedAt: new Date().toISOString(),
   135→      acknowledged,
   136→      signature,
   137→      age,
   138→    };
   139→
   140→    saveConsent(data);
   141→    onConsent(data);
   142→    onClose();
   143→
   144→    // Reset state
   145→    setStep(1);
   146→    setAcknowledged({
   147→      risks: false,
   148→      costs: false,
   149→      recovery: false,
   150→      consultation: false,
   151→      informational: false,
   152→    });
   153→    setSignature('');
   154→    setAge(undefined);
   155→  }, [procedure, acknowledged, signature, age, onConsent, onClose]);
   156→
   157→  if (!isOpen) return null;
   158→
   159→  return (
   160→    <AnimatePresence>
   161→      <motion.div
   162→        className="fixed inset-0 z-50 flex items-center justify-center bg-black/80 backdrop-blur-sm p-4"
   163→        initial={{ opacity: 0 }}
   164→        animate={{ opacity: 1 }}
   165→        exit={{ opacity: 0 }}
   166→        onClick={onClose}
   167→      >
   168→        <motion.div
   169→          className="bg-neutral-900 border border-neutral-800 rounded-2xl w-full max-w-lg max-h-[90vh] overflow-y-auto"
   170→          initial={{ scale: 0.95, opacity: 0 }}
   171→          animate={{ scale: 1, opacity: 1 }}
   172→          exit={{ scale: 0.95, opacity: 0 }}
   173→          onClick={(e) => e.stopPropagation()}
   174→        >
   175→          {/* Header */}
   176→          <div className="flex items-center justify-between p-4 border-b border-neutral-800 sticky top-0 bg-neutral-900 z-10">
   177→            <div className="flex items-center gap-2">
   178→              <Shield size={20} className="text-yellow-500" />
   179→              <h3 className="font-semibold text-white">Consent Required</h3>
   180→            </div>
   181→            <button
   182→              onClick={onClose}
   183→              className="p-2 rounded-lg text-neutral-500 hover:text-white hover:bg-neutral-800"
   184→            >
   185→              <X size={18} />
   186→            </button>
   187→          </div>
   188→
   189→          {/* Step 1: Information */}
   190→          {step === 1 && (
   191→            <div className="p-6 space-y-4">
   192→              <div className="text-center mb-6">
   193→                <div className="w-16 h-16 mx-auto mb-4 rounded-2xl bg-yellow-500/20 flex items-center justify-center">
   194→                  <AlertTriangle size={32} className="text-yellow-500" />
   195→                </div>
   196→                <h4 className="text-xl font-bold text-white mb-2">
   197→                  Important Notice
   198→                </h4>
   199→                <p className="text-sm text-neutral-400">
   200→                  You are about to view information about{' '}
   201→                  <span className="text-white font-medium">{procedure.name}</span>.
   202→                  This is a surgical procedure with associated risks.
   203→                </p>
   204→              </div>
   205→
   206→              {/* Procedure Details */}
   207→              <div className="bg-neutral-800/50 rounded-xl p-4 space-y-3">
   208→                <div className="flex items-center gap-3">
   209→                  <Stethoscope size={18} className="text-cyan-400" />
   210→                  <div>
   211→                    <p className="text-xs text-neutral-500">Procedure</p>
   212→                    <p className="text-sm text-white">{procedure.name}</p>
   213→                  </div>
   214→                </div>
   215→
   216→                {procedure.costMin && procedure.costMax && (
   217→                  <div className="flex items-center gap-3">
   218→                    <DollarSign size={18} className="text-green-400" />
   219→                    <div>
   220→                      <p className="text-xs text-neutral-500">Estimated Cost</p>
   221→                      <p className="text-sm text-white">
   222→                        ${procedure.costMin.toLocaleString()} - ${procedure.costMax.toLocaleString()}
   223→                      </p>
   224→                    </div>
   225→                  </div>
   226→                )}
   227→
   228→                {procedure.recoveryTime && (
   229→                  <div className="flex items-center gap-3">
   230→                    <Clock size={18} className="text-blue-400" />
   231→                    <div>
   232→                      <p className="text-xs text-neutral-500">Recovery Time</p>
   233→                      <p className="text-sm text-white">{procedure.recoveryTime}</p>
   234→                    </div>
   235→                  </div>
   236→                )}
   237→
   238→                {procedure.risks && (
   239→                  <div className="flex items-start gap-3">
   240→                    <Heart size={18} className="text-red-400 mt-0.5" />
   241→                    <div>
   242→                      <p className="text-xs text-neutral-500">Associated Risks</p>
   243→                      <p className="text-sm text-neutral-300">{procedure.risks}</p>
   244→                    </div>
   245→                  </div>
   246→                )}
   247→              </div>
   248→
   249→              <button
   250→                onClick={() => setStep(2)}
   251→                className="w-full py-3 px-4 rounded-lg bg-gradient-to-r from-cyan-500 to-blue-500 hover:from-cyan-400 hover:to-blue-400 text-white font-medium transition-all flex items-center justify-center gap-2"
   252→              >
   253→                I Understand, Continue
   254→                <ChevronRight size={18} />
   255→              </button>
   256→            </div>
   257→          )}
   258→
   259→          {/* Step 2: Acknowledgments */}
   260→          {step === 2 && (
   261→            <div className="p-6 space-y-4">
   262→              <div className="text-center mb-4">
   263→                <FileText size={32} className="mx-auto text-cyan-400 mb-2" />
   264→                <h4 className="text-lg font-bold text-white">
