'use client';

import React, { Suspense, useEffect, useState } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { Elements } from '@stripe/react-stripe-js';
import { useSearchParams } from 'next/navigation';
import { CheckoutForm } from '@/components/checkout/CheckoutForm';
import { ShieldCheck, ArrowLeft, Zap } from 'lucide-react';
import Link from 'next/link';

// Initialize Stripe outside to avoid recreation
const stripePromise = process.env.NEXT_PUBLIC_STRIPE_PUBLISHABLE_KEY
    ? loadStripe(process.env.NEXT_PUBLIC_STRIPE_PUBLISHABLE_KEY)
    : null;

function CheckoutContent() {
    const searchParams = useSearchParams();
    const planId = searchParams.get('plan') || 'weekly';

    const [clientSecret, setClientSecret] = useState('');
    const isDemoMode = !process.env.NEXT_PUBLIC_STRIPE_PUBLISHABLE_KEY;

    // Define plan details
    const planDetails = {
        weekly: {
            name: 'LooksMaxx Pro',
            price: 6.99,
            originalPrice: 19.99,
            features: ['PSL God-Tier Rank', '60+ Hidden Ratios', 'Growth Potential']
        },
        action: {
            name: 'Advanced Plus',
            price: 49.99,
            originalPrice: 149.99,
            features: ['Lifetime Access', 'VIP Support', 'Aesthetic Strategy']
        }
    }[planId] || { name: 'Unknown Plan', price: 0, originalPrice: 0, features: [] };


    useEffect(() => {
        // Create PaymentIntent as soon as the page loads
        // Only if NOT in demo mode
        if (!isDemoMode) {
            fetch('/api/create-payment-intent', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ items: [{ id: planId }] }),
            })
                .then((res) => res.json())
                .then((data) => setClientSecret(data.clientSecret));
        }
    }, [planId, isDemoMode]);

    const appearance = {
        theme: 'night' as const,
        variables: {
            colorPrimary: '#06b6d4',
            colorBackground: '#0a0a0a',
            colorText: '#ffffff',
            colorDanger: '#ef4444',
            fontFamily: 'Manrope, system-ui, sans-serif',
            spacingUnit: '4px',
            borderRadius: '12px',
        },
    };

    const options = {
        clientSecret,
        appearance,
    };

    return (
        <div className="min-h-screen bg-black text-white font-sans selection:bg-cyan-500/30">

            {/* Header */}
            <div className="border-b border-white/5 bg-neutral-900/30 backdrop-blur-md sticky top-0 z-10">
                <div className="max-w-5xl mx-auto px-6 py-4 flex items-center justify-between">
                    <Link href="/" className="flex items-center gap-2 text-neutral-400 hover:text-white transition-colors text-xs font-bold uppercase tracking-widest">
                        <ArrowLeft size={14} />
                        Back
                    </Link>
                    <div className="flex items-center gap-2">
                        <div className="w-8 h-8 bg-cyan-500 rounded-lg flex items-center justify-center font-black italic text-black">L</div>
                        <span className="font-black tracking-tighter text-lg">LOOXSMAXX<span className="text-cyan-500">LABS</span></span>
                    </div>
                    <div className="flex items-center gap-1 text-[10px] font-bold text-green-500 bg-green-500/10 px-2 py-1 rounded-full border border-green-500/20">
                        <ShieldCheck size={12} />
                        SECURE
                    </div>
                </div>
            </div>

            <main className="max-w-5xl mx-auto p-6 py-12 grid grid-cols-1 md:grid-cols-2 gap-12 items-start">

                {/* Order Summary */}
                <div className="space-y-8">
                    <div>
                        <h1 className="text-3xl font-black italic uppercase text-white mb-2">Order Summary</h1>
                        <p className="text-neutral-500 text-sm">Review your selected upgrade package.</p>
                    </div>

                    <div className="bg-neutral-900/40 border border-white/10 rounded-3xl p-8 relative overflow-hidden">
                        <div className="absolute top-0 right-0 p-32 bg-cyan-500/5 blur-[80px] rounded-full pointer-events-none" />

                        <div className="relative z-10">
                            <div className="flex justify-between items-start mb-6">
                                <div>
                                    <div className="text-xs font-black uppercase text-cyan-500 tracking-widest mb-1">Selected Plan</div>
                                    <h2 className="text-2xl font-black italic uppercase text-white">{planDetails.name}</h2>
                                </div>
                                <div className="text-right">
                                    <div className="text-sm text-neutral-500 line-through font-bold">${planDetails.originalPrice}</div>
                                    <div className="text-3xl font-black text-white">${planDetails.price}</div>
                                </div>
                            </div>

                            <div className="space-y-4 mb-8">
                                {planDetails.features.map((feature: string, i: number) => (
                                    <div key={i} className="flex items-center gap-3">
                                        <div className="w-5 h-5 rounded-full bg-cyan-500/10 border border-cyan-500/20 flex items-center justify-center shrink-0">
                                            <Zap size={10} className="fill-cyan-500 text-cyan-500" />
                                        </div>
                                        <span className="text-sm font-bold text-neutral-300">{feature}</span>
                                    </div>
                                ))}
                            </div>

                            <div className="pt-6 border-t border-white/5 flex justify-between items-center">
                                <span className="text-sm font-bold text-neutral-500 uppercase">Total Due Today</span>
                                <span className="text-xl font-black text-white underline decoration-cyan-500 decoration-2 underline-offset-4">
                                    ${planDetails.price}
                                </span>
                            </div>
                        </div>
                    </div>

                    <div className="flex items-start gap-4 p-4 bg-neutral-900 rounded-xl border border-white/5">
                        <div className="w-10 h-10 rounded-lg bg-neutral-800 flex items-center justify-center shrink-0 font-serif text-2xl italic text-neutral-500">&ldquo;</div>
                        <div className="space-y-2">
                            <p className="text-sm text-neutral-400 italic">
                                The detailed facial analysis provided me with actionable steps I never knew I needed. Worth every penny.
                            </p>
                            <div className="flex items-center gap-2">
                                <div className="w-5 h-5 rounded-full bg-gradient-to-br from-purple-500 to-indigo-500" />
                                <span className="text-xs font-bold text-neutral-300">Verified User</span>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Payment Form */}
                <div className="bg-white/[0.02] border border-white/5 rounded-3xl p-8 backdrop-blur-sm">
                    <div className="mb-6">
                        <h2 className="text-xl font-black uppercase text-white mb-1">Payment Details</h2>
                        <p className="text-xs text-neutral-500 font-bold uppercase tracking-wider">Complete your secure transaction</p>
                    </div>

                    {(isDemoMode || (clientSecret && stripePromise)) ? (
                        <Elements options={isDemoMode ? undefined : options} stripe={isDemoMode ? null : stripePromise}>
                            <CheckoutForm
                                amount={planDetails.price}
                                planName={planDetails.name}
                                isDemoMode={isDemoMode}
                            />
                        </Elements>
                    ) : (
                        <div className="py-20 text-center">
                            <div className="w-10 h-10 border-2 border-cyan-500 border-t-transparent rounded-full animate-spin mx-auto mb-4" />
                            <p className="text-xs font-bold uppercase text-neutral-500 animate-pulse">Initializing Secure Gateway...</p>
                        </div>
                    )}
                </div>

            </main>
        </div>
    );
}

// Wrap in Suspense for useSearchParams() - required for Next.js 14 static generation
export default function CheckoutPage() {
    return (
        <Suspense fallback={
            <div className="min-h-screen bg-black flex items-center justify-center">
                <div className="w-10 h-10 border-2 border-cyan-500 border-t-transparent rounded-full animate-spin" />
            </div>
        }>
            <CheckoutContent />
        </Suspense>
    );
}

'use client';

import { useEffect, useState } from 'react';
import Link from 'next/link';
import { useRouter } from 'next/navigation';
import { Trophy, ShieldCheck, ArrowLeft, Search, X, Bookmark } from 'lucide-react';
import { useLeaderboardOptional } from '@/contexts/LeaderboardContext';
import { api } from '@/lib/api';
import { NotificationBell } from '@/components/NotificationBell';

export function ForumHeader() {
  const router = useRouter();
  const leaderboard = useLeaderboardOptional();
  const userRank = leaderboard?.userRank;
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showSearchInput, setShowSearchInput] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  // Check auth status on client only to avoid hydration mismatch
  useEffect(() => {
    setIsAuthenticated(!!api.getToken());
  }, []);

  // Auto-fetch user's rank on mount if authenticated
  useEffect(() => {
    if (isAuthenticated && !userRank && leaderboard?.fetchMyRank) {
      leaderboard.fetchMyRank();
    }
  }, [isAuthenticated, userRank, leaderboard]);

  // Handle search submit
  const handleSearchSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchQuery.trim().length >= 2) {
      router.push(`/forum/search?q=${encodeURIComponent(searchQuery.trim())}`);
      setShowSearchInput(false);
      setSearchQuery('');
    }
  };

  return (
    <header className="sticky top-0 z-50 bg-black/95 backdrop-blur-xl border-b border-white/5">
      <div className="max-w-6xl mx-auto px-6 h-16 flex items-center justify-between">
        <div className="flex items-center gap-6">
          <Link href="/" className="flex items-center gap-3">
            <div className="w-9 h-9 rounded-xl bg-gradient-to-br from-cyan-400 to-blue-600 flex items-center justify-center shadow-lg shadow-cyan-500/20">
              <ShieldCheck size={18} className="text-white" />
            </div>
            <span className="text-xs font-black uppercase tracking-widest text-white hidden sm:block">LOOKSMAXX</span>
          </Link>
          <div className="h-6 w-px bg-white/10 hidden sm:block" />
          <Link
            href="/forum"
            className="text-[10px] font-black uppercase tracking-[0.2em] text-neutral-500 hover:text-cyan-400 transition-colors hidden sm:flex items-center gap-2"
          >
            Community
          </Link>
          {isAuthenticated && (
            <Link
              href="/forum/saved"
              className="text-[10px] font-black uppercase tracking-[0.2em] text-neutral-500 hover:text-cyan-400 transition-colors hidden sm:flex items-center gap-2"
              title="Saved posts"
            >
              <Bookmark size={12} />
              Saved
            </Link>
          )}
        </div>

        <div className="flex items-center gap-4">
          {/* Inline Search Input (Expandable) */}
          {showSearchInput ? (
            <form onSubmit={handleSearchSubmit} className="flex items-center gap-2">
              <div className="relative">
                <Search size={14} className="absolute left-3 top-1/2 -translate-y-1/2 text-neutral-500" />
                <input
                  type="text"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  placeholder="Search..."
                  autoFocus
                  className="w-48 sm:w-64 h-9 pl-9 pr-3 rounded-lg bg-neutral-900/80 border border-white/10
                             text-sm text-white placeholder:text-neutral-600
                             focus:outline-none focus:border-cyan-500/30 transition-all"
                />
              </div>
              <button
                type="button"
                onClick={() => {
                  setShowSearchInput(false);
                  setSearchQuery('');
                }}
                className="p-2 rounded-lg text-neutral-500 hover:text-white hover:bg-white/10 transition-all"
              >
                <X size={16} />
              </button>
            </form>
          ) : (
            <button
              onClick={() => setShowSearchInput(true)}
              className="p-2.5 rounded-xl bg-neutral-900/50 border border-white/5 hover:border-cyan-500/30 transition-all"
              title="Search forum"
            >
              <Search size={16} className="text-neutral-400 hover:text-cyan-400" />
            </button>
          )}

          {/* Notification Bell */}
          {isAuthenticated && <NotificationBell />}

          {/* Rank Badge */}
          <Link
            href="/results?tab=leaderboard"
            className="flex items-center gap-3 px-4 py-2 rounded-xl bg-neutral-900/50 border border-white/5 hover:border-cyan-500/30 transition-all"
          >
            {userRank ? (
              <>
                <div className="flex items-center justify-center w-8 h-8 rounded-lg bg-gradient-to-br from-yellow-500/20 to-amber-500/20 border border-yellow-500/30">
                  <Trophy size={14} className="text-yellow-400" />
                </div>
                <div className="hidden sm:block">
                  <p className="text-[9px] font-black uppercase tracking-widest text-neutral-600">Your Rank</p>
                  <div className="flex items-baseline gap-2">
                    <span className="text-sm font-black italic text-yellow-400">#{userRank.genderRank}</span>
                    <span className="text-[10px] text-neutral-600">Top {userRank.percentile.toFixed(1)}%</span>
                  </div>
                </div>
                <span className="sm:hidden text-sm font-black italic text-yellow-400">#{userRank.genderRank}</span>
              </>
            ) : (
              <>
                <div className="flex items-center justify-center w-8 h-8 rounded-lg bg-neutral-900 border border-white/10">
                  <Trophy size={14} className="text-neutral-600" />
                </div>
                <span className="text-[10px] font-black uppercase tracking-widest text-neutral-600 hidden sm:block">
                  {isAuthenticated ? 'Not Ranked' : 'Sign In'}
                </span>
              </>
            )}
          </Link>

          {isAuthenticated ? (
            <Link
              href="/results"
              className="h-10 px-5 rounded-xl bg-cyan-500 text-black text-[10px] font-black uppercase tracking-widest flex items-center gap-2 hover:bg-cyan-400 transition-all shadow-lg shadow-cyan-500/20"
            >
              Dashboard
            </Link>
          ) : (
            <Link
              href="/login"
              className="h-10 px-5 rounded-xl bg-cyan-500 text-black text-[10px] font-black uppercase tracking-widest flex items-center gap-2 hover:bg-cyan-400 transition-all shadow-lg shadow-cyan-500/20"
            >
              Get Started
            </Link>
          )}
        </div>
      </div>
    </header>
  );
}

interface ForumBreadcrumbProps {
  items: { label: string; href?: string }[];
}

export function ForumBreadcrumb({ items }: ForumBreadcrumbProps) {
  return (
    <div className="border-b border-white/5">
      <div className="max-w-6xl mx-auto px-6 py-5">
        <div className="flex items-center gap-3">
          {items.map((item, idx) => (
            <div key={idx} className="flex items-center gap-3">
              {idx === 0 && (
                <ArrowLeft size={14} className="text-neutral-600" />
              )}
              {item.href ? (
                <Link
                  href={item.href}
                  className="text-[10px] font-black uppercase tracking-[0.2em] text-cyan-400 hover:text-cyan-300 transition-colors"
                >
                  {item.label}
                </Link>
              ) : (
                <span className="text-[10px] font-black uppercase tracking-[0.2em] text-neutral-500">
                  {item.label}
                </span>
              )}
              {idx < items.length - 1 && (
                <span className="text-neutral-700">/</span>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

