   907→
   908→export class AdviceEngine {
   909→  private plans: RawPlan[];
   910→
   911→  constructor(customPlans?: RawPlan[]) {
   912→    this.plans = customPlans || PLANS;
   913→  }
   914→
   915→  /**
   916→   * Check if a single threshold condition is met
   917→   */
   918→  private checkThreshold(userValue: number, operator: '<' | '>', thresholdValue: number): boolean {
   919→    if (operator === '<') {
   920→      return userValue < thresholdValue;
   921→    }
   922→    return userValue > thresholdValue;
   923→  }
   924→
   925→  /**
   926→   * Evaluate trigger rules against user metrics
   927→   */
   928→  private evaluateTriggerRules(
   929→    triggerRules: TriggerRules,
   930→    metricsDict: Record<string, number>
   931→  ): { isTriggered: boolean; triggeredMetrics: TriggerResult[] } {
   932→    const { metrics, thresholds, condition } = triggerRules;
   933→    const triggeredMetrics: TriggerResult[] = [];
   934→    const results: boolean[] = [];
   935→
   936→    for (const metricName of metrics) {
   937→      if (!(metricName in metricsDict)) continue;
   938→      if (!(metricName in thresholds)) continue;
   939→
   940→      const userValue = metricsDict[metricName];
   941→      const { operator, value: thresholdValue } = thresholds[metricName];
   942→      const isMet = this.checkThreshold(userValue, operator, thresholdValue);
   943→
   944→      if (isMet) {
   945→        triggeredMetrics.push({
   946→          metric: metricName,
   947→          value: userValue,
   948→          threshold: thresholdValue,
   949→          operator
   950→        });
   951→      }
   952→
   953→      results.push(isMet);
   954→    }
   955→
   956→    const isTriggered = condition === 'AND'
   957→      ? results.length > 0 && results.every(r => r)
   958→      : results.some(r => r);
   959→
   960→    return { isTriggered, triggeredMetrics };
   961→  }
   962→
   963→  /**
   964→   * Get all recommendations that match the user's metrics
   965→   *
   966→   * @param metricsDict - Raw metric values
   967→   * @param severityDict - Severity classifications (ideal/good/moderate/severe) for each metric
   968→   *                       If a metric is 'ideal', it will NOT trigger remediation plans
   969→   */
   970→  getRecommendations(
   971→    metricsDict: Record<string, number>,
   972→    severityDict?: Record<string, string>
   973→  ): Plan[] {
   974→    const recommendations: Plan[] = [];
   975→
   976→    for (const rawPlan of this.plans) {
   977→      const { isTriggered, triggeredMetrics } = this.evaluateTriggerRules(
   978→        rawPlan.trigger_rules,
   979→        metricsDict
   980→      );
   981→
   982→      // If severity data is provided, filter out metrics that are already ideal
   983→      if (isTriggered && severityDict) {
   984→        // Check if ALL triggered metrics are outside ideal range
   985→        const hasActualFlaw = triggeredMetrics.some(tm => {
   986→          const severity = severityDict[tm.metric];
   987→          // Only trigger if severity is NOT ideal (i.e., it's good/moderate/severe)
   988→          return severity && severity !== 'ideal';
   989→        });
   990→
   991→        // Skip this plan if all triggered metrics are already ideal
   992→        if (!hasActualFlaw) {
   993→          continue;
   994→        }
   995→      }
   996→
   997→      if (isTriggered) {
   998→        recommendations.push({
   999→          id: rawPlan.id,
  1000→          title: rawPlan.title,
  1001→          content: {
  1002→            description: rawPlan.content.description,
  1003→            cost_min: rawPlan.content.cost_min,
  1004→            cost_max: rawPlan.content.cost_max,
  1005→            time_min: rawPlan.content.time_min || 'Unknown',
  1006→            time_max: rawPlan.content.time_max || 'Unknown',
  1007→            risks: rawPlan.content.risks || 'Consult a professional.',
  1008→            citations: rawPlan.content.citations || [],
  1009→            tags: rawPlan.content.tags
  1010→          },
  1011→          trigger_reason: triggeredMetrics
  1012→        });
  1013→      }
  1014→    }
  1015→
  1016→    return recommendations;
  1017→  }
  1018→
  1019→  /**
  1020→   * Get recommendations sorted by cost (cheapest first)
  1021→   */
  1022→  getRecommendationsByCost(
  1023→    metricsDict: Record<string, number>,
  1024→    severityDict?: Record<string, string>
  1025→  ): Plan[] {
  1026→    return this.getRecommendations(metricsDict, severityDict).sort(
  1027→      (a, b) => a.content.cost_min - b.content.cost_min
  1028→    );
  1029→  }
  1030→
  1031→  /**
  1032→   * Get recommendations grouped by phase/tag
  1033→   */
  1034→  getRecommendationsByPhase(
  1035→    metricsDict: Record<string, number>,
  1036→    severityDict?: Record<string, string>
  1037→  ): Record<string, Plan[]> {
  1038→    const recommendations = this.getRecommendations(metricsDict, severityDict);
  1039→    const grouped: Record<string, Plan[]> = {
  1040→      'Foundational': [],
  1041→      'Minimally Invasive': [],
  1042→      'Surgical': []
  1043→    };
  1044→
  1045→    for (const rec of recommendations) {
  1046→      const tags = rec.content.tags || [];
  1047→      if (tags.includes('Foundational')) {
  1048→        grouped['Foundational'].push(rec);
  1049→      } else if (tags.includes('Surgical')) {
  1050→        grouped['Surgical'].push(rec);
  1051→      } else if (tags.includes('Minimally Invasive')) {
  1052→        grouped['Minimally Invasive'].push(rec);
  1053→      } else {
  1054→        grouped['Minimally Invasive'].push(rec); // Default
  1055→      }
  1056→    }
  1057→
  1058→    return grouped;
  1059→  }
  1060→}
