   160â†’  return ALL_GUIDES.filter(
   161â†’    g =>
   162â†’      g.title.toLowerCase().includes(lowered) ||
   163â†’      g.description.toLowerCase().includes(lowered) ||
   164â†’      (g.subtitle && g.subtitle.toLowerCase().includes(lowered)) ||
