/**
 * Notification Types - Matches backend Pydantic schemas
 */

// === ENUMS ===

export type NotificationType = 'reply' | 'mention' | 'vote' | 'system' | 'milestone';

export type NotificationSourceType = 'post' | 'comment' | 'system';

// === NOTIFICATION ACTOR ===

export interface NotificationActor {
  id: string;
  username: string;
}

// === NOTIFICATION ===

export interface Notification {
  id: string;
  type: NotificationType;
  title: string;
  content: string | null;
  sourceType: NotificationSourceType | null;
  sourceId: string | null;
  postId: string | null;
  actor: NotificationActor | null;
  link: string | null;
  read: boolean;
  createdAt: string;
  readAt: string | null;
}

// === LIST RESPONSE ===

export interface NotificationListResponse {
  notifications: Notification[];
  totalCount: number;
  unreadCount: number;
  hasMore: boolean;
}

// === UNREAD COUNT RESPONSE ===

export interface UnreadCountResponse {
  unreadCount: number;
}

// === ACTION RESPONSES ===

export interface MarkReadResponse {
  success: boolean;
  notificationId: string;
}

export interface MarkAllReadResponse {
  success: boolean;
  count: number;
}

export interface DeleteNotificationResponse {
  success: boolean;
  notificationId: string;
}

