    18→import { ForumHeader, ForumBreadcrumb } from '@/components/forum/ForumHeader';
    19→
    20→type NotificationType = 'reply' | 'mention' | 'vote' | 'system' | 'milestone';
    21→
    22→export default function NotificationsPage() {
    23→  const router = useRouter();
    24→  const [notifications, setNotifications] = useState<Notification[]>([]);
    25→  // eslint-disable-next-line @typescript-eslint/no-unused-vars
    26→  const [_totalCount, setTotalCount] = useState(0);
    27→  const [unreadCount, setUnreadCount] = useState(0);
    28→  const [hasMore, setHasMore] = useState(false);
    29→  const [loading, setLoading] = useState(true);
    30→  const [loadingMore, setLoadingMore] = useState(false);
    31→  const [error, setError] = useState<string | null>(null);
    32→  const [filter, setFilter] = useState<'all' | 'unread'>('all');
    33→
    34→  const fetchNotifications = useCallback(async (offset = 0, append = false) => {
    35→    try {
    36→      if (offset === 0) {
    37→        setLoading(true);
    38→      } else {
    39→        setLoadingMore(true);
    40→      }
    41→      setError(null);
    42→
    43→      const response = await api.getNotifications({
    44→        unreadOnly: filter === 'unread',
    45→        limit: 20,
    46→        offset,
    47→      });
