   847→interface ApiOptions {
   848→  method?: 'GET' | 'POST' | 'PUT' | 'DELETE';
   849→  body?: unknown;
   850→  token?: string;
   851→}
   852→
   853→interface Analysis {
   854→  id: string;
   855→  front_image_url: string | null;
   856→  side_image_url: string | null;
   857→  front_landmarks: Record<string, unknown> | null;
   858→  side_landmarks: Record<string, unknown> | null;
