   864→
   865→class ApiClient {
   866→  private token: string | null = null;
   867→
   868→  setToken(token: string | null) {
   869→    this.token = token;
   870→  }
   871→
   872→  getToken(): string | null {
   873→    if (typeof window !== 'undefined') {
   874→      return this.token || localStorage.getItem('auth_token');
   875→    }
   876→    return this.token;
   877→  }
   878→
   879→  private async request<T>(endpoint: string, options: ApiOptions = {}): Promise<T> {
   880→    const { method = 'GET', body, token } = options;
   881→    const authToken = token || this.getToken();
   882→
   883→    const headers: Record<string, string> = {
   884→      'Content-Type': 'application/json',
   885→    };
   886→
   887→    if (authToken) {
   888→      headers['Authorization'] = `Bearer ${authToken}`;
   889→    }
   890→
   891→    try {
   892→      const response = await fetch(`${API_URL}${endpoint}`, {
   893→        method,
   894→        headers,
   895→        body: body ? JSON.stringify(body) : undefined,
   896→      });
   897→
   898→      if (!response.ok) {
   899→        const error = await response.json().catch(() => ({ detail: 'Request failed' }));
   900→        throw new Error(error.detail || `HTTP ${response.status}`);
   901→      }
   902→
   903→      // Handle empty responses (204 No Content)
   904→      if (response.status === 204) {
   905→        return undefined as T;
   906→      }
   907→
   908→      return response.json();
   909→    } catch (err) {
   910→      // Network errors, CORS issues, etc.
   911→      if (err instanceof TypeError && err.message.includes('fetch')) {
   912→        throw new Error('Network error - please check your internet connection');
   913→      }
   914→      throw err;
   915→    }
   916→  }
