    37â†’import { mindsetGuide } from './mindset';
    38â†’import { maintenanceGuide } from './maintenance';
    39â†’import { bodyFatGuide } from './body-fat';
    40â†’import { vTaperGuide } from './v-taper';
    41â†’import { trainingGuide } from './training';
    42â†’import { coreNeckGuide } from './core-neck';
