    61â†’
    62â†’/**
    63â†’ * Guide lookup by ID
    64â†’ */
    65â†’export const GUIDES_BY_ID: Record<string, Guide> = ALL_GUIDES.reduce(
    66â†’  (acc, guide) => {
    67â†’    acc[guide.id] = guide;
    68â†’    return acc;
    69â†’  },
    70â†’  {} as Record<string, Guide>
    71â†’);
