   148â†’  const guide = GUIDES_BY_ID[guideId];
   149â†’  if (!guide || !guide.relatedGuides) return [];
   150â†’  return guide.relatedGuides
   151â†’    .map(id => GUIDES_BY_ID[id])
   152â†’    .filter((g): g is Guide => g !== undefined);
