     1→'use client';
     2→
     3→import { useState, useRef, useCallback, useEffect } from 'react';
     4→import Image from 'next/image';
     5→import { motion } from 'framer-motion';
     6→import {
     7→  ChevronLeft,
     8→  ChevronRight,
     9→  ChevronUp,
    10→  ChevronDown,
    11→  Check,
    12→  Info,
    13→  Eye,
    14→  EyeOff,
    15→  Scan,
    16→  Loader2,
    17→  Crosshair,
    18→} from 'lucide-react';
    19→import {
    20→  LandmarkPoint,
    21→  FRONT_PROFILE_LANDMARKS,
    22→  SIDE_PROFILE_LANDMARKS,
    23→  getLandmarkColor,
    24→  FRONT_LANDMARK_CATEGORIES,
    25→  SIDE_LANDMARK_CATEGORIES,
    26→} from '@/lib/landmarks';
    27→import { getLandmarkImagePath, getLandmarkPlacementGuide } from '@/lib/landmarkImages';
    28→import { detectFromImageUrl } from '@/lib/mediapipeDetection';
    29→
    30→interface LandmarkAnalysisToolProps {
    31→  imageUrl: string;
    32→  mode: 'front' | 'side';
    33→  initialLandmarks?: LandmarkPoint[];
    34→  onLandmarksChange?: (landmarks: LandmarkPoint[]) => void;
    35→  onComplete?: (landmarks: LandmarkPoint[]) => void;
    36→  onBack?: () => void;
    37→}
    38→
    39→function getOrderedLandmarks(mode: 'front' | 'side'): string[] {
    40→  const landmarks = mode === 'front' ? FRONT_PROFILE_LANDMARKS : SIDE_PROFILE_LANDMARKS;
    41→  return landmarks.map(lm => lm.id);
    42→}
    43→
    44→export function LandmarkAnalysisTool({
    45→  imageUrl,
    46→  mode,
    47→  initialLandmarks,
    48→  onLandmarksChange,
    49→  onComplete,
    50→  onBack,
    51→}: LandmarkAnalysisToolProps) {
    52→  const containerRef = useRef<HTMLDivElement>(null);
    53→  const [imageDimensions, setImageDimensions] = useState<{ width: number; height: number } | null>(null);
    54→  const defaultLandmarks = mode === 'front' ? FRONT_PROFILE_LANDMARKS : SIDE_PROFILE_LANDMARKS;
    55→  const categories = mode === 'front' ? FRONT_LANDMARK_CATEGORIES : SIDE_LANDMARK_CATEGORIES;
    56→  const orderedIds = getOrderedLandmarks(mode);
    57→
    58→  const [landmarks, setLandmarks] = useState<LandmarkPoint[]>(
    59→    initialLandmarks || defaultLandmarks
    60→  );
    61→  const [currentStepIndex, setCurrentStepIndex] = useState(0);
    62→  const [zoomLevel, setZoomLevel] = useState(1);
    63→  const [pan, setPan] = useState({ x: 0, y: 0 });
    64→  const [isDragging, setIsDragging] = useState(false);
    65→  const [showAllPoints, setShowAllPoints] = useState(false);
    66→  const [pressedKeys, setPressedKeys] = useState<Set<string>>(new Set());
    67→  const [activeTab, setActiveTab] = useState<'info' | 'reference'>('info');
    68→  const [isDetecting, setIsDetecting] = useState(true); // Start detecting immediately
    69→  const [hasAutoDetected, setHasAutoDetected] = useState(false);
    70→  const [autoZoomEnabled, setAutoZoomEnabled] = useState(true);
    71→
    72→  const currentLandmarkId = orderedIds[currentStepIndex];
    73→  const currentLandmark = landmarks.find((lm) => lm.id === currentLandmarkId);
    74→  const totalSteps = orderedIds.length;
    75→  const progress = ((currentStepIndex + 1) / totalSteps) * 100;
    76→
    77→  const color = currentLandmark
    78→    ? getLandmarkColor(currentLandmark.id, categories)
    79→    : '#00f3ff';
    80→
    81→  // Load image dimensions on mount
    82→  useEffect(() => {
    83→    const img = new window.Image();
    84→    img.onload = () => {
    85→      setImageDimensions({ width: img.naturalWidth, height: img.naturalHeight });
    86→    };
    87→    img.src = imageUrl;
    88→  }, [imageUrl]);
    89→
    90→  // Calculate the rendered image bounds within the container (accounting for object-contain)
    91→  const getImageBounds = useCallback(() => {
    92→    if (!containerRef.current || !imageDimensions) {
    93→      return { offsetX: 0, offsetY: 0, renderedWidth: 1, renderedHeight: 1, containerWidth: 1, containerHeight: 1 };
    94→    }
    95→
    96→    const container = containerRef.current;
    97→    const containerWidth = container.clientWidth;
    98→    const containerHeight = container.clientHeight;
    99→    const imageAspect = imageDimensions.width / imageDimensions.height;
   100→    const containerAspect = containerWidth / containerHeight;
   101→
   102→    let renderedWidth: number;
   103→    let renderedHeight: number;
   104→    let offsetX = 0;
   105→    let offsetY = 0;
   106→
   107→    if (imageAspect > containerAspect) {
   108→      // Image is wider - letterboxing top/bottom
   109→      renderedWidth = containerWidth;
   110→      renderedHeight = containerWidth / imageAspect;
   111→      offsetY = (containerHeight - renderedHeight) / 2;
   112→    } else {
   113→      // Image is taller - letterboxing left/right
   114→      renderedHeight = containerHeight;
   115→      renderedWidth = containerHeight * imageAspect;
   116→      offsetX = (containerWidth - renderedWidth) / 2;
   117→    }
   118→
   119→    return { offsetX, offsetY, renderedWidth, renderedHeight, containerWidth, containerHeight };
   120→  }, [imageDimensions]);
   121→
   122→  // Convert normalized image coordinates (0-1) to container pixel position
   123→  const getLandmarkPosition = useCallback((x: number, y: number) => {
   124→    const bounds = getImageBounds();
   125→    const pixelX = bounds.offsetX + x * bounds.renderedWidth;
   126→    const pixelY = bounds.offsetY + y * bounds.renderedHeight;
   127→    // Convert to percentage of container
   128→    return {
   129→      left: `${(pixelX / bounds.containerWidth) * 100}%`,
   130→      top: `${(pixelY / bounds.containerHeight) * 100}%`,
   131→    };
   132→  }, [getImageBounds]);
   133→
   134→  useEffect(() => {
   135→    onLandmarksChange?.(landmarks);
   136→  }, [landmarks, onLandmarksChange]);
   137→
   138→  const updateLandmarkPosition = useCallback(
   139→    (x: number, y: number) => {
   140→      setLandmarks((prev) =>
   141→        prev.map((lm) =>
   142→          lm.id === currentLandmarkId ? { ...lm, x, y } : lm
   143→        )
   144→      );
   145→    },
   146→    [currentLandmarkId]
   147→  );
   148→
   149→  const handleContainerClick = useCallback(
   150→    (e: React.MouseEvent<HTMLDivElement>) => {
   151→      if (!containerRef.current || isDragging) return;
   152→
   153→      const rect = containerRef.current.getBoundingClientRect();
   154→      const bounds = getImageBounds();
   155→
   156→      // Get click position in container space (accounting for zoom and pan)
   157→      const rawX = (e.clientX - rect.left - rect.width / 2 - pan.x) / zoomLevel + rect.width / 2;
   158→      const rawY = (e.clientY - rect.top - rect.height / 2 - pan.y) / zoomLevel + rect.height / 2;
   159→
   160→      // Convert container coordinates to normalized image coordinates (accounting for object-contain)
   161→      const x = Math.max(0, Math.min(1, (rawX - bounds.offsetX) / bounds.renderedWidth));
   162→      const y = Math.max(0, Math.min(1, (rawY - bounds.offsetY) / bounds.renderedHeight));
   163→
   164→      updateLandmarkPosition(x, y);
   165→    },
   166→    [isDragging, pan, zoomLevel, updateLandmarkPosition, getImageBounds]
   167→  );
   168→
   169→  const handleMouseDown = useCallback(
   170→    (e: React.MouseEvent) => {
   171→      if (e.button === 0 && zoomLevel > 1) {
   172→        setIsDragging(true);
   173→      }
   174→    },
   175→    [zoomLevel]
   176→  );
   177→
   178→  const handleMouseMove = useCallback(
   179→    (e: React.MouseEvent) => {
   180→      if (isDragging && zoomLevel > 1) {
   181→        setPan((prev) => ({
   182→          x: prev.x + e.movementX,
   183→          y: prev.y + e.movementY,
   184→        }));
   185→      }
   186→    },
   187→    [isDragging, zoomLevel]
   188→  );
   189→
   190→  const handleMouseUp = useCallback(() => {
   191→    setIsDragging(false);
   192→  }, []);
   193→
   194→  const handleResetZoom = useCallback(() => {
   195→    setZoomLevel(1);
   196→    setPan({ x: 0, y: 0 });
   197→  }, []);
   198→
   199→  const moveLandmark = useCallback((direction: 'up' | 'down' | 'left' | 'right') => {
   200→    const step = 0.002;
   201→    setLandmarks((prev) =>
   202→      prev.map((lm) => {
   203→        if (lm.id !== currentLandmarkId) return lm;
   204→        let newX = lm.x;
   205→        let newY = lm.y;
   206→        switch (direction) {
   207→          case 'up': newY = Math.max(0, lm.y - step); break;
   208→          case 'down': newY = Math.min(1, lm.y + step); break;
   209→          case 'left': newX = Math.max(0, lm.x - step); break;
   210→          case 'right': newX = Math.min(1, lm.x + step); break;
   211→        }
   212→        return { ...lm, x: newX, y: newY };
   213→      })
   214→    );
   215→  }, [currentLandmarkId]);
   216→
   217→  const handleNext = useCallback(() => {
   218→    if (currentStepIndex < totalSteps - 1) {
   219→      setCurrentStepIndex((prev) => prev + 1);
   220→      handleResetZoom();
   221→    } else {
   222→      onComplete?.(landmarks);
   223→    }
   224→  }, [currentStepIndex, totalSteps, landmarks, onComplete, handleResetZoom]);
   225→
   226→  const handlePrevious = useCallback(() => {
   227→    if (currentStepIndex > 0) {
   228→      setCurrentStepIndex((prev) => prev - 1);
   229→      handleResetZoom();
   230→    } else {
   231→      onBack?.();
   232→    }
   233→  }, [currentStepIndex, handleResetZoom, onBack]);
   234→
   235→  // Auto-zoom to the current landmark's region
   236→  const zoomToLandmark = useCallback((landmarkId: string) => {
   237→    if (!autoZoomEnabled || !containerRef.current) return;
   238→
   239→    const landmark = landmarks.find(lm => lm.id === landmarkId);
   240→    if (!landmark) return;
   241→
   242→    // Zoom to 2x and center on the landmark
   243→    const newZoom = 2;
   244→    setZoomLevel(newZoom);
   245→
   246→    // Calculate pan to center the landmark (accounting for object-contain)
   247→    const bounds = getImageBounds();
   248→    const centerX = bounds.containerWidth / 2;
   249→    const centerY = bounds.containerHeight / 2;
   250→    // Convert normalized landmark coords to actual pixel position
   251→    const landmarkX = bounds.offsetX + landmark.x * bounds.renderedWidth;
   252→    const landmarkY = bounds.offsetY + landmark.y * bounds.renderedHeight;
   253→
   254→    setPan({
   255→      x: (centerX - landmarkX) * newZoom,
   256→      y: (centerY - landmarkY) * newZoom,
   257→    });
   258→  }, [autoZoomEnabled, landmarks, getImageBounds]);
   259→
   260→  // Auto-detect landmarks using MediaPipe
   261→  const handleAutoDetect = useCallback(async () => {
   262→    setIsDetecting(true);
   263→    try {
   264→      const result = await detectFromImageUrl(imageUrl, mode);
   265→      if (result && result.landmarks.length > 0) {
   266→        // Update landmarks with detected positions
   267→        setLandmarks(prev =>
   268→          prev.map(lm => {
   269→            const detected = result.landmarks.find(d => d.id === lm.id);
   270→            if (detected) {
   271→              return { ...lm, x: detected.x, y: detected.y };
   272→            }
   273→            return lm;
   274→          })
   275→        );
   276→        setHasAutoDetected(true);
   277→        // Zoom to first landmark after detection
   278→        zoomToLandmark(orderedIds[0]);
   279→      }
   280→    } catch (error) {
   281→      console.error('Auto-detection failed:', error);
   282→    } finally {
   283→      setIsDetecting(false);
   284→    }
   285→  }, [imageUrl, mode, orderedIds, zoomToLandmark]);
   286→
   287→  // Auto-detect on mount
   288→  useEffect(() => {
   289→    handleAutoDetect();
   290→    // eslint-disable-next-line react-hooks/exhaustive-deps
   291→  }, []); // Run once on mount
   292→
   293→  // Auto-zoom when step changes
   294→  useEffect(() => {
   295→    if (hasAutoDetected && autoZoomEnabled && currentLandmarkId) {
   296→      zoomToLandmark(currentLandmarkId);
   297→    }
   298→  }, [currentStepIndex, currentLandmarkId, hasAutoDetected, autoZoomEnabled, zoomToLandmark]);
   299→
   300→  // Keyboard handlers with proper state tracking
   301→  useEffect(() => {
   302→    const handleKeyDown = (e: KeyboardEvent) => {
   303→      if (['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight', 'Enter', 'Backspace', 'r', 'R'].includes(e.key)) {
   304→        e.preventDefault();
   305→      }
   306→
   307→      setPressedKeys((prev) => new Set(prev).add(e.key));
   308→
   309→      switch (e.key) {
   310→        case 'ArrowUp': moveLandmark('up'); break;
   311→        case 'ArrowDown': moveLandmark('down'); break;
   312→        case 'ArrowLeft': moveLandmark('left'); break;
   313→        case 'ArrowRight': moveLandmark('right'); break;
   314→        case 'Enter': handleNext(); break;
   315→        case 'Backspace': handlePrevious(); break;
   316→        case 'r':
   317→        case 'R': handleResetZoom(); break;
   318→      }
   319→    };
   320→
   321→    const handleKeyUp = (e: KeyboardEvent) => {
   322→      setPressedKeys((prev) => {
   323→        const next = new Set(prev);
   324→        next.delete(e.key);
   325→        return next;
   326→      });
   327→    };
   328→
   329→    window.addEventListener('keydown', handleKeyDown);
   330→    window.addEventListener('keyup', handleKeyUp);
   331→    return () => {
   332→      window.removeEventListener('keydown', handleKeyDown);
   333→      window.removeEventListener('keyup', handleKeyUp);
   334→    };
   335→  }, [moveLandmark, handleNext, handlePrevious, handleResetZoom]);
   336→
   337→  const isKeyPressed = (key: string) => pressedKeys.has(key);
   338→
   339→  return (
   340→    <div className="fixed inset-0 bg-black">
   341→      <div className="flex flex-col md:flex-row h-full gap-3 p-3 md:p-4">
   342→        {/* Sidebar - Right on desktop */}
   343→        <div className="w-full md:w-80 flex-shrink-0 flex flex-col gap-3 md:order-2 overflow-hidden md:overflow-y-auto">
   344→          {/* Progress Card */}
   345→          <div className="bg-neutral-900 rounded-xl border border-neutral-800 overflow-hidden flex-shrink-0">
   346→            <div className="p-4">
   347→              <div className="flex items-center justify-between text-xs text-neutral-400 mb-2">
   348→                <span>Step {currentStepIndex + 1} of {totalSteps}</span>
   349→                <span>{Math.round(progress)}%</span>
   350→              </div>
   351→              <div className="h-1.5 bg-neutral-800 rounded-full overflow-hidden mb-3">
   352→                <motion.div
   353→                  className="h-full rounded-full"
   354→                  style={{ width: `${progress}%`, backgroundColor: '#00f3ff' }}
   355→                  transition={{ duration: 0.3 }}
   356→                />
   357→              </div>
   358→              <div className="flex items-center gap-2">
   359→                <div className="w-6 h-6 bg-[#00f3ff] rounded-full flex items-center justify-center flex-shrink-0">
   360→                  <Check className="w-3.5 h-3.5 text-black" />
   361→                </div>
   362→                <span className="text-sm font-medium text-white">
   363→                  {currentStepIndex} placed
   364→                </span>
   365→              </div>
   366→            </div>
   367→
   368→            {/* View Tabs */}
   369→            <div className="flex border-t border-neutral-800">
   370→              <button
   371→                onClick={() => setActiveTab('info')}
   372→                className={`flex-1 py-2.5 text-xs font-medium transition-colors ${
   373→                  activeTab === 'info'
   374→                    ? 'text-[#00f3ff] border-b-2 border-[#00f3ff]'
   375→                    : 'text-neutral-500 hover:text-neutral-300'
   376→                }`}
   377→              >
   378→                Current Point
   379→              </button>
   380→              <button
   381→                onClick={() => setActiveTab('reference')}
