   597→            <div className="flex gap-2">
   598→              <button
   599→                onClick={() => setShowAllPoints(!showAllPoints)}
   600→                className={`flex-1 py-2.5 rounded-lg text-xs font-medium transition-all flex items-center justify-center gap-2 ${
   601→                  showAllPoints
   602→                    ? 'bg-[#00f3ff] text-black'
   603→                    : 'bg-neutral-800 text-neutral-400 hover:bg-neutral-700'
   604→                }`}
   605→              >
   606→                {showAllPoints ? <Eye className="w-4 h-4" /> : <EyeOff className="w-4 h-4" />}
   607→                {showAllPoints ? 'All Points' : 'Current Only'}
   608→              </button>
   609→              <button
