   629→            style={{
   630→              cursor: zoomLevel > 1 && isDragging ? 'grabbing' : zoomLevel > 1 ? 'grab' : 'crosshair',
   631→            }}
   632→          >
   633→            {/* Transformed Content */}
   634→            <div
   635→              className="absolute inset-0 transition-transform duration-100"
   636→              style={{
   637→                transform: `scale(${zoomLevel}) translate(${pan.x / zoomLevel}px, ${pan.y / zoomLevel}px)`,
   638→                transformOrigin: 'center center',
   639→              }}
   640→            >
   641→              {/* User's Image */}
   642→              <Image
   643→                src={imageUrl}
   644→                alt="Your photo"
   645→                fill
