   647→                unoptimized
   648→                draggable={false}
   649→              />
   650→
   651→              {/* Loading Overlay */}
   652→              {isDetecting && (
   653→                <div className="absolute inset-0 bg-black/70 flex flex-col items-center justify-center z-50">
   654→                  <Loader2 className="w-12 h-12 text-[#00f3ff] animate-spin mb-4" />
   655→                  <p className="text-white text-lg font-medium">Detecting landmarks...</p>
   656→                  <p className="text-neutral-400 text-sm mt-2">This may take a few seconds</p>
   657→                </div>
   658→              )}
   659→
   660→              {/* All Landmarks (dimmed) */}
   661→              {showAllPoints &&
   662→                landmarks.map((landmark) => {
   663→                  if (landmark.id === currentLandmarkId) return null;
   664→                  const lmColor = getLandmarkColor(landmark.id, categories);
   665→                  const position = getLandmarkPosition(landmark.x, landmark.y);
   666→                  return (
   667→                    <div
   668→                      key={landmark.id}
   669→                      className="absolute w-2.5 h-2.5 -translate-x-1/2 -translate-y-1/2 pointer-events-none"
   670→                      style={{
   671→                        ...position,
   672→                        opacity: 0.4,
   673→                        transform: `translate(-50%, -50%) scale(${1 / zoomLevel})`,
   674→                      }}
   675→                    >
   676→                      <div
   677→                        className="w-full h-full rounded-full border border-black/50"
   678→                        style={{ backgroundColor: lmColor }}
   679→                      />
