   681→                  );
   682→                })}
   683→
   684→              {/* Current Landmark (highlighted) */}
   685→              {currentLandmark && (
   686→                <div
   687→                  className="absolute pointer-events-none"
   688→                  style={{
   689→                    ...getLandmarkPosition(currentLandmark.x, currentLandmark.y),
   690→                    transform: `scale(${1 / zoomLevel})`,
   691→                  }}
   692→                >
   693→                  {/* Pulsing glow - centered on the point */}
   694→                  <motion.div
   695→                    className="absolute rounded-full"
   696→                    style={{
   697→                      width: 48,
   698→                      height: 48,
   699→                      left: -24,
   700→                      top: -24,
   701→                      background: `radial-gradient(circle, ${color}40 0%, transparent 70%)`,
   702→                    }}
   703→                    animate={{
   704→                      scale: [1, 1.5, 1],
   705→                      opacity: [0.8, 0.3, 0.8],
   706→                    }}
   707→                    transition={{
   708→                      duration: 1.5,
   709→                      repeat: Infinity,
   710→                      ease: 'easeInOut',
   711→                    }}
   712→                  />
   713→
   714→                  {/* Main dot - centered on the point */}
   715→                  <motion.div
   716→                    initial={{ scale: 0 }}
   717→                    animate={{ scale: 1 }}
   718→                    className="absolute rounded-full border-2 border-white"
   719→                    style={{
   720→                      width: 14,
   721→                      height: 14,
   722→                      left: -7,
   723→                      top: -7,
   724→                      backgroundColor: color,
   725→                      boxShadow: `0 0 12px ${color}, 0 0 24px ${color}50`,
   726→                    }}
   727→                  />
   728→                </div>
   729→              )}
   730→            </div>
   731→
   732→            {/* Zoom indicator */}
   733→            {zoomLevel > 1 && (
   734→              <div className="absolute top-3 left-3 px-3 py-1.5 rounded-lg bg-black/80 border border-neutral-700 text-white text-xs">
   735→                {zoomLevel}x zoom - Drag to pan
   736→              </div>
   737→            )}
   738→
   739→            {/* Current landmark label overlay */}
   740→            <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black via-black/80 to-transparent p-4 pointer-events-none">
   741→              <p className="text-center text-white text-sm font-medium">
   742→                Click to place:{' '}
   743→                <span className="font-semibold" style={{ color }}>{currentLandmark?.label}</span>
   744→              </p>
   745→            </div>
   746→          </div>
   747→
   748→          {/* Navigation Bar - Fixed at bottom of image area */}
   749→          <div className="mt-3 flex items-center justify-center gap-3">
   750→            <button
   751→              onClick={handlePrevious}
   752→              className="p-2.5 rounded-xl bg-neutral-800 text-neutral-300 hover:bg-neutral-700 border border-neutral-700 transition-all"
   753→            >
   754→              <ChevronLeft className="w-5 h-5" />
   755→            </button>
   756→
   757→            {/* Step dots - show current position indicator */}
   758→            <div className="flex items-center gap-2 px-4 py-2 bg-neutral-900 rounded-xl border border-neutral-800">
   759→              <span className="text-xs text-neutral-400 font-mono min-w-[4ch]">
   760→                {currentStepIndex + 1}
   761→              </span>
   762→              <div className="w-32 h-1.5 bg-neutral-800 rounded-full overflow-hidden">
   763→                <div
   764→                  className="h-full bg-[#00f3ff] rounded-full transition-all duration-200"
   765→                  style={{ width: `${((currentStepIndex + 1) / totalSteps) * 100}%` }}
   766→                />
   767→              </div>
   768→              <span className="text-xs text-neutral-400 font-mono min-w-[4ch] text-right">
   769→                {totalSteps}
