     1→/**
     2→ * Unified Face Detection Service
     3→ * Uses MediaPipe Tasks Vision for optimal performance
     4→ * Singleton pattern with preloading and warmup support
     5→ */
     6→
     7→import { FaceLandmarker, FilesetResolver, FaceLandmarkerResult } from '@mediapipe/tasks-vision';
     8→
     9→const CONFIG = {
    10→  MODEL_URL: 'https://storage.googleapis.com/mediapipe-models/face_landmarker/face_landmarker/float16/1/face_landmarker.task',
    11→  WASM_URL: 'https://cdn.jsdelivr.net/npm/@mediapipe/tasks-vision@0.10.22/wasm',
    12→};
    13→
    14→class FaceDetectionService {
    15→  private landmarker: FaceLandmarker | null = null;
    16→  private initPromise: Promise<FaceLandmarker> | null = null;
    17→  private isWarmedUp = false;
    18→
    19→  async initialize(): Promise<FaceLandmarker> {
    20→    if (this.landmarker) return this.landmarker;
    21→    if (this.initPromise) return this.initPromise;
    22→
    23→    this.initPromise = this.createLandmarker();
    24→    this.landmarker = await this.initPromise;
    25→    this.initPromise = null;
    26→    return this.landmarker;
    27→  }
    28→
    29→  private async createLandmarker(): Promise<FaceLandmarker> {
    30→    const vision = await FilesetResolver.forVisionTasks(CONFIG.WASM_URL);
    31→    return FaceLandmarker.createFromOptions(vision, {
    32→      baseOptions: {
    33→        modelAssetPath: CONFIG.MODEL_URL,
    34→        delegate: 'GPU',
    35→      },
    36→      runningMode: 'IMAGE',
    37→      numFaces: 1,
    38→      outputFaceBlendshapes: false,
    39→      outputFacialTransformationMatrixes: false,
    40→    });
    41→  }
    42→
    43→  async warmup(): Promise<void> {
    44→    if (this.isWarmedUp) return;
    45→    const landmarker = await this.initialize();
    46→
    47→    // Create dummy image for warmup (forces GPU shader compilation)
    48→    const canvas = document.createElement('canvas');
    49→    canvas.width = 224;
