   525→
   526→  const maleGuideIds = ['hair-loss', 'beard-growth', 'mewing-jawline', 'gynecomastia', 'neck-training'];
   527→  const femaleGuideIds = ['anti-aging-women', 'glass-skin', 'hormonal-skin', 'body-contouring-women', 'postpartum-recovery', 'feminine-features'];
   528→
   529→  if (gender === 'male') {
   530→    return ALL_GUIDES.filter(g => !femaleGuideIds.includes(g.id));
   531→  } else {
   532→    return ALL_GUIDES.filter(g => !maleGuideIds.includes(g.id));
   533→  }
   534→}
