   318→// ============================================
   319→// GUIDE VIEWER MODAL
   320→// ============================================
   321→
   322→interface GuideViewerProps {
   323→  guide: Guide;
   324→  onClose: () => void;
   325→  onSwitchGuide: (guide: Guide) => void;
   326→  allGuides: Guide[];
   327→  gender: 'male' | 'female';
   328→}
   329→
   330→function GuideViewer({ guide, onClose, onSwitchGuide, allGuides, gender }: GuideViewerProps) {
   331→  const [currentSection, setCurrentSection] = useState(0);
   332→  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
   333→  const [expandedCategories, setExpandedCategories] = useState<string[]>(() => {
   334→    // Find the category of the current guide and expand it by default
   335→    const currentCategory = GUIDE_CATEGORIES.find(c => c.guideIds.includes(guide.id));
   336→    return currentCategory ? [currentCategory.id] : [];
   337→  });
   338→
   339→  // Reset to first section when switching guides
   340→  useEffect(() => {
   341→    setCurrentSection(0);
   342→    // Also expand the new guide's category
   343→    const newCategory = GUIDE_CATEGORIES.find(c => c.guideIds.includes(guide.id));
   344→    if (newCategory) {
   345→      setExpandedCategories(prev =>
   346→        prev.includes(newCategory.id) ? prev : [...prev, newCategory.id]
   347→      );
   348→    }
   349→  }, [guide.id]);
   350→
   351→  const section = guide.sections[currentSection];
   352→
   353→  if (!section) return null;
   354→
   355→  // Calculate reading progress (0% at start, 100% at last section)
   356→  const progress = guide.sections.length > 1
   357→    ? (currentSection / (guide.sections.length - 1)) * 100
   358→    : 100;
   359→
   360→  const toggleCategory = (categoryId: string) => {
   361→    setExpandedCategories(prev =>
   362→      prev.includes(categoryId)
   363→        ? prev.filter(id => id !== categoryId)
   364→        : [...prev, categoryId]
   365→    );
   366→  };
   367→
   368→  // Filter categories by gender
   369→  const filteredCategories = GUIDE_CATEGORIES.filter(cat => {
   370→    if (cat.id === 'male' && gender === 'female') return false;
   371→    if (cat.id === 'female' && gender === 'male') return false;
   372→    return true;
   373→  });
   374→
   375→  const colorClasses: Record<string, { text: string; bg: string; border: string }> = {
   376→    blue: { text: 'text-cyan-400', bg: 'bg-cyan-500/10', border: 'border-cyan-500/20' },
   377→    purple: { text: 'text-purple-400', bg: 'bg-purple-500/10', border: 'border-purple-500/20' },
   378→    amber: { text: 'text-amber-400', bg: 'bg-amber-500/10', border: 'border-amber-500/20' },
   379→    pink: { text: 'text-pink-400', bg: 'bg-pink-500/10', border: 'border-pink-500/20' },
   380→    emerald: { text: 'text-emerald-400', bg: 'bg-emerald-500/10', border: 'border-emerald-500/20' },
   381→    red: { text: 'text-red-400', bg: 'bg-red-500/10', border: 'border-red-500/20' },
   382→  };
   383→
   384→  return (
   385→    <motion.div
   386→      initial={{ opacity: 0 }}
   387→      animate={{ opacity: 1 }}
   388→      exit={{ opacity: 0 }}
   389→      className="fixed inset-0 z-50 bg-black"
   390→    >
   391→      {/* Header */}
   392→      <div className="sticky top-0 z-20 bg-black/95 backdrop-blur-md border-b border-neutral-900">
   393→        <div className="flex items-center justify-between px-4 lg:px-8 py-3">
   394→          <button
   395→            onClick={onClose}
   396→            className="flex items-center gap-2 text-neutral-500 hover:text-cyan-400 text-xs font-black uppercase tracking-widest transition-colors"
   397→          >
   398→            <ArrowLeft size={14} />
   399→            <span className="hidden sm:inline">BACK TO GUIDES</span>
   400→          </button>
   401→
   402→          {/* Progress bar */}
   403→          <div className="hidden md:flex items-center gap-4 flex-1 max-w-md mx-8">
   404→            <div className="flex-1 h-1 bg-neutral-900 rounded-full overflow-hidden">
   405→              <motion.div
   406→                className="h-full bg-gradient-to-r from-cyan-500 to-cyan-400"
   407→                initial={{ width: 0 }}
   408→                animate={{ width: `${progress}%` }}
   409→                transition={{ duration: 0.3 }}
   410→              />
   411→            </div>
   412→            <span className="text-[10px] font-black uppercase tracking-widest text-neutral-600 whitespace-nowrap">
   413→              {currentSection + 1} / {guide.sections.length}
   414→            </span>
   415→          </div>
   416→
   417→          <div className="flex items-center gap-3">
   418→            {/* Mobile menu button */}
   419→            <button
   420→              onClick={() => setIsSidebarOpen(!isSidebarOpen)}
