   422→            >
   423→              <BookOpen size={18} className="text-neutral-400" />
   424→            </button>
   425→            <button
   426→              onClick={onClose}
   427→              className="p-2 rounded-xl bg-neutral-900/50 border border-white/5 hover:border-white/10 transition-colors"
   428→            >
   429→              <X size={18} className="text-neutral-400" />
   430→            </button>
   431→          </div>
   432→        </div>
   433→      </div>
