   871→                      )}
   872→                    </div>
   873→                  </button>
   874→
   875→                  <button
   876→                    onClick={() => setCurrentSection(Math.min(guide.sections.length - 1, currentSection + 1))}
   877→                    disabled={currentSection === guide.sections.length - 1}
   878→                    className={`flex items-center gap-3 group transition-all ${currentSection === guide.sections.length - 1
   879→                      ? 'text-neutral-800 cursor-not-allowed'
   880→                      : 'text-neutral-500 hover:text-white'
