   899→
   900→            {/* Forum Discussion Link (after last section) */}
   901→            {currentSection === guide.sections.length - 1 && (
   902→              <div className="mt-16">
   903→                <ForumDiscussionLink
   904→                  forumCategory={guide.forumCategory}
   905→                  guideTitle={guide.title}
