   908→            )}
   909→          </article>
   910→        </main>
   911→      </div>
   912→    </motion.div>
   913→  );
   914→}
   915→
   916→// ============================================
   917→// GUIDE CARD
   918→// ============================================
   919→
   920→interface GuideCardProps {
   921→  guide: Guide;
   922→  index: number;
   923→  onOpen: (guide: Guide) => void;
   924→}
   925→
   926→function GuideCard({ guide, index, onOpen }: GuideCardProps) {
   927→  const getCategoryColor = (guideId: string) => {
   928→    const category = GUIDE_CATEGORIES.find(c => c.guideIds.includes(guideId));
   929→    if (!category) return 'cyan';
   930→    const colorMap: Record<string, string> = {
   931→      blue: 'cyan',
   932→      purple: 'purple',
   933→      amber: 'amber',
   934→      pink: 'pink',
   935→      emerald: 'emerald',
   936→      red: 'red',
   937→    };
   938→    return colorMap[category.color] || 'cyan';
   939→  };
   940→
   941→  const color = getCategoryColor(guide.id);
   942→
   943→  const colorClasses: Record<string, { bg: string; border: string; text: string; iconBg: string }> = {
   944→    cyan: {
   945→      bg: 'bg-neutral-900/40',
   946→      border: 'border-white/5 hover:border-cyan-500/20',
   947→      text: 'text-cyan-400',
   948→      iconBg: 'bg-neutral-900 border border-white/10',
   949→    },
   950→    purple: {
   951→      bg: 'bg-neutral-900/40',
   952→      border: 'border-white/5 hover:border-purple-500/20',
   953→      text: 'text-purple-400',
   954→      iconBg: 'bg-neutral-900 border border-white/10',
   955→    },
   956→    amber: {
   957→      bg: 'bg-neutral-900/40',
   958→      border: 'border-white/5 hover:border-amber-500/20',
   959→      text: 'text-amber-400',
   960→      iconBg: 'bg-neutral-900 border border-white/10',
   961→    },
   962→    pink: {
   963→      bg: 'bg-neutral-900/40',
   964→      border: 'border-white/5 hover:border-pink-500/20',
   965→      text: 'text-pink-400',
   966→      iconBg: 'bg-neutral-900 border border-white/10',
   967→    },
   968→    emerald: {
   969→      bg: 'bg-neutral-900/40',
   970→      border: 'border-white/5 hover:border-emerald-500/20',
   971→      text: 'text-emerald-400',
   972→      iconBg: 'bg-neutral-900 border border-white/10',
   973→    },
   974→    red: {
   975→      bg: 'bg-neutral-900/40',
   976→      border: 'border-white/5 hover:border-red-500/20',
   977→      text: 'text-red-400',
   978→      iconBg: 'bg-neutral-900 border border-white/10',
   979→    },
   980→  };
   981→
   982→  const styles = colorClasses[color] || colorClasses.cyan;
   983→
   984→  return (
   985→    <motion.div
   986→      onClick={() => onOpen(guide)}
   987→      className={`${styles.bg} ${styles.border} border rounded-2xl p-5 transition-all cursor-pointer group hover:-translate-y-0.5`}
   988→      initial={{ opacity: 0, y: 20 }}
   989→      animate={{ opacity: 1, y: 0 }}
   990→      transition={{ delay: index * 0.05 }}
   991→    >
   992→      <div className="flex items-start justify-between mb-4">
   993→        <div className={`w-10 h-10 ${styles.iconBg} rounded-xl flex items-center justify-center ${styles.text}`}>
   994→          {getIconComponent(guide.icon)}
   995→        </div>
   996→        <div className="flex items-center gap-1.5 px-2 py-1 rounded-lg bg-neutral-900/50 border border-white/5">
   997→          <Clock size={10} className="text-neutral-600" />
   998→          <span className="text-[10px] font-black uppercase tracking-wider text-neutral-500">{guide.estimatedReadTime} MIN</span>
   999→        </div>
  1000→      </div>
  1001→
  1002→      <h3 className="font-black uppercase tracking-tight text-white mb-1 group-hover:text-cyan-400 transition-colors">
  1003→        {guide.title}
  1004→      </h3>
  1005→
  1006→      {guide.subtitle && (
  1007→        <p className="text-[10px] font-bold uppercase tracking-wider text-neutral-600 mb-2">{guide.subtitle}</p>
  1008→      )}
  1009→
  1010→      <p className="text-sm text-neutral-500 mb-4 line-clamp-2 font-medium">{guide.description}</p>
  1011→
  1012→      <div className="flex items-center justify-between pt-3 border-t border-white/5">
  1013→        <div className="flex items-center gap-2">
  1014→          <span className="text-[10px] font-black uppercase tracking-widest text-neutral-600">
  1015→            {guide.sections.length} SECTIONS
  1016→          </span>
  1017→          {guide.productIds && guide.productIds.length > 0 && (
  1018→            <>
  1019→              <span className="w-1 h-1 rounded-full bg-neutral-800" />
  1020→              <span className="text-[10px] font-black uppercase tracking-widest text-neutral-600">
  1021→                {guide.productIds.length} PRODUCTS
  1022→              </span>
  1023→            </>
  1024→          )}
  1025→        </div>
  1026→        <ChevronRight size={16} className="text-neutral-700 group-hover:text-cyan-400 transition-colors" />
  1027→      </div>
  1028→    </motion.div>
  1029→  );
  1030→}
  1031→
  1032→// ============================================
  1033→// CATEGORY SECTION
  1034→// ============================================
  1035→
  1036→interface CategorySectionProps {
  1037→  category: GuideCategory;
  1038→  onOpenGuide: (guide: Guide) => void;
  1039→  gender: 'male' | 'female';
  1040→}
  1041→
  1042→function CategorySection({ category, onOpenGuide, gender }: CategorySectionProps) {
  1043→  // Skip gender-specific categories for the opposite gender
  1044→  if (category.id === 'male' && gender === 'female') return null;
  1045→  if (category.id === 'female' && gender === 'male') return null;
  1046→
  1047→  const guides = getGuidesByCategory(category.id);
  1048→
  1049→  if (guides.length === 0) return null;
  1050→
  1051→  const colorClasses: Record<string, { text: string; iconBg: string }> = {
  1052→    blue: { text: 'text-cyan-400', iconBg: 'bg-neutral-900 border border-white/10' },
  1053→    purple: { text: 'text-purple-400', iconBg: 'bg-neutral-900 border border-white/10' },
  1054→    pink: { text: 'text-pink-400', iconBg: 'bg-neutral-900 border border-white/10' },
  1055→    cyan: { text: 'text-cyan-400', iconBg: 'bg-neutral-900 border border-white/10' },
  1056→    emerald: { text: 'text-emerald-400', iconBg: 'bg-neutral-900 border border-white/10' },
  1057→    red: { text: 'text-red-400', iconBg: 'bg-neutral-900 border border-white/10' },
  1058→    amber: { text: 'text-amber-400', iconBg: 'bg-neutral-900 border border-white/10' },
  1059→  };
  1060→
  1061→  const styles = colorClasses[category.color] || colorClasses.cyan;
  1062→
  1063→  return (
  1064→    <div className="mb-12">
  1065→      {/* Category Header */}
  1066→      <div className="flex items-center gap-4 mb-6">
  1067→        <div className={`w-10 h-10 ${styles.iconBg} rounded-xl flex items-center justify-center ${styles.text}`}>
  1068→          {getIconComponent(category.icon)}
  1069→        </div>
  1070→        <div className="flex-1">
  1071→          <h3 className="text-[10px] font-black uppercase tracking-[0.4em] text-neutral-600 flex items-center gap-4">
  1072→            {category.name}
  1073→            <div className="flex-1 h-px bg-neutral-800" />
  1074→          </h3>
  1075→          <p className="text-[10px] font-bold uppercase tracking-wider text-neutral-700 mt-1">{category.description}</p>
  1076→        </div>
  1077→      </div>
  1078→
  1079→      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
  1080→        {guides.map((guide, idx) => (
  1081→          <GuideCard key={guide.id} guide={guide} index={idx} onOpen={onOpenGuide} />
  1082→        ))}
  1083→      </div>
  1084→    </div>
  1085→  );
  1086→}
  1087→
  1088→// ============================================
  1089→// SEARCH BAR
  1090→// ============================================
  1091→
  1092→interface SearchBarProps {
  1093→  query: string;
  1094→  onQueryChange: (query: string) => void;
  1095→}
  1096→
  1097→function SearchBar({ query, onQueryChange }: SearchBarProps) {
  1098→  return (
  1099→    <div className="relative mb-8">
  1100→      <Search size={16} className="absolute left-4 top-1/2 -translate-y-1/2 text-neutral-600" />
  1101→      <input
  1102→        type="text"
  1103→        placeholder="SEARCH GUIDES..."
  1104→        value={query}
  1105→        onChange={(e) => onQueryChange(e.target.value)}
  1106→        className="w-full bg-neutral-900/40 border border-white/5 rounded-xl pl-11 pr-4 py-3.5 text-white placeholder:text-neutral-600 placeholder:font-black placeholder:uppercase placeholder:tracking-widest placeholder:text-xs focus:outline-none focus:border-cyan-500/30 transition-colors font-medium text-sm"
  1107→      />
  1108→    </div>
  1109→  );
  1110→}
  1111→
  1112→// ============================================
  1113→// MAIN TAB COMPONENT
  1114→// ============================================
  1115→
  1116→export function GuidesTab() {
  1117→  const [searchQuery, setSearchQuery] = useState('');
  1118→  const [selectedGuide, setSelectedGuide] = useState<Guide | null>(null);
  1119→  const { gender } = useResults();
  1120→
  1121→  // Filter guides by gender (excludes opposite gender's specific guides)
  1122→  const genderFilteredGuides = getGuidesByGender(gender);
  1123→
  1124→  const filteredGuides = searchQuery.trim()
  1125→    ? genderFilteredGuides.filter(g =>
  1126→        g.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
  1127→        g.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
  1128→        (g.subtitle && g.subtitle.toLowerCase().includes(searchQuery.toLowerCase())) ||
  1129→        (g.tags && g.tags.some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase())))
  1130→      )
  1131→    : null;
  1132→
  1133→  const handleOpenGuide = (guide: Guide) => {
  1134→    setSelectedGuide(guide);
  1135→  };
  1136→
  1137→  const handleCloseGuide = () => {
  1138→    setSelectedGuide(null);
  1139→  };
  1140→
  1141→  const handleSwitchGuide = (guide: Guide) => {
  1142→    setSelectedGuide(guide);
  1143→  };
  1144→
  1145→  return (
  1146→    <>
  1147→      <TabContent
  1148→        title="Product Guides"
  1149→        subtitle="Evidence-based guides for optimal results"
  1150→      >
  1151→        <div className="max-w-5xl mx-auto">
  1152→          <GuideStatsCard />
  1153→
  1154→          <SearchBar query={searchQuery} onQueryChange={setSearchQuery} />
  1155→
  1156→          {filteredGuides ? (
  1157→            // Search Results
  1158→            <div>
  1159→              <p className="text-[10px] font-black uppercase tracking-widest text-neutral-600 mb-6">
  1160→                FOUND {filteredGuides.length} GUIDE{filteredGuides.length !== 1 ? 'S' : ''} MATCHING &ldquo;{searchQuery.toUpperCase()}&rdquo;
  1161→              </p>
  1162→              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
  1163→                {filteredGuides.map((guide, idx) => (
  1164→                  <GuideCard key={guide.id} guide={guide} index={idx} onOpen={handleOpenGuide} />
  1165→                ))}
  1166→              </div>
  1167→              {filteredGuides.length === 0 && (
  1168→                <div className="text-center py-16">
  1169→                  <div className="w-16 h-16 rounded-2xl bg-neutral-900 border border-white/10 flex items-center justify-center mx-auto mb-4">
  1170→                    <Search size={24} className="text-neutral-700" />
  1171→                  </div>
  1172→                  <p className="text-[10px] font-black uppercase tracking-widest text-neutral-600">NO GUIDES FOUND MATCHING YOUR SEARCH</p>
  1173→                </div>
  1174→              )}
  1175→            </div>
  1176→          ) : (
  1177→            // Category View
  1178→            <div>
  1179→              {GUIDE_CATEGORIES.map(category => (
  1180→                <CategorySection key={category.id} category={category} onOpenGuide={handleOpenGuide} gender={gender} />
  1181→              ))}
  1182→            </div>
  1183→          )}
  1184→        </div>
  1185→      </TabContent>
  1186→
  1187→      {/* Guide Viewer Modal */}
  1188→      <AnimatePresence>
  1189→        {selectedGuide && (
  1190→          <GuideViewer
  1191→            guide={selectedGuide}
  1192→            onClose={handleCloseGuide}
  1193→            onSwitchGuide={handleSwitchGuide}
  1194→            allGuides={genderFilteredGuides}
  1195→            gender={gender}
  1196→          />
  1197→        )}
  1198→      </AnimatePresence>
  1199→    </>
  1200→  );
  1201→}
