     1→/**
     2→ * Face Landmark Detection using MediaPipe Tasks Vision
     3→ * Maps detected landmarks to our custom facial landmarks
     4→ *
     5→ * Detection hierarchy:
     6→ * - Front profile: MediaPipe Face Mesh (478 landmarks)
     7→ * - Side profile: Server-side InsightFace (106 landmarks) → Edge-based fallback
     8→ */
     9→
    10→import { faceDetectionService } from './faceDetectionService';
    11→import { detectSideProfile } from './sideProfileDetection';
    12→import { detectSideProfileServer } from './serverSideDetection';
    13→
    14→// MediaPipe Face Mesh landmark indices (478 landmarks total)
    15→// Reference: https://github.com/google/mediapipe/blob/master/mediapipe/modules/face_geometry/data/canonical_face_model_uv_visualization.png
    16→
    17→// Front profile landmark mapping
    18→// Based on the reference website's exact MediaPipe indices
    19→// Uses SUBJECT's perspective: "left" = subject's left (appears on RIGHT side of image)
    20→export const MEDIAPIPE_FRONT_MAPPING: Record<string, number> = {
    21→  // Head
    22→  trichion: 10, // hairline
    23→
    24→  // Left Eye (subject's LEFT eye - appears on RIGHT of image)
    25→  left_pupila: 468,
    26→  left_canthus_medialis: 133,
    27→  left_canthus_lateralis: 33,
    28→  left_palpebra_superior: 470,
    29→  left_palpebra_inferior: 472,
    30→  left_sulcus_palpebralis_lateralis: 247,
    31→  left_pretarsal_skin_crease: 470,
    32→
    33→  // Right Eye (subject's RIGHT eye - appears on LEFT of image)
    34→  right_pupila: 473,
    35→  right_canthus_medialis: 362,
    36→  right_canthus_lateralis: 263,
    37→  right_palpebra_superior: 475,
    38→  right_palpebra_inferior: 374,
    39→  right_sulcus_palpebralis_lateralis: 467,
    40→  right_pretarsal_skin_crease: 475,
    41→
    42→  // Left Brow (subject's LEFT brow - appears on RIGHT of image)
    43→  left_supercilium_medialis: 107,
    44→  left_supercilium_medial_corner: 55,
    45→  left_supercilium_superior: 52,
    46→  left_supercilium_apex: 105,
    47→  left_supercilium_lateralis: 70,
    48→
    49→  // Right Brow (subject's RIGHT brow - appears on LEFT of image)
    50→  right_supercilium_medialis: 336,
    51→  right_supercilium_medial_corner: 285,
    52→  right_supercilium_superior: 282,
    53→  right_supercilium_apex: 334,
    54→  right_supercilium_lateralis: 276,
    55→
    56→  // Nose
    57→  nasal_base: 290, // Base of nose (per landmarkers.js)
    58→  left_dorsum_nasi: 174,
    59→  right_dorsum_nasi: 399,
    60→  left_ala_nasi: 48,
    61→  right_ala_nasi: 278,
    62→  subnasale: 2, // noseBottom in landmarkers.js
    63→
    64→  // Mouth
    65→  labrale_superius: 267, // cupidsBow - peak of cupid's bow
    66→  cupids_bow: 0, // innerCupidsBow - center dip of upper lip
    67→  mouth_middle: 14,
    68→  labrale_inferius: 17,
    69→  left_cheilion: 61,
    70→  right_cheilion: 306,
    71→
    72→  // Jaw
    73→  left_gonion_superior: 58,
    74→  right_gonion_superior: 288,
    75→  left_gonion_inferior: 172,
    76→  right_gonion_inferior: 397,
    77→
    78→  // Chin
    79→  left_mentum_lateralis: 176,
    80→  right_mentum_lateralis: 400,
    81→  menton: 152,
    82→
    83→  // Cheeks
    84→  left_zygion: 234,
    85→  right_zygion: 454,
    86→  left_malar: 205,        // Cheek center point for fullness calculation
    87→  right_malar: 425,       // Cheek center point for fullness calculation
    88→  left_temporal: 54,
    89→  right_temporal: 284,
    90→
    91→  // Ears
    92→  left_auricular_lateral: 127,
    93→  right_auricular_lateral: 356,
    94→
    95→  // Neck (same as bottom gonion indices, but apply +5% yOffset when rendering)
    96→  // Per landmarkers.js: neckLeft: 172 with yOffset: 0.05, neckRight: 397 with yOffset: 0.05
    97→  left_cervical_lateralis: 172,
    98→  right_cervical_lateralis: 397,
    99→};
   100→
   101→// ============================================
   102→// DEPTH VARIANCE RESULT INTERFACE
   103→// ============================================
   104→
   105→export interface DepthVarianceResult {
   106→  isValid: boolean;
   107→  confidence: number;
   108→  depthSpread: number;        // Horizontal distance from nose to ear (0-1 normalized)
   109→  nosalProjection: number;    // How much the nose projects forward
   110→  frankfortDeviation: number; // Degrees deviation from horizontal
   111→  issues: string[];           // List of validation issues
   112→}
   113→
   114→// Side profile landmark mapping (IDs must match SIDE_PROFILE_LANDMARKS in landmarks.ts)
   115→// Matches 28 side profile landmarks exactly
   116→// For side profile photos, uses MediaPipe LEFT indices (appearing on left side of image)
   117→// Note: Server uses a custom server-side model for side detection; this is our MediaPipe fallback
   118→export const MEDIAPIPE_SIDE_MAPPING: Record<string, number> = {
   119→  // 1. vertex - top of head
   120→  vertex: 10,
   121→  // 2. occiput - back of head (no good MediaPipe equivalent, use top)
   122→  occiput: 10,
   123→  // 3. pronasale - nose tip
   124→  pronasale: 1,
   125→  // 4. neckPoint - lower neck (no good equivalent, use chin)
   126→  neckPoint: 152,
   127→  // 5. porion - ear canal opening (use ear area)
   128→  porion: 127,
   129→  // 6. orbitale - lowest point of orbital rim (index 33 per cephalometric landmarkers.js)
   130→  orbitale: 33,
   131→  // 7. tragus - ear cartilage
   132→  tragus: 127,
   133→  // 8. intertragicNotch - notch in ear
   134→  intertragicNotch: 127,
   135→  // 9. cornealApex - forward point of cornea
   136→  cornealApex: 468,
   137→  // 10. cheekbone - zygomatic prominence
   138→  cheekbone: 234,
   139→  // 11. trichion - hairline
   140→  trichion: 10,
   141→  // 12. glabella - between brows
   142→  glabella: 9,
   143→  // 13. nasion - nasal root depression
   144→  nasion: 6,
   145→  // 14. rhinion - dorsal nose point
   146→  rhinion: 4,
   147→  // 15. supratip - above nose tip
   148→  supratip: 4,
   149→  // 16. infratip - below nose tip
   150→  infratip: 2,
   151→  // 17. columella - nasal septum
   152→  columella: 2,
   153→  // 18. subnasale - nose base meets upper lip
   154→  subnasale: 2,
   155→  // 19. subalare - nostril wing
   156→  subalare: 129,
   157→  // 20. labraleSuperius - upper lip
   158→  labraleSuperius: 0,
   159→  // 21. cheilion - mouth corner
   160→  cheilion: 61,
   161→  // 22. labraleInferius - lower lip
   162→  labraleInferius: 17,
   163→  // 23. sublabiale - below lower lip
   164→  sublabiale: 18,
   165→  // 24. pogonion - chin point
   166→  pogonion: 152,
   167→  // 25. menton - chin bottom
   168→  menton: 152,
   169→  // 26. cervicalPoint - high neck point
   170→  cervicalPoint: 152,
   171→  // 27. gonionTop - upper jaw angle
   172→  gonionTop: 116,
   173→  // 28. gonionBottom - lower jaw angle
   174→  gonionBottom: 172,
   175→};
   176→
   177→export interface DetectedLandmarks {
   178→  landmarks: Array<{ id: string; x: number; y: number }>;
   179→  confidence: number;
   180→  faceBox: { x: number; y: number; width: number; height: number };
   181→  frankfortPlane?: {
   182→    angle: number;
   183→    orbitale: { x: number; y: number };
   184→    porion: { x: number; y: number };
   185→  };
   186→  depthValidation?: DepthVarianceResult;
   187→}
   188→
   189→/**
   190→ * Detect facial landmarks from an image URL
   191→ *
   192→ * Detection hierarchy:
   193→ * - Front profile: MediaPipe Face Mesh (478 landmarks) - unchanged
   194→ * - Side profile: Server-side InsightFace → Edge-based fallback → Manual placement
   195→ */
   196→export async function detectFromImageUrl(
   197→  imageUrl: string,
   198→  mode: 'front' | 'side'
   199→): Promise<DetectedLandmarks | null> {
   200→  try {
   201→    // ============================================
   202→    // SIDE PROFILE: Use server-side detection
   203→    // ============================================
   204→    if (mode === 'side') {
   205→      console.log('[Detection] Side profile mode - trying server-side detection...');
   206→
   207→      // Try server-side InsightFace first (best accuracy)
   208→      const serverResult = await detectSideProfileServer(imageUrl);
   209→      if (serverResult) {
   210→        console.log('[Detection] Server-side detection succeeded');
   211→
   212→        // Validate side profile depth variance
   213→        const depthValidation = validateSideProfileDepth(
   214→          serverResult.landmarks,
   215→          serverResult.frankfortPlane
   216→        );
   217→        console.log('[Detection] Depth validation:', {
   218→          isValid: depthValidation.isValid,
   219→          confidence: depthValidation.confidence.toFixed(2),
   220→          depthSpread: (depthValidation.depthSpread * 100).toFixed(1) + '%',
   221→          issues: depthValidation.issues,
   222→        });
   223→
   224→        return {
   225→          landmarks: serverResult.landmarks,
   226→          confidence: serverResult.confidence,
   227→          faceBox: serverResult.faceBox,
   228→          frankfortPlane: serverResult.frankfortPlane,
   229→          depthValidation,
   230→        };
   231→      }
   232→
   233→      console.log('[Detection] Server-side failed, trying edge-based detection...');
   234→
   235→      // Fallback to edge-based detection
   236→      const sideResult = await detectSideProfile(imageUrl);
   237→      if (sideResult) {
   238→        console.log('[Detection] Edge-based side profile detection succeeded');
   239→
   240→        // Validate side profile depth variance
   241→        const depthValidation = validateSideProfileDepth(sideResult.landmarks);
   242→        console.log('[Detection] Depth validation (edge-based):', {
   243→          isValid: depthValidation.isValid,
   244→          confidence: depthValidation.confidence.toFixed(2),
   245→          issues: depthValidation.issues,
   246→        });
   247→
   248→        return {
   249→          landmarks: sideResult.landmarks,
   250→          confidence: sideResult.confidence,
   251→          faceBox: {
   252→            x: 0,
   253→            y: 0,
   254→            width: 1,
   255→            height: 1,
   256→          },
   257→          depthValidation,
   258→        };
   259→      }
   260→
   261→      console.log('[Detection] All side profile detection methods failed - manual placement required');
   262→      return null;
   263→    }
   264→
   265→    // ============================================
   266→    // FRONT PROFILE: Use MediaPipe (UNCHANGED)
   267→    // ============================================
   268→    // Load the image
   269→    const img = await loadImage(imageUrl);
   270→
   271→    // Use the singleton service
   272→    const result = await faceDetectionService.detect(img);
   273→
   274→    // Check if MediaPipe detected a face
   275→    const mediaPipeSucceeded = result.faceLandmarks && result.faceLandmarks.length > 0;
   276→
   277→    if (!mediaPipeSucceeded) {
   278→      console.log('No faces detected');
   279→      return null;
   280→    }
   281→
   282→    const faceLandmarks = result.faceLandmarks[0];
   283→
   284→    // Front profile uses MediaPipe mapping
   285→    const mapping = MEDIAPIPE_FRONT_MAPPING;
   286→
   287→    // Neck landmarks need +5% yOffset per cephalometric landmarkers.js
   288→    const NECK_Y_OFFSET = 0.05;
   289→    const NECK_LANDMARKS = ['left_cervical_lateralis', 'right_cervical_lateralis'];
   290→
   291→    // Map keypoints to our landmarks
   292→    const landmarks = Object.entries(mapping).map(([id, index]) => {
   293→      // Handle index bounds
   294→      const safeIndex = Math.min(index, faceLandmarks.length - 1);
   295→      const landmark = faceLandmarks[safeIndex];
   296→
   297→      // Apply yOffset for neck landmarks
   298→      const yOffset = NECK_LANDMARKS.includes(id) ? NECK_Y_OFFSET : 0;
   299→
   300→      return {
   301→        id,
   302→        // MediaPipe returns normalized coordinates (0-1)
   303→        x: landmark ? landmark.x : 0.5,
   304→        y: landmark ? Math.min(landmark.y + yOffset, 1) : 0.5,
   305→      };
   306→    });
   307→
   308→    // Calculate face bounding box from all landmarks
   309→    let minX = 1, minY = 1, maxX = 0, maxY = 0;
   310→    faceLandmarks.forEach((lm) => {
   311→      minX = Math.min(minX, lm.x);
   312→      minY = Math.min(minY, lm.y);
   313→      maxX = Math.max(maxX, lm.x);
   314→      maxY = Math.max(maxY, lm.y);
   315→    });
   316→
   317→    return {
   318→      landmarks,
   319→      confidence: 0.95, // MediaPipe doesn't return confidence per landmark
   320→      faceBox: {
   321→        x: minX,
   322→        y: minY,
   323→        width: maxX - minX,
   324→        height: maxY - minY,
   325→      },
   326→    };
   327→  } catch (error) {
   328→    console.error('Face detection error:', error);
   329→    return null;
   330→  }
   331→}
   332→
   333→/**
   334→ * Load an image from URL
   335→ */
   336→function loadImage(url: string): Promise<HTMLImageElement> {
   337→  return new Promise((resolve, reject) => {
   338→    const img = new Image();
   339→    img.crossOrigin = 'anonymous';
   340→    img.onload = () => resolve(img);
   341→    img.onerror = (e) => reject(e);
   342→    img.src = url;
   343→  });
   344→}
   345→
   346→// ============================================
   347→// SIDE PROFILE DEPTH VARIANCE VALIDATION
   348→// ============================================
   349→
   350→/**
   351→ * Validate 3D depth variance from side profile landmarks
   352→ *
   353→ * Checks that the photo shows a true side profile by analyzing:
   354→ * 1. Depth spread: Horizontal distance from nose tip to ear landmarks
   355→ * 2. Nasal projection: How far the nose projects forward
   356→ * 3. Frankfort plane: Should be relatively horizontal in proper side profiles
   357→ *
   358→ * @param landmarks Array of detected landmarks with x, y coordinates
   359→ * @param frankfortPlane Optional Frankfort plane data
   360→ * @returns Validation result with confidence score and issues
   361→ */
   362→export function validateSideProfileDepth(
   363→  landmarks: Array<{ id: string; x: number; y: number }>,
   364→  frankfortPlane?: { angle: number; orbitale: { x: number; y: number }; porion: { x: number; y: number } }
   365→): DepthVarianceResult {
   366→  const issues: string[] = [];
   367→  let confidence = 1.0;
   368→
   369→  // Get key landmarks for depth validation
   370→  const getLandmark = (id: string) => landmarks.find(l => l.id === id);
   371→
   372→  const pronasale = getLandmark('pronasale');     // Nose tip
   373→  const nasion = getLandmark('nasion');           // Nasal root
   374→  const porion = getLandmark('porion');           // Ear opening
   375→  const tragus = getLandmark('tragus');           // Ear cartilage
   376→  const orbitale = getLandmark('orbitale');       // Lowest orbital point
   377→  const pogonion = getLandmark('pogonion');       // Chin point
   378→  const subnasale = getLandmark('subnasale');     // Nose base
   379→
   380→  // Calculate depth spread (nose to ear horizontal distance)
   381→  let depthSpread = 0;
   382→  const earLandmark = porion || tragus;
   383→
   384→  if (pronasale && earLandmark) {
   385→    // In a true side profile, nose tip should be significantly forward of ear
   386→    depthSpread = Math.abs(pronasale.x - earLandmark.x);
   387→
   388→    // Minimum depth spread threshold (nose should be at least 15% face width from ear)
   389→    const MIN_DEPTH_SPREAD = 0.15;
   390→    if (depthSpread < MIN_DEPTH_SPREAD) {
   391→      issues.push(`Insufficient depth spread (${(depthSpread * 100).toFixed(1)}%): Photo may not be a true side profile`);
   392→      confidence -= 0.3;
   393→    }
   394→  } else {
   395→    issues.push('Missing key landmarks for depth calculation (pronasale or ear landmark)');
   396→    confidence -= 0.4;
   397→  }
   398→
   399→  // Calculate nasal projection
   400→  let nosalProjection = 0;
   401→  if (pronasale && subnasale) {
   402→    // Nasal projection: horizontal distance from nose base to tip
   403→    nosalProjection = Math.abs(pronasale.x - subnasale.x);
   404→
   405→    // Minimum nasal projection (nose tip should project at least 5% forward of base)
   406→    const MIN_NASAL_PROJECTION = 0.05;
   407→    if (nosalProjection < MIN_NASAL_PROJECTION) {
   408→      issues.push(`Low nasal projection (${(nosalProjection * 100).toFixed(1)}%): Profile angle may be too frontal`);
   409→      confidence -= 0.2;
   410→    }
   411→  }
   412→
   413→  // Check Frankfort plane deviation
   414→  let frankfortDeviation = 0;
   415→  if (frankfortPlane) {
   416→    frankfortDeviation = Math.abs(frankfortPlane.angle);
   417→
   418→    // Frankfort plane should be within ±10 degrees of horizontal in ideal photos
   419→    const MAX_FRANKFORT_DEVIATION = 15;
   420→    if (frankfortDeviation > MAX_FRANKFORT_DEVIATION) {
   421→      issues.push(`Head tilt detected (${frankfortDeviation.toFixed(1)}°): Consider re-taking photo with head level`);
   422→      confidence -= 0.15;
   423→    }
   424→  } else if (orbitale && (porion || tragus)) {
   425→    // Calculate from landmarks if not provided
   426→    const ear = porion || tragus;
   427→    if (ear && orbitale) {
   428→      const dx = ear.x - orbitale.x;
   429→      const dy = ear.y - orbitale.y;
   430→      frankfortDeviation = Math.abs(Math.atan2(dy, dx) * (180 / Math.PI));
   431→
   432→      const MAX_FRANKFORT_DEVIATION = 15;
   433→      if (frankfortDeviation > MAX_FRANKFORT_DEVIATION) {
   434→        issues.push(`Head tilt detected (${frankfortDeviation.toFixed(1)}°): Consider re-taking photo with head level`);
   435→        confidence -= 0.15;
   436→      }
   437→    }
   438→  }
   439→
   440→  // Additional validation: Check landmark horizontal ordering
   441→  // In a true side profile facing left, nose should be leftmost, ear rightmost
   442→  // In a true side profile facing right, the opposite
   443→  if (pronasale && earLandmark && nasion) {
   444→    const noseToEar = pronasale.x - earLandmark.x;
   445→    const nasionToEar = nasion.x - earLandmark.x;
   446→
   447→    // Both should have the same sign (nose and nasion on same side relative to ear)
   448→    if (noseToEar * nasionToEar < 0) {
   449→      issues.push('Inconsistent landmark positions: May indicate partial profile or rotation');
   450→      confidence -= 0.2;
   451→    }
   452→  }
   453→
   454→  // Check chin position relative to profile
   455→  if (pogonion && pronasale && earLandmark) {
   456→    const chinDepth = Math.abs(pogonion.x - earLandmark.x);
   457→    const noseDepth = Math.abs(pronasale.x - earLandmark.x);
   458→
   459→    // In a normal profile, chin should be closer to ear than nose tip
   460→    // A severely recessed chin might have issues, but this is a soft check
   461→    if (noseDepth > 0 && chinDepth / noseDepth < 0.3) {
   462→      issues.push('Unusual chin position: Very recessed or photo angle issue');
   463→      confidence -= 0.1;
   464→    }
   465→  }
   466→
   467→  // Clamp confidence between 0 and 1
   468→  confidence = Math.max(0, Math.min(1, confidence));
   469→
   470→  return {
   471→    isValid: confidence >= 0.5 && issues.length < 3,
   472→    confidence,
   473→    depthSpread,
   474→    nosalProjection,
   475→    frankfortDeviation,
   476→    issues,
   477→  };
   478→}
   479→
   480→/**
   481→ * Get the region to zoom into for a specific landmark
   482→ */
   483→export function getLandmarkZoomRegion(
   484→  landmarkId: string
   485→): { centerX: number; centerY: number; zoomLevel: number } {
   486→  const regions: Record<string, { centerX: number; centerY: number; zoomLevel: number }> = {
   487→    // Eyes - zoom in close
   488→    left_pupila: { centerX: 0.35, centerY: 0.35, zoomLevel: 3 },
   489→    left_canthus_medialis: { centerX: 0.4, centerY: 0.35, zoomLevel: 3 },
   490→    left_canthus_lateralis: { centerX: 0.3, centerY: 0.35, zoomLevel: 3 },
   491→    right_pupila: { centerX: 0.65, centerY: 0.35, zoomLevel: 3 },
   492→    right_canthus_medialis: { centerX: 0.6, centerY: 0.35, zoomLevel: 3 },
   493→    right_canthus_lateralis: { centerX: 0.7, centerY: 0.35, zoomLevel: 3 },
   494→
   495→    // Brows
   496→    left_supercilium_apex: { centerX: 0.35, centerY: 0.28, zoomLevel: 2.5 },
   497→    right_supercilium_apex: { centerX: 0.65, centerY: 0.28, zoomLevel: 2.5 },
   498→
   499→    // Nose
   500→    nasal_base: { centerX: 0.5, centerY: 0.4, zoomLevel: 2 },
   501→    subnasale: { centerX: 0.5, centerY: 0.55, zoomLevel: 2.5 },
   502→
   503→    // Mouth
