export default function CheckoutSuccessPage() {
    const searchParams = useSearchParams();
    const plan = searchParams.get('plan') || 'pro';
    const sessionId = searchParams.get('session_id');
    const [isProcessing, setIsProcessing] = useState(true);

    useEffect(() => {
        // Simulate verifying the session and upgrading the user
        const timer = setTimeout(() => {
            setIsProcessing(false);

            // Fire confetti
            const duration = 3 * 1000;
            const animationEnd = Date.now() + duration;
            const defaults = { startVelocity: 30, spread: 360, ticks: 60, zIndex: 0 };

            const randomInRange = (min: number, max: number) => Math.random() * (max - min) + min;

            const interval: ReturnType<typeof setInterval> = setInterval(function () {
                const timeLeft = animationEnd - Date.now();

                if (timeLeft <= 0) {
                    return clearInterval(interval);
                }

                const particleCount = 50 * (timeLeft / duration);
                confetti({ ...defaults, particleCount, origin: { x: randomInRange(0.1, 0.3), y: Math.random() - 0.2 } });
                confetti({ ...defaults, particleCount, origin: { x: randomInRange(0.7, 0.9), y: Math.random() - 0.2 } });
            }, 250);

            // MOCK: Unlock feature in localStorage for testing
            // In production, the backend would handle this via webhook
            if (typeof window !== 'undefined') {
                // Unlock all results
                localStorage.setItem('has_paid_results', 'true');
                // If pro plan, could add more specific flags
                if (plan === 'pro') localStorage.setItem('is_pro_member', 'true');

                console.log('[MOCK] Upgraded user status to PAID');
